sqlMap.update:

/* deptDAO.deleteDept */
/* 부서_코드 삭제 */
UPDATE TC_DEPT
   SET DELETE_AT = 'Y'
     , DELETE_DT = SYSDATE
WHERE DEPT_CODE = #DEPT_CODE# /* 부서_코드.부서_코드 */

sqlMap.sql:
WHERE 1 = 1


	AND
	tcdept.DEPT_CODE = #searchKeyword#


	AND
	tcdept.DEPT_NM LIKE '%' || #searchKeyword# || '%'

 /* 부서_코드.부서_코드 */ AND (tcdept.DEPT_CODE = #DEPT_CODE# or tcdept.SEHIGH_DEPT_CODE = #DEPT_CODE# or tcdept.UPPER_SEHIGH_DEPT_CODE = #DEPT_CODE# ) 
 /* 부서_코드.부서_명 */ AND tcdept.DEPT_NM like '%' || #DEPT_NM# || '%' 
 /* 부서_코드.최하위_부서_명 */ AND tcdept.LOWEST_DEPT_NM = #LOWEST_DEPT_NM# 
 /* 부서_코드.차수 */ AND tcdept.ODR = #ODR# 
 /* 부서_코드.서열 */ AND tcdept.ORD = #ORD# 
 /* 부서_코드.차상위_부서_코드 */ AND tcdept.SEHIGH_DEPT_CODE = #SEHIGH_DEPT_CODE# 
 /* 부서_코드.최상위_부서_코드 */ AND tcdept.TOP_DEPT_CODE = #TOP_DEPT_CODE# 
 /* 부서_코드.소속_부서_차수 */ AND tcdept.PSITN_DEPT_ODR = #PSITN_DEPT_ODR# 
 /* 부서_코드.대표_부서_코드 */ AND tcdept.RPRSNT_DEPT_CODE = #RPRSNT_DEPT_CODE# 
 /* 부서_코드.기관장_직급_명 */ AND tcdept.ISTDR_CLSF_NM = #ISTDR_CLSF_NM# 
 /* 부서_코드.행정동_코드 */ AND tcdept.ADSTRD_CODE = #ADSTRD_CODE# 
 /* 부서_코드.우편번호 */ AND tcdept.ZIP = #ZIP# 
 /* 부서_코드.팩스_번호 */ AND tcdept.FAX_NO = #FAX_NO# 
 /* 부서_코드.생성_일자 */ AND tcdept.CREAT_DE = #CREAT_DE# 
 /* 부서_코드.폐지_일자 */ AND tcdept.ABL_DE = #ABL_DE# 
 /* 부서_코드.최종_작업_일자 */ AND tcdept.LAST_OPERT_DE = #LAST_OPERT_DE# 
 /* 부서_코드.기관_유형 */ AND tcdept.INSTT_TY = #INSTT_TY# 
 /* 부서_코드.이전_기관 */ AND tcdept.PREV_INSTT = #PREV_INSTT# 
 /* 부서_코드.교육_훈련_기관_여부 */ AND tcdept.EDC_TRAING_INSTT_AT = #EDC_TRAING_INSTT_AT# 
 /* 부서_코드.사용_여부 */ AND tcdept.USE_AT = #USE_AT# 
 /* 부서_코드.삭제_여부 */ AND tcdept.DELETE_AT = #DELETE_AT# 
 /* 부서_코드.생성자_번호 */ AND tcdept.CRTR_NO = #CRTR_NO# 
 /* 부서_코드.생성_일시 */ AND tcdept.CREAT_DT = #CREAT_DT# 
 /* 부서_코드.수정자_번호 */ AND tcdept.UPDUSR_NO = #UPDUSR_NO# 
 /* 부서_코드.수정_일시 */ AND tcdept.UPDT_DT = #UPDT_DT# 
 /* 부서_코드.전화_번호 */ AND tcdept.TEL_NO = #TEL_NO#

sqlMap.select:
/* deptDAO.selectDeptListPage */ /* 부서_코드 조회(페이징) */ WITH tcdept AS ( SELECT tcdept.DEPT_CODE /* 부서_코드.부서_코드 */ , tcdept.DEPT_NM /* 부서_코드.부서_명 */ , tcdept.LOWEST_DEPT_NM /* 부서_코드.최하위_부서_명 */ , tcdept.ODR /* 부서_코드.차수 */ , tcdept.ORD /* 부서_코드.서열 */ , tcdept.SEHIGH_DEPT_CODE /* 부서_코드.차상위_부서_코드 */ , tcdept.TOP_DEPT_CODE /* 부서_코드.최상위_부서_코드 */ , tcdept.PSITN_DEPT_ODR /* 부서_코드.소속_부서_차수 */ , tcdept.RPRSNT_DEPT_CODE /* 부서_코드.대표_부서_코드 */ , tcdept.ISTDR_CLSF_NM /* 부서_코드.기관장_직급_명 */ , tcdept.ADSTRD_CODE /* 부서_코드.행정동_코드 */ , tcdept.ZIP /* 부서_코드.우편번호 */ , tcdept.FAX_NO /* 부서_코드.팩스_번호 */ , tcdept.CREAT_DE /* 부서_코드.생성_일자 */ , tcdept.ABL_DE /* 부서_코드.폐지_일자 */ , tcdept.LAST_OPERT_DE /* 부서_코드.최종_작업_일자 */ , tcdept.INSTT_TY /* 부서_코드.기관_유형 */ , tcdept.PREV_INSTT /* 부서_코드.이전_기관 */ , tcdept.EDC_TRAING_INSTT_AT /* 부서_코드.교육_훈련_기관_여부 */ , tcdept.USE_AT /* 부서_코드.사용_여부 */ , tcdept.DELETE_AT /* 부서_코드.삭제_여부 */ , tcdept.CRTR_NO /* 부서_코드.생성자_번호 */ , tcdept.CREAT_DT /* 부서_코드.생성_일시 */ , tcdept.UPDUSR_NO /* 부서_코드.수정자_번호 */ , tcdept.UPDT_DT /* 부서_코드.수정_일시 */ , tcdept.TEL_NO /* 부서_코드.전화_번호 */
FROM TC_DEPT tcdept
WHERE 1 = 1
AND tcdept.DEPT_CODE = #searchKeyword#
AND tcdept.DEPT_NM LIKE '%' || #searchKeyword# || '%'
/* 부서_코드.부서_코드 */ AND tcdept.DEPT_CODE = #DEPT_CODE#
/* 부서_코드.부서_명 */ AND tcdept.DEPT_NM = #DEPT_NM#
/* 부서_코드.최하위_부서_명 */ AND tcdept.LOWEST_DEPT_NM = #LOWEST_DEPT_NM#
/* 부서_코드.차수 */ AND tcdept.ODR = #ODR#
/* 부서_코드.서열 */ AND tcdept.ORD = #ORD#
/* 부서_코드.차상위_부서_코드 */ AND tcdept.SEHIGH_DEPT_CODE = #SEHIGH_DEPT_CODE#
/* 부서_코드.최상위_부서_코드 */ AND tcdept.TOP_DEPT_CODE = #TOP_DEPT_CODE#
/* 부서_코드.소속_부서_차수 */ AND tcdept.PSITN_DEPT_ODR = #PSITN_DEPT_ODR#
/* 부서_코드.대표_부서_코드 */ AND tcdept.RPRSNT_DEPT_CODE = #RPRSNT_DEPT_CODE#
/* 부서_코드.기관장_직급_명 */ AND tcdept.ISTDR_CLSF_NM = #ISTDR_CLSF_NM#
/* 부서_코드.행정동_코드 */ AND tcdept.ADSTRD_CODE = #ADSTRD_CODE#
/* 부서_코드.우편번호 */ AND tcdept.ZIP = #ZIP#
/* 부서_코드.팩스_번호 */ AND tcdept.FAX_NO = #FAX_NO#
/* 부서_코드.생성_일자 */ AND tcdept.CREAT_DE = #CREAT_DE#
/* 부서_코드.폐지_일자 */ AND tcdept.ABL_DE = #ABL_DE#
/* 부서_코드.최종_작업_일자 */ AND tcdept.LAST_OPERT_DE = #LAST_OPERT_DE#
/* 부서_코드.기관_유형 */ AND tcdept.INSTT_TY = #INSTT_TY#
/* 부서_코드.이전_기관 */ AND tcdept.PREV_INSTT = #PREV_INSTT#
/* 부서_코드.교육_훈련_기관_여부 */ AND tcdept.EDC_TRAING_INSTT_AT = #EDC_TRAING_INSTT_AT#
/* 부서_코드.사용_여부 */ AND tcdept.USE_AT = #USE_AT#
/* 부서_코드.삭제_여부 */ AND tcdept.DELETE_AT = #DELETE_AT#
/* 부서_코드.생성자_번호 */ AND tcdept.CRTR_NO = #CRTR_NO#
/* 부서_코드.생성_일시 */ AND tcdept.CREAT_DT = #CREAT_DT#
/* 부서_코드.수정자_번호 */ AND tcdept.UPDUSR_NO = #UPDUSR_NO#
/* 부서_코드.수정_일시 */ AND tcdept.UPDT_DT = #UPDT_DT#
/* 부서_코드.전화_번호 */ AND tcdept.TEL_NO = #TEL_NO#
) SELECT tcdept.* FROM ( SELECT tcdept.DEPT_CODE /* 부서_코드.부서_코드 */ , tcdept.DEPT_NM /* 부서_코드.부서_명 */ , tcdept.LOWEST_DEPT_NM /* 부서_코드.최하위_부서_명 */ , tcdept.ODR /* 부서_코드.차수 */ , tcdept.ORD /* 부서_코드.서열 */ , tcdept.SEHIGH_DEPT_CODE /* 부서_코드.차상위_부서_코드 */ , tcdept.TOP_DEPT_CODE /* 부서_코드.최상위_부서_코드 */ , tcdept.PSITN_DEPT_ODR /* 부서_코드.소속_부서_차수 */ , tcdept.RPRSNT_DEPT_CODE /* 부서_코드.대표_부서_코드 */ , tcdept.ISTDR_CLSF_NM /* 부서_코드.기관장_직급_명 */ , tcdept.ADSTRD_CODE /* 부서_코드.행정동_코드 */ , tcdept.ZIP /* 부서_코드.우편번호 */ , tcdept.FAX_NO /* 부서_코드.팩스_번호 */ , tcdept.CREAT_DE /* 부서_코드.생성_일자 */ , tcdept.ABL_DE /* 부서_코드.폐지_일자 */ , tcdept.LAST_OPERT_DE /* 부서_코드.최종_작업_일자 */ , tcdept.INSTT_TY /* 부서_코드.기관_유형 */ , tcdept.PREV_INSTT /* 부서_코드.이전_기관 */ , tcdept.EDC_TRAING_INSTT_AT /* 부서_코드.교육_훈련_기관_여부 */ , tcdept.USE_AT /* 부서_코드.사용_여부 */ , tcdept.DELETE_AT /* 부서_코드.삭제_여부 */ , tcdept.CRTR_NO /* 부서_코드.생성자_번호 */ , tcdept.CREAT_DT /* 부서_코드.생성_일시 */ , tcdept.UPDUSR_NO /* 부서_코드.수정자_번호 */ , tcdept.UPDT_DT /* 부서_코드.수정_일시 */ , tcdept.TEL_NO /* 부서_코드.전화_번호 */ /* 부서_코드.부서_코드 */ /* 부서_코드.부서_명 */ , ROW_NUMBER() OVER( ORDER BY tcdept.DEPT_CODE ASC, tcdept.DEPT_NM DESC ) ROW_NO FROM tcdept WHERE 1=1 ) tcdept WHERE 1=1
AND tcdept.ROW_NO BETWEEN #firstIndex# + 1 AND #lastIndex#
AND tcdept.ROW_NO <= #lastIndex# AND ROW_NO > #firstIndex#
