sqlMap.delete:

/* pavFrmulaIdxDAO.deletePavFrmulaIdx */
/* 포장_수식_지수 삭제 */
DELETE FROM TN_PAV_FRMULA_IDX 
WHERE 1 = 1
AND IDX_NO = #IDX_NO# /* 포장_수식_지수.지수_번호 */

sqlMap.sql:
WHERE 1 = 1


	AND tnpavfrmulaidx.IDX_NO = #searchKeyword#


	AND tnpavfrmulaidx.FRMULA_NO LIKE '%' || #searchKeyword# || '%'

 /* 포장_수식_지수.지수_번호 */ AND tnpavfrmulaidx.IDX_NO = #IDX_NO# 
 /* 포장_수식_지수.수식_번호 */ AND tnpavfrmulaidx.FRMULA_NO = #FRMULA_NO# 
 /* 포장_수식_지수.지수_명 */ AND tnpavfrmulaidx.IDX_NM = #IDX_NM# 
 /* 포장_수식_지수.지수_값 */ AND tnpavfrmulaidx.IDX_VAL = #IDX_VAL# 
 /* 포장_수식_지수.설명 */ AND tnpavfrmulaidx.DC = #DC# 
 /* 포장_수식_지수.삭제_여부 */ AND tnpavfrmulaidx.DELETE_AT = #DELETE_AT# 
 /* 포장_수식_지수.사용_여부 */ AND tnpavfrmulaidx.USE_AT = #USE_AT# 
 /* 포장_수식_지수.생성자_번호 */ AND tnpavfrmulaidx.CRTR_NO = #CRTR_NO# 
 /* 포장_수식_지수.생성_일시 */ AND tnpavfrmulaidx.CREAT_DT = #CREAT_DT# 
 /* 포장_수식_지수.수정자_번호 */ AND tnpavfrmulaidx.UPDUSR_NO = #UPDUSR_NO# 
 /* 포장_수식_지수.수정_일시 */ AND tnpavfrmulaidx.UPDT_DT = #UPDT_DT#

sqlMap.select:
/* pavFrmulaIdxDAO.selectPavFrmulaIdxListPage */ /* 포장_수식_지수 조회(페이징) */ WITH tnpavfrmulaidx AS ( SELECT tnpavfrmulaidx.IDX_NO /* 포장_수식_지수.지수_번호 */ , tnpavfrmulaidx.FRMULA_NO /* 포장_수식_지수.수식_번호 */ , tnpavfrmulaidx.IDX_NM /* 포장_수식_지수.지수_명 */ , tnpavfrmulaidx.IDX_VAL /* 포장_수식_지수.지수_값 */ , tnpavfrmulaidx.DC /* 포장_수식_지수.설명 */ , tnpavfrmulaidx.DELETE_AT /* 포장_수식_지수.삭제_여부 */ , tnpavfrmulaidx.USE_AT /* 포장_수식_지수.사용_여부 */ , tnpavfrmulaidx.CRTR_NO /* 포장_수식_지수.생성자_번호 */ , tnpavfrmulaidx.CREAT_DT /* 포장_수식_지수.생성_일시 */ , tnpavfrmulaidx.UPDUSR_NO /* 포장_수식_지수.수정자_번호 */ , tnpavfrmulaidx.UPDT_DT /* 포장_수식_지수.수정_일시 */
FROM TN_PAV_FRMULA_IDX tnpavfrmulaidx
WHERE 1 = 1
AND tnpavfrmulaidx.IDX_NO = #searchKeyword#
AND tnpavfrmulaidx.FRMULA_NO LIKE '%' || #searchKeyword# || '%'
/* 포장_수식_지수.지수_번호 */ AND tnpavfrmulaidx.IDX_NO = #IDX_NO#
/* 포장_수식_지수.수식_번호 */ AND tnpavfrmulaidx.FRMULA_NO = #FRMULA_NO#
/* 포장_수식_지수.지수_명 */ AND tnpavfrmulaidx.IDX_NM = #IDX_NM#
/* 포장_수식_지수.지수_값 */ AND tnpavfrmulaidx.IDX_VAL = #IDX_VAL#
/* 포장_수식_지수.설명 */ AND tnpavfrmulaidx.DC = #DC#
/* 포장_수식_지수.삭제_여부 */ AND tnpavfrmulaidx.DELETE_AT = #DELETE_AT#
/* 포장_수식_지수.사용_여부 */ AND tnpavfrmulaidx.USE_AT = #USE_AT#
/* 포장_수식_지수.생성자_번호 */ AND tnpavfrmulaidx.CRTR_NO = #CRTR_NO#
/* 포장_수식_지수.생성_일시 */ AND tnpavfrmulaidx.CREAT_DT = #CREAT_DT#
/* 포장_수식_지수.수정자_번호 */ AND tnpavfrmulaidx.UPDUSR_NO = #UPDUSR_NO#
/* 포장_수식_지수.수정_일시 */ AND tnpavfrmulaidx.UPDT_DT = #UPDT_DT#
) SELECT tnpavfrmulaidx.* FROM ( SELECT tnpavfrmulaidx.IDX_NO /* 포장_수식_지수.지수_번호 */ , tnpavfrmulaidx.FRMULA_NO /* 포장_수식_지수.수식_번호 */ , tnpavfrmulaidx.IDX_NM /* 포장_수식_지수.지수_명 */ , tnpavfrmulaidx.IDX_VAL /* 포장_수식_지수.지수_값 */ , tnpavfrmulaidx.DC /* 포장_수식_지수.설명 */ , tnpavfrmulaidx.DELETE_AT /* 포장_수식_지수.삭제_여부 */ , tnpavfrmulaidx.USE_AT /* 포장_수식_지수.사용_여부 */ , tnpavfrmulaidx.CRTR_NO /* 포장_수식_지수.생성자_번호 */ , tnpavfrmulaidx.CREAT_DT /* 포장_수식_지수.생성_일시 */ , tnpavfrmulaidx.UPDUSR_NO /* 포장_수식_지수.수정자_번호 */ , tnpavfrmulaidx.UPDT_DT /* 포장_수식_지수.수정_일시 */ /* 포장_수식_지수.지수_번호 */ /* 포장_수식_지수.수식_번호 */ , ROW_NUMBER() OVER( ORDER BY tnpavfrmulaidx.IDX_NO ASC, tnpavfrmulaidx.FRMULA_NO DESC ) ROW_NO FROM tnpavfrmulaidx WHERE 1=1 ) tnpavfrmulaidx WHERE 1=1
AND tnpavfrmulaidx.ROW_NO BETWEEN #firstIndex# + 1 AND #lastIndex#
AND tnpavfrmulaidx.ROW_NO <= #lastIndex# AND ROW_NO > #firstIndex#

/* pavFrmulaIdxDAO.selectPavFrmulaIdxList */ /* 포장_수식_지수 조회 */ SELECT * FROM (SELECT ROWNUM AS rn, tb.* FROM ( SELECT tnpavfrmulaidx.IDX_NO /* 포장_수식_지수.지수_번호 */ , tnpavfrmulaidx.FRMULA_NO /* 포장_수식_지수.수식_번호 */ , tnpavfrmulaidx.IDX_NM /* 포장_수식_지수.지수_명 */ , tnpavfrmulaidx.IDX_VAL /* 포장_수식_지수.지수_값 */ , tnpavfrmulaidx.DC /* 포장_수식_지수.설명 */ , tnpavfrmulaidx.DELETE_AT /* 포장_수식_지수.삭제_여부 */ , tnpavfrmulaidx.USE_AT /* 포장_수식_지수.사용_여부 */ , tnpavfrmulaidx.CRTR_NO /* 포장_수식_지수.생성자_번호 */ , tnpavfrmulaidx.CREAT_DT /* 포장_수식_지수.생성_일시 */ , tnpavfrmulaidx.UPDUSR_NO /* 포장_수식_지수.수정자_번호 */ , tnpavfrmulaidx.UPDT_DT /* 포장_수식_지수.수정_일시 */ FROM TN_PAV_FRMULA_IDX tnpavfrmulaidx
ORDER BY tnpavfrmulaidx.IDX_NO DESC /* 포장_수식_지수.지수_번호 */ ) tb) tnpavfrmulaidx WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#


/* pavFrmulaIdxDAO.selectPavFrmulaIdxListTotalCount */
/* 포장_수식_지수 목록 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TN_PAV_FRMULA_IDX tnpavfrmulaidx
