 /* 포장_지수_초기값.초기값_CR */ AND tnpavidxeryyval.INITVAL_CR = #INITVAL_CR# 
 /* 포장_지수_초기값.초기값_RD */ AND tnpavidxeryyval.INITVAL_RD = #INITVAL_RD# 
 /* 포장_지수_초기값.초기값_IRI */ AND tnpavidxeryyval.INITVAL_IRI = #INITVAL_IRI# 
