 /* 포장_지수_초기값.기준_결함_RD */ AND tnpavidxeryyval.STDR_DFECT_RD = #STDR_DFECT_RD# 
 /* 포장_지수_초기값.기준_결함_IRI */ AND tnpavidxeryyval.STDR_DFECT_IRI = #STDR_DFECT_IRI# 
 /* 포장_지수_초기값.생성자_번호 */ AND tnpavidxeryyval.CRTR_NO = #CRTR_NO# 
