 /* 포장_지수_초기값.수정_일시 */ AND tnpavidxeryyval.UPDT_DT = #UPDT_DT#

sqlMap.select:
/* pavIdxEryyvalDAO.selectPavIdxEryyvalListPage */ /* 포장_지수_초기값 조회(페이징) */ WITH tnpavidxeryyval AS ( SELECT tnpavidxeryyval.INITVAL_CR /* 포장_지수_초기값.초기값_CR */ , tnpavidxeryyval.INITVAL_RD /* 포장_지수_초기값.초기값_RD */ , tnpavidxeryyval.INITVAL_IRI /* 포장_지수_초기값.초기값_IRI */ , tnpavidxeryyval.STDR_DFECT_CR /* 포장_지수_초기값.기준_결함_CR */ , tnpavidxeryyval.STDR_DFECT_RD /* 포장_지수_초기값.기준_결함_RD */ , tnpavidxeryyval.STDR_DFECT_IRI /* 포장_지수_초기값.기준_결함_IRI */ , tnpavidxeryyval.CRTR_NO /* 포장_지수_초기값.생성자_번호 */ , tnpavidxeryyval.CREAT_DT /* 포장_지수_초기값.생성_일시 */ , tnpavidxeryyval.UPDUSR_NO /* 포장_지수_초기값.수정자_번호 */ , tnpavidxeryyval.UPDT_DT /* 포장_지수_초기값.수정_일시 */
FROM TN_PAV_IDX_INITVAL tnpavidxeryyval
WHERE 1 = 1
AND tnpavidxeryyval.INITVAL_CR = #searchKeyword#
AND tnpavidxeryyval.INITVAL_RD LIKE '%' || #searchKeyword# || '%'
/* 포장_지수_초기값.초기값_CR */ AND tnpavidxeryyval.INITVAL_CR = #INITVAL_CR#
/* 포장_지수_초기값.초기값_RD */ AND tnpavidxeryyval.INITVAL_RD = #INITVAL_RD#
/* 포장_지수_초기값.초기값_IRI */ AND tnpavidxeryyval.INITVAL_IRI = #INITVAL_IRI#
/* 포장_지수_초기값.기준_결함_CR */ AND tnpavidxeryyval.STDR_DFECT_CR = #STDR_DFECT_CR#
/* 포장_지수_초기값.기준_결함_RD */ AND tnpavidxeryyval.STDR_DFECT_RD = #STDR_DFECT_RD#
/* 포장_지수_초기값.기준_결함_IRI */ AND tnpavidxeryyval.STDR_DFECT_IRI = #STDR_DFECT_IRI#
/* 포장_지수_초기값.생성자_번호 */ AND tnpavidxeryyval.CRTR_NO = #CRTR_NO#
/* 포장_지수_초기값.생성_일시 */ AND tnpavidxeryyval.CREAT_DT = #CREAT_DT#
/* 포장_지수_초기값.수정자_번호 */ AND tnpavidxeryyval.UPDUSR_NO = #UPDUSR_NO#
/* 포장_지수_초기값.수정_일시 */ AND tnpavidxeryyval.UPDT_DT = #UPDT_DT#
) SELECT tnpavidxeryyval.* FROM ( SELECT tnpavidxeryyval.INITVAL_CR /* 포장_지수_초기값.초기값_CR */ , tnpavidxeryyval.INITVAL_RD /* 포장_지수_초기값.초기값_RD */ , tnpavidxeryyval.INITVAL_IRI /* 포장_지수_초기값.초기값_IRI */ , tnpavidxeryyval.STDR_DFECT_CR /* 포장_지수_초기값.기준_결함_CR */ , tnpavidxeryyval.STDR_DFECT_RD /* 포장_지수_초기값.기준_결함_RD */ , tnpavidxeryyval.STDR_DFECT_IRI /* 포장_지수_초기값.기준_결함_IRI */ , tnpavidxeryyval.CRTR_NO /* 포장_지수_초기값.생성자_번호 */ , tnpavidxeryyval.CREAT_DT /* 포장_지수_초기값.생성_일시 */ , tnpavidxeryyval.UPDUSR_NO /* 포장_지수_초기값.수정자_번호 */ , tnpavidxeryyval.UPDT_DT /* 포장_지수_초기값.수정_일시 */ /* 포장_지수_초기값.초기값_CR */ /* 포장_지수_초기값.초기값_RD */ , ROW_NUMBER() OVER( ORDER BY tnpavidxeryyval.INITVAL_CR ASC, tnpavidxeryyval.INITVAL_RD DESC ) ROW_NO FROM tnpavidxeryyval WHERE 1=1 ) tnpavidxeryyval WHERE 1=1
AND tnpavidxeryyval.ROW_NO BETWEEN #firstIndex# + 1 AND #lastIndex#
AND tnpavidxeryyval.ROW_NO <= #lastIndex# AND ROW_NO > #firstIndex#

/* pavIdxEryyvalDAO.selectPavIdxEryyvalList */ /* 포장_지수_초기값 조회 */ SELECT * FROM (SELECT ROWNUM AS rn, tb.* FROM ( SELECT tnpavidxeryyval.INITVAL_CR /* 포장_지수_초기값.초기값_CR */ , tnpavidxeryyval.INITVAL_RD /* 포장_지수_초기값.초기값_RD */ , tnpavidxeryyval.INITVAL_IRI /* 포장_지수_초기값.초기값_IRI */ , tnpavidxeryyval.STDR_DFECT_CR /* 포장_지수_초기값.기준_결함_CR */ , tnpavidxeryyval.STDR_DFECT_RD /* 포장_지수_초기값.기준_결함_RD */ , tnpavidxeryyval.STDR_DFECT_IRI /* 포장_지수_초기값.기준_결함_IRI */ , tnpavidxeryyval.CRTR_NO /* 포장_지수_초기값.생성자_번호 */ , tnpavidxeryyval.CREAT_DT /* 포장_지수_초기값.생성_일시 */ , tnpavidxeryyval.UPDUSR_NO /* 포장_지수_초기값.수정자_번호 */ , tnpavidxeryyval.UPDT_DT /* 포장_지수_초기값.수정_일시 */ FROM TN_PAV_IDX_INITVAL tnpavidxeryyval
) tb) tnpavidxeryyval WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#


/* pavIdxEryyvalDAO.selectPavIdxEryyvalListTotalCount */
