sqlMap.insert:
select SEQ_TN_PREDCT_FRMULA_IDX.NEXTVAL from dual
/* predctFrmulaIdxDAO.insertPredctFrmulaIdx */ /* 예측_수식_지수 등록 */
INSERT INTO TN_PREDCT_FRMULA_IDX ( PREDCT_IDX_NO /* 예측_수식_지수.예측_지수_번호 */ , CELL_TYPE /* 예측_수식_지수.섹션_구분 */ , PRDTFML_TRGET /* 예측_수식_지수.예측수식_적용대상 */ , RK_A /* 예측_수식_지수.계수_A */ , RK_B /* 예측_수식_지수.계수_B */ , RK_C /* 예측_수식_지수.계수_C */ , DELETE_AT /* 예측_수식_지수.삭제_여부 */ , USE_AT /* 예측_수식_지수.사용_여부 */ , CRTR_NO /* 예측_수식_지수.생성자_번호 */ , CREAT_DT /* 예측_수식_지수.생성_일시 */ , UPDUSR_NO /* 예측_수식_지수.수정자_번호 */ , UPDT_DT /* 예측_수식_지수.수정_일시 */ ) VALUES ( #PREDCT_IDX_NO# /* 예측_수식_지수.예측_지수_번호 */ , #CELL_TYPE# /* 예측_수식_지수.섹션_구분 */ , #PRDTFML_TRGET# /* 예측_수식_지수.예측수식_적용대상 */ , #RK_A# /* 예측_수식_지수.계수_A */ , #RK_B# /* 예측_수식_지수.계수_B */ , #RK_C# /* 예측_수식_지수.계수_C */ , #DELETE_AT# /* 예측_수식_지수.삭제_여부 */ , #USE_AT# /* 예측_수식_지수.사용_여부 */ , #CRTR_NO# /* 예측_수식_지수.생성자_번호 */ , SYSDATE /* 예측_수식_지수.생성_일시 */ , #UPDUSR_NO# /* 예측_수식_지수.수정자_번호 */ , SYSDATE /* 예측_수식_지수.수정_일시 */ )

sqlMap.sql:
WHERE 1 = 1



		AND tnpredctfrmulaidx.PREDCT_IDX_NO = #searchKeyword#




		AND tnpredctfrmulaidx.CELL_TYPE LIKE '%' || #searchKeyword# || '%'


 /* 예측_수식_지수.예측_지수_번호 */ AND tnpredctfrmulaidx.PREDCT_IDX_NO = #PREDCT_IDX_NO# 
 /* 예측_수식_지수.섹션_구분 */ AND tnpredctfrmulaidx.CELL_TYPE = #CELL_TYPE# 
 /* 예측_수식_지수.예측수식_적용대상 */ AND tnpredctfrmulaidx.PRDTFML_TRGET = #PRDTFML_TRGET# 
 /* 예측_수식_지수.계수_A */ AND tnpredctfrmulaidx.RK_A = #RK_A# 
 /* 예측_수식_지수.계수_B */ AND tnpredctfrmulaidx.RK_B = #RK_B# 
 /* 예측_수식_지수.계수_C */ AND tnpredctfrmulaidx.RK_C = #RK_C# 
 /* 예측_수식_지수.삭제_여부 */ AND tnpredctfrmulaidx.DELETE_AT = #DELETE_AT# 
 /* 예측_수식_지수.사용_여부 */ AND tnpredctfrmulaidx.USE_AT = #USE_AT# 
 /* 예측_수식_지수.생성자_번호 */ AND tnpredctfrmulaidx.CRTR_NO = #CRTR_NO# 
 /* 예측_수식_지수.생성_일시 */ AND tnpredctfrmulaidx.CREAT_DT = #CREAT_DT# 
 /* 예측_수식_지수.수정자_번호 */ AND tnpredctfrmulaidx.UPDUSR_NO = #UPDUSR_NO# 
 /* 예측_수식_지수.수정_일시 */ AND tnpredctfrmulaidx.UPDT_DT = #UPDT_DT#

sqlMap.select:
/* predctFrmulaIdxDAO.selectPredctFrmulaIdxList */ /* 예측_수식_지수 조회 */ SELECT * FROM (SELECT ROW_NUMBER() OVER(ORDER BY $sidx$ $sord$) rn, tb.* FROM ( SELECT tnpredctfrmulaidx.PREDCT_IDX_NO /* 예측_수식_지수.예측_지수_번호 */ , COUNT(*) OVER(PARTITION BY PRDTFML_TRGET) ROW_CNT , (SELECT tccode.CODE_NM FROM TC_CODE tccode WHERE tccode.USE_AT='Y' AND tccode.DELETE_AT='N' AND tccode.CODE_VAL = tnpredctfrmulaidx.PRDTFML_TRGET) PRDTFML_TRGET , (SELECT tccode.CODE_NM FROM TC_CODE tccode WHERE tccode.USE_AT='Y' AND tccode.DELETE_AT='N' AND tccode.CODE_VAL = tnpredctfrmulaidx.CELL_TYPE) CELL_TYPE , tnpredctfrmulaidx.RK_A /* 예측_수식_지수.계수_A */ , tnpredctfrmulaidx.RK_B /* 예측_수식_지수.계수_B */ , tnpredctfrmulaidx.RK_C /* 예측_수식_지수.계수_C */ , tnpredctfrmulaidx.DELETE_AT /* 예측_수식_지수.삭제_여부 */ , tnpredctfrmulaidx.USE_AT /* 예측_수식_지수.사용_여부 */ , tnpredctfrmulaidx.CRTR_NO /* 예측_수식_지수.생성자_번호 */ , tnpredctfrmulaidx.CREAT_DT /* 예측_수식_지수.생성_일시 */ , (SELECT tnsysuser.USER_NM FROM TN_SYS_USER tnsysuser WHERE tnsysuser.USE_AT='Y' AND tnsysuser.DELETE_AT='N' AND tnsysuser.USER_NO = tnpredctfrmulaidx.UPDUSR_NO) UPDUSR_NM /* 예측_수식_지수.수정자_번호 */ , tnpredctfrmulaidx.UPDT_DT /* 예측_수식_지수.수정_일시 */ FROM TN_PREDCT_FRMULA_IDX tnpredctfrmulaidx
) tb) WHERE 1=1
rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#


/* predctFrmulaIdxDAO.selectPredctFrmulaIdxListTotalCount */
/* 예측_수식_지수 목록 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TN_PREDCT_FRMULA_IDX tnpredctfrmulaidx
