sqlMap.delete:

/* srvyDtaLogDAO.deleteSrvyDtaLog */
/* 조사_자료_로그 삭제 */
DELETE FROM TL_SRVY_DTA_LOG 
WHERE 1 = 1
AND PROCESS_LOG_NO = #PROCESS_LOG_NO# /* 조사_자료_로그.처리_로그_번호 */

sqlMap.sql:
WHERE 1 = 1


	AND tlsrvydtalog.PROCESS_LOG_NO = #searchKeyword#


	AND tlsrvydtalog.LOG_MSSAGE LIKE '%' || #searchKeyword# || '%'

 /* 조사_자료_로그.처리_로그_번호 */ AND tlsrvydtalog.PROCESS_LOG_NO = #PROCESS_LOG_NO# 
 /* 조사_자료_로그.로그_메세지 */ AND tlsrvydtalog.LOG_MSSAGE = #LOG_MSSAGE# 
 /* 조사_자료_로그.생성자_번호 */ AND tlsrvydtalog.CRTR_NO = #CRTR_NO# 
 /* 조사_자료_로그.시작_일시 */ AND tlsrvydtalog.BEGIN_DT = #BEGIN_DT# 
 /* 조사_자료_로그.조사_번호 */ AND tlsrvydtalog.SRVY_NO = #SRVY_NO# 
 /* 조사_자료_로그.처리_구분 */ AND tlsrvydtalog.PROCESS_SE = #PROCESS_SE# 
 /* 조사_자료_로그.처리_상태 */ AND tlsrvydtalog.PROCESS_STTUS = #PROCESS_STTUS# 
 /* 조사_자료_로그.종료_일시 */ AND tlsrvydtalog.END_DT = #END_DT#

sqlMap.select:
/* srvyDtaLogDAO.selectSrvyDtaLogListPage */ /* 조사_자료_로그 조회(페이징) */ WITH tlsrvydtalog AS ( SELECT tlsrvydtalog.PROCESS_LOG_NO /* 조사_자료_로그.처리_로그_번호 */ , tlsrvydtalog.LOG_MSSAGE /* 조사_자료_로그.로그_메세지 */ , tlsrvydtalog.CRTR_NO /* 조사_자료_로그.생성자_번호 */ , tlsrvydtalog.BEGIN_DT /* 조사_자료_로그.시작_일시 */ , tlsrvydtalog.SRVY_NO /* 조사_자료_로그.조사_번호 */ , tlsrvydtalog.PROCESS_SE /* 조사_자료_로그.처리_구분 */ , tlsrvydtalog.PROCESS_STTUS /* 조사_자료_로그.처리_상태 */ , tlsrvydtalog.END_DT /* 조사_자료_로그.종료_일시 */
FROM TL_SRVY_DTA_LOG tlsrvydtalog
WHERE 1 = 1
AND tlsrvydtalog.PROCESS_LOG_NO = #searchKeyword#
AND tlsrvydtalog.LOG_MSSAGE LIKE '%' || #searchKeyword# || '%'
/* 조사_자료_로그.처리_로그_번호 */ AND tlsrvydtalog.PROCESS_LOG_NO = #PROCESS_LOG_NO#
/* 조사_자료_로그.로그_메세지 */ AND tlsrvydtalog.LOG_MSSAGE = #LOG_MSSAGE#
/* 조사_자료_로그.생성자_번호 */ AND tlsrvydtalog.CRTR_NO = #CRTR_NO#
/* 조사_자료_로그.시작_일시 */ AND tlsrvydtalog.BEGIN_DT = #BEGIN_DT#
/* 조사_자료_로그.조사_번호 */ AND tlsrvydtalog.SRVY_NO = #SRVY_NO#
/* 조사_자료_로그.처리_구분 */ AND tlsrvydtalog.PROCESS_SE = #PROCESS_SE#
/* 조사_자료_로그.처리_상태 */ AND tlsrvydtalog.PROCESS_STTUS = #PROCESS_STTUS#
/* 조사_자료_로그.종료_일시 */ AND tlsrvydtalog.END_DT = #END_DT#
) SELECT tlsrvydtalog.* FROM ( SELECT tlsrvydtalog.PROCESS_LOG_NO /* 조사_자료_로그.처리_로그_번호 */ , tlsrvydtalog.LOG_MSSAGE /* 조사_자료_로그.로그_메세지 */ , tlsrvydtalog.CRTR_NO /* 조사_자료_로그.생성자_번호 */ , tlsrvydtalog.BEGIN_DT /* 조사_자료_로그.시작_일시 */ , tlsrvydtalog.SRVY_NO /* 조사_자료_로그.조사_번호 */ , tlsrvydtalog.PROCESS_SE /* 조사_자료_로그.처리_구분 */ , tlsrvydtalog.PROCESS_STTUS /* 조사_자료_로그.처리_상태 */ , tlsrvydtalog.END_DT /* 조사_자료_로그.종료_일시 */ /* 조사_자료_로그.처리_로그_번호 */ /* 조사_자료_로그.로그_메세지 */ , ROW_NUMBER() OVER( ORDER BY tlsrvydtalog.PROCESS_LOG_NO ASC, tlsrvydtalog.LOG_MSSAGE DESC ) ROW_NO FROM tlsrvydtalog WHERE 1=1 ) tlsrvydtalog WHERE 1=1
AND tlsrvydtalog.ROW_NO BETWEEN #firstIndex# + 1 AND #lastIndex#
AND tlsrvydtalog.ROW_NO <= #lastIndex# AND ROW_NO > #firstIndex#

/* srvyDtaLogDAO.selectSrvyDtaLogList */ /* 조사_자료_로그 조회 */ SELECT * FROM (SELECT ROWNUM AS rn, tb.* FROM ( SELECT tlsrvydtalog.PROCESS_LOG_NO /* 조사_자료_로그.처리_로그_번호 */ , tlsrvydtalog.LOG_MSSAGE /* 조사_자료_로그.로그_메세지 */ , tlsrvydtalog.CRTR_NO /* 조사_자료_로그.생성자_번호 */ , tlsrvydtalog.BEGIN_DT /* 조사_자료_로그.시작_일시 */ , tlsrvydtalog.SRVY_NO /* 조사_자료_로그.조사_번호 */ , tlsrvydtalog.PROCESS_SE /* 조사_자료_로그.처리_구분 */ , tlsrvydtalog.PROCESS_STTUS /* 조사_자료_로그.처리_상태 */ , tlsrvydtalog.END_DT /* 조사_자료_로그.종료_일시 */ FROM TL_SRVY_DTA_LOG tlsrvydtalog
ORDER BY tlsrvydtalog.PROCESS_LOG_NO DESC /* 조사_자료_로그.처리_로그_번호 */ ) tb) tlsrvydtalog WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#


/* srvyDtaLogDAO.selectSrvyDtaLogListTotalCount */
/* 조사_자료_로그 목록 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TL_SRVY_DTA_LOG tlsrvydtalog
