sqlMap.delete:

/* sysConnectDAO.deleteSysConnect */
/* 시스템접속로그 삭제 */
DELETE FROM TL_SYS_CONNECT 
WHERE 1 = 1
AND CONECT_LOG_NO = #CONECT_LOG_NO# /* 시스템접속로그.접속_로그_번호 */

sqlMap.sql:
WHERE 1 = 1


	AND tlsysconnect.CONECT_LOG_NO = #searchKeyword#


	AND tlsysconnect.USER_NO LIKE '%' || #searchKeyword# || '%'

 /* 시스템접속로그.접속_로그_번호 */ AND tlsysconnect.CONECT_LOG_NO = #CONECT_LOG_NO# 
 /* 시스템접속로그.사용자_번호 */ AND tlsysconnect.USER_NO = #USER_NO# 
 /* 시스템접속로그.메뉴_ID */ AND tlsysconnect.MENU_ID = #MENU_ID# 
 /* 시스템접속로그.세션_ID */ AND tlsysconnect.SESION_ID = #SESION_ID# 
 /* 시스템접속로그.생성_일시 */ AND tlsysconnect.CREAT_DT = #CREAT_DT#

sqlMap.select:
/* sysConnectDAO.selectSysConnectListPage */ /* 시스템접속로그 조회(페이징) */ WITH tlsysconnect AS ( SELECT tlsysconnect.CONECT_LOG_NO /* 시스템접속로그.접속_로그_번호 */ , tlsysconnect.USER_NO /* 시스템접속로그.사용자_번호 */ , tlsysconnect.MENU_ID /* 시스템접속로그.메뉴_ID */ , tlsysconnect.SESION_ID /* 시스템접속로그.세션_ID */ , tlsysconnect.CREAT_DT /* 시스템접속로그.생성_일시 */
FROM TL_SYS_CONNECT tlsysconnect
WHERE 1 = 1
AND tlsysconnect.CONECT_LOG_NO = #searchKeyword#
AND tlsysconnect.USER_NO LIKE '%' || #searchKeyword# || '%'
/* 시스템접속로그.접속_로그_번호 */ AND tlsysconnect.CONECT_LOG_NO = #CONECT_LOG_NO#
/* 시스템접속로그.사용자_번호 */ AND tlsysconnect.USER_NO = #USER_NO#
/* 시스템접속로그.메뉴_ID */ AND tlsysconnect.MENU_ID = #MENU_ID#
/* 시스템접속로그.세션_ID */ AND tlsysconnect.SESION_ID = #SESION_ID#
/* 시스템접속로그.생성_일시 */ AND tlsysconnect.CREAT_DT = #CREAT_DT#
) SELECT tlsysconnect.* FROM ( SELECT tlsysconnect.CONECT_LOG_NO /* 시스템접속로그.접속_로그_번호 */ , tlsysconnect.USER_NO /* 시스템접속로그.사용자_번호 */ , tlsysconnect.MENU_ID /* 시스템접속로그.메뉴_ID */ , tlsysconnect.SESION_ID /* 시스템접속로그.세션_ID */ , tlsysconnect.CREAT_DT /* 시스템접속로그.생성_일시 */ /* 시스템접속로그.접속_로그_번호 */ /* 시스템접속로그.사용자_번호 */ , ROW_NUMBER() OVER( ORDER BY tlsysconnect.CONECT_LOG_NO ASC, tlsysconnect.USER_NO DESC ) ROW_NO FROM tlsysconnect WHERE 1=1 ) tlsysconnect WHERE 1=1
AND tlsysconnect.ROW_NO BETWEEN #firstIndex# + 1 AND #lastIndex#
AND tlsysconnect.ROW_NO <= #lastIndex# AND ROW_NO > #firstIndex#

/* sysConnectDAO.selectSysConnectList */ /* 시스템접속로그 조회 */ SELECT * FROM (SELECT ROWNUM AS rn, tb.* FROM ( SELECT tlsysconnect.CONECT_LOG_NO /* 시스템접속로그.접속_로그_번호 */ , tlsysconnect.USER_NO /* 시스템접속로그.사용자_번호 */ , tlsysconnect.MENU_ID /* 시스템접속로그.메뉴_ID */ , tlsysconnect.SESION_ID /* 시스템접속로그.세션_ID */ , tlsysconnect.CREAT_DT /* 시스템접속로그.생성_일시 */ FROM TL_SYS_CONNECT tlsysconnect
ORDER BY tlsysconnect.CONECT_LOG_NO DESC /* 시스템접속로그.접속_로그_번호 */ ) tb) tlsysconnect WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#


/* sysConnectDAO.selectSysConnectListTotalCount */
/* 시스템접속로그 목록 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TL_SYS_CONNECT tlsysconnect
