sqlMap.insert:

/* userAuthDAO.insertUserAuth */
/* 사용자권한 등록 */		
INSERT INTO TN_USER_AUTH (
	USER_NO /* 사용자권한.사용자_번호 */
	, AUTHOR_ID /* 사용자권한.권한_ID */
	, CRTR_NO /* 사용자권한.생성자_번호 */
	, CREAT_DT /* 사용자권한.생성_일시 */
) VALUES (
	#USER_NO# /* 사용자권한.사용자_번호 */
	, #AUTHOR_ID# /* 사용자권한.권한_ID */
	, #CRTR_NO# /* 사용자권한.생성자_번호 */
	, SYSDATE /* 사용자권한.생성_일시 */
)

sqlMap.update:

/* userAuthDAO.updateUserAuth */
/* 사용자권한 수정 */
UPDATE TN_USER_AUTH 
   SET	AUTHOR_ID = #AUTHOR_ID# /* 사용자권한.권한_ID */
WHERE 1 = 1
AND USER_NO = #USER_NO# /* 사용자권한.사용자_번호 */

sqlMap.delete:

/* userAuthDAO.deleteUserAuth */
/* 사용자권한 삭제 */
DELETE FROM TN_USER_AUTH 
WHERE 1 = 1
AND USER_NO = #USER_NO# /* 사용자권한.사용자_번호 */
AND AUTHOR_ID = #AUTHOR_ID# /* 사용자권한.권한_ID */


/* userAuthDAO.deleteUserAuthByUserNo */
/* 사용자권한 삭제 */
DELETE FROM TN_USER_AUTH 
WHERE 1 = 1
AND USER_NO = #USER_NO# /* 사용자권한.사용자_번호 */

sqlMap.sql:
WHERE 1 = 1


	AND tnuserauth.USER_NO = #searchKeyword#


	AND tnuserauth.AUTHOR_ID LIKE '%' || #searchKeyword# || '%'

 /* 사용자권한.사용자_번호 */ AND tnuserauth.USER_NO = #USER_NO# 
 /* 사용자권한.권한_ID */ AND tnuserauth.AUTHOR_ID = #AUTHOR_ID# 
 /* 사용자권한.생성자_번호 */ AND tnuserauth.CRTR_NO = #CRTR_NO# 
 /* 사용자권한.생성_일시 */ AND tnuserauth.CREAT_DT = #CREAT_DT# 

 AND tnsysuser.USER_NM LIKE '%' || #SCH_USER_NM# || '%'

sqlMap.select:
/* userAuthDAO.selectUserAuthListPage */ /* 사용자권한 조회(페이징) */ WITH tnuserauth AS ( SELECT tnuserauth.USER_NO /* 사용자권한.사용자_번호 */ , tnuserauth.AUTHOR_ID /* 사용자권한.권한_ID */ , tnuserauth.CRTR_NO /* 사용자권한.생성자_번호 */ , tnuserauth.CREAT_DT /* 사용자권한.생성_일시 */ FROM TN_USER_AUTH tnuserauth
WHERE 1 = 1
AND tnuserauth.USER_NO = #searchKeyword#
AND tnuserauth.AUTHOR_ID LIKE '%' || #searchKeyword# || '%'
/* 사용자권한.사용자_번호 */ AND tnuserauth.USER_NO = #USER_NO#
/* 사용자권한.권한_ID */ AND tnuserauth.AUTHOR_ID = #AUTHOR_ID#
/* 사용자권한.생성자_번호 */ AND tnuserauth.CRTR_NO = #CRTR_NO#
/* 사용자권한.생성_일시 */ AND tnuserauth.CREAT_DT = #CREAT_DT#
) SELECT tnuserauth.* FROM ( SELECT tnuserauth.USER_NO /* 사용자권한.사용자_번호 */ , tnuserauth.AUTHOR_ID /* 사용자권한.권한_ID */ , tnuserauth.CRTR_NO /* 사용자권한.생성자_번호 */ , tnuserauth.CREAT_DT /* 사용자권한.생성_일시 */ /* 사용자권한.사용자_번호 */ /* 사용자권한.권한_ID */ , ROW_NUMBER() OVER( ORDER BY tnuserauth.USER_NO ASC, tnuserauth.AUTHOR_ID DESC ) ROW_NO FROM tnuserauth WHERE 1=1 ) tnuserauth WHERE 1=1
AND tnuserauth.ROW_NO BETWEEN #firstIndex# + 1 AND #lastIndex#
AND tnuserauth.ROW_NO <= #lastIndex# AND ROW_NO > #firstIndex#

/* userAuthDAO.selectUserAuthList */ /* 사용자권한 조회 */ SELECT * FROM (SELECT
ROWNUM AS rn,
ROW_NUMBER() OVER(ORDER BY $sidx$ $sord$) rn,
tb.* FROM ( SELECT tnsysuser.USER_NO ,tnsysuser.USER_NM ,tnsysuser.USER_ID ,tnsysuser.BRTHDY ,tnuserauth.AUTHOR_ID ,(SELECT tnauthority.AUTHOR_NM FROM TN_AUTHORITY tnauthority WHERE tnuserauth.AUTHOR_ID = tnauthority.AUTHOR_ID) AS AUTHOR_NM FROM TN_SYS_USER tnsysuser LEFT OUTER JOIN TN_USER_AUTH tnuserauth ON tnsysuser.USER_NO = tnuserauth.USER_NO
) tb) tnuserauth WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#


	/* userAuthDAO.selectUserAuthListTotalCount */
	/* 사용자권한 목록 개수 조회  */
	SELECT COUNT(*) AS totCnt
      FROM TN_SYS_USER tnsysuser LEFT OUTER JOIN TN_USER_AUTH tnuserauth
            ON tnsysuser.USER_NO = tnuserauth.USER_NO
