sqlMap.delete:

/* cntrwkSctnHistDAO.deleteCntrwkSctnHist */
/* 공사_구간_이력 삭제 */
DELETE FROM TH_CNTRWK_SCTN_HIST 
WHERE 1 = 1
AND CNTRWKSCTN_NO = #CNTRWKSCTN_NO# /* 공사_구간_이력.공사구간_번호 */

sqlMap.sql:
WHERE 1 = 1


	AND thcntrwksctnhist.CNTRWKSCTN_NO = #searchKeyword#


	AND thcntrwksctnhist.ROUTE_CODE LIKE '%' || #searchKeyword# || '%'

 /* 공사_구간_이력.공사구간_번호 */ AND thcntrwksctnhist.CNTRWKSCTN_NO = #CNTRWKSCTN_NO# 
 /* 공사_구간_이력.노선_코드 */ AND thcntrwksctnhist.ROUTE_CODE = #ROUTE_CODE# 
