sqlMap.select:
/* cntrwkSctnHistDAO.selectCntrwkSctnHistListPage */ /* 공사_구간_이력 조회(페이징) */ WITH thcntrwksctnhist AS ( SELECT thcntrwksctnhist.CNTRWKSCTN_NO /* 공사_구간_이력.공사구간_번호 */ , thcntrwksctnhist.ROUTE_CODE /* 공사_구간_이력.노선_코드 */ , thcntrwksctnhist.DIRECT_CODE /* 공사_구간_이력.행선_코드 */ , thcntrwksctnhist.TRACK /* 공사_구간_이력.차로 */ , thcntrwksctnhist.STRTPT /* 공사_구간_이력.시점 */ , thcntrwksctnhist.ENDPT /* 공사_구간_이력.종점 */ , thcntrwksctnhist.RPAIR_DE /* 공사_구간_이력.보수_일자 */ , thcntrwksctnhist.CRTR_NO /* 공사_구간_이력.생성자_번호 */ , thcntrwksctnhist.CREAT_DT /* 공사_구간_이력.생성_일시 */ , thcntrwksctnhist.DELETE_AT /* 공사_구간_이력.삭제_여부 */ , thcntrwksctnhist.USE_AT /* 공사_구간_이력.사용_여부 */ , thcntrwksctnhist.UPDUSR_NO /* 공사_구간_이력.수정자_번호 */ , thcntrwksctnhist.UPDT_DT /* 공사_구간_이력.수정_일시 */
FROM TH_CNTRWK_SCTN_HIST thcntrwksctnhist
WHERE 1 = 1
AND thcntrwksctnhist.CNTRWKSCTN_NO = #searchKeyword#
AND thcntrwksctnhist.ROUTE_CODE LIKE '%' || #searchKeyword# || '%'
/* 공사_구간_이력.공사구간_번호 */ AND thcntrwksctnhist.CNTRWKSCTN_NO = #CNTRWKSCTN_NO#
/* 공사_구간_이력.노선_코드 */ AND thcntrwksctnhist.ROUTE_CODE = #ROUTE_CODE#
/* 공사_구간_이력.행선_코드 */ AND thcntrwksctnhist.DIRECT_CODE = #DIRECT_CODE#
/* 공사_구간_이력.차로 */ AND thcntrwksctnhist.TRACK = #TRACK#
/* 공사_구간_이력.시점 */ AND thcntrwksctnhist.STRTPT = #STRTPT#
/* 공사_구간_이력.종점 */ AND thcntrwksctnhist.ENDPT = #ENDPT#
/* 공사_구간_이력.보수_일자 */ AND thcntrwksctnhist.RPAIR_DE = #RPAIR_DE#
/* 공사_구간_이력.생성자_번호 */ AND thcntrwksctnhist.CRTR_NO = #CRTR_NO#
/* 공사_구간_이력.생성_일시 */ AND thcntrwksctnhist.CREAT_DT = #CREAT_DT#
/* 공사_구간_이력.삭제_여부 */ AND thcntrwksctnhist.DELETE_AT = #DELETE_AT#
/* 공사_구간_이력.사용_여부 */ AND thcntrwksctnhist.USE_AT = #USE_AT#
/* 공사_구간_이력.수정자_번호 */ AND thcntrwksctnhist.UPDUSR_NO = #UPDUSR_NO#
/* 공사_구간_이력.수정_일시 */ AND thcntrwksctnhist.UPDT_DT = #UPDT_DT#
) SELECT thcntrwksctnhist.* FROM ( SELECT thcntrwksctnhist.CNTRWKSCTN_NO /* 공사_구간_이력.공사구간_번호 */ , thcntrwksctnhist.ROUTE_CODE /* 공사_구간_이력.노선_코드 */ , thcntrwksctnhist.DIRECT_CODE /* 공사_구간_이력.행선_코드 */ , thcntrwksctnhist.TRACK /* 공사_구간_이력.차로 */ , thcntrwksctnhist.STRTPT /* 공사_구간_이력.시점 */ , thcntrwksctnhist.ENDPT /* 공사_구간_이력.종점 */ , thcntrwksctnhist.RPAIR_DE /* 공사_구간_이력.보수_일자 */ , thcntrwksctnhist.CRTR_NO /* 공사_구간_이력.생성자_번호 */ , thcntrwksctnhist.CREAT_DT /* 공사_구간_이력.생성_일시 */ , thcntrwksctnhist.DELETE_AT /* 공사_구간_이력.삭제_여부 */ , thcntrwksctnhist.USE_AT /* 공사_구간_이력.사용_여부 */ , thcntrwksctnhist.UPDUSR_NO /* 공사_구간_이력.수정자_번호 */ , thcntrwksctnhist.UPDT_DT /* 공사_구간_이력.수정_일시 */ /* 공사_구간_이력.공사구간_번호 */ /* 공사_구간_이력.노선_코드 */ , ROW_NUMBER() OVER( ORDER BY thcntrwksctnhist.CNTRWKSCTN_NO ASC, thcntrwksctnhist.ROUTE_CODE DESC ) ROW_NO FROM thcntrwksctnhist WHERE 1=1 ) thcntrwksctnhist WHERE 1=1
AND thcntrwksctnhist.ROW_NO BETWEEN #firstIndex# + 1 AND #lastIndex#
AND thcntrwksctnhist.ROW_NO <= #lastIndex# AND ROW_NO > #firstIndex#

/* cntrwkSctnHistDAO.selectCntrwkSctnHistList */ /* 공사_구간_이력 조회 */ SELECT * FROM (SELECT ROWNUM AS rn, tb.* FROM ( SELECT thcntrwksctnhist.CNTRWKSCTN_NO /* 공사_구간_이력.공사구간_번호 */ , thcntrwksctnhist.ROUTE_CODE /* 공사_구간_이력.노선_코드 */ , thcntrwksctnhist.DIRECT_CODE /* 공사_구간_이력.행선_코드 */ , thcntrwksctnhist.TRACK /* 공사_구간_이력.차로 */ , thcntrwksctnhist.STRTPT /* 공사_구간_이력.시점 */ , thcntrwksctnhist.ENDPT /* 공사_구간_이력.종점 */ , thcntrwksctnhist.RPAIR_DE /* 공사_구간_이력.보수_일자 */ , thcntrwksctnhist.CRTR_NO /* 공사_구간_이력.생성자_번호 */ , thcntrwksctnhist.CREAT_DT /* 공사_구간_이력.생성_일시 */ , thcntrwksctnhist.DELETE_AT /* 공사_구간_이력.삭제_여부 */ , thcntrwksctnhist.USE_AT /* 공사_구간_이력.사용_여부 */ , thcntrwksctnhist.UPDUSR_NO /* 공사_구간_이력.수정자_번호 */ , thcntrwksctnhist.UPDT_DT /* 공사_구간_이력.수정_일시 */ FROM TH_CNTRWK_SCTN_HIST thcntrwksctnhist
ORDER BY thcntrwksctnhist.CNTRWKSCTN_NO DESC /* 공사_구간_이력.공사구간_번호 */ ) tb) thcntrwksctnhist WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#


/* cntrwkSctnHistDAO.selectCntrwkSctnHistListTotalCount */
/* 공사_구간_이력 목록 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TH_CNTRWK_SCTN_HIST thcntrwksctnhist
