sqlMap.delete:

/* attachFileDAO.deleteAttachFile */
/* 공통첨부파일 삭제 */
DELETE FROM TN_ATTACH_FILE 
WHERE 1 = 1
AND FILE_NO = #FILE_NO#::int /* 공통첨부파일.파일_번호 */

sqlMap.sql:
WHERE 1 = 1


	AND tnattachfile.FILE_NO = #searchKeyword#::int


	AND tnattachfile.FILE_NM LIKE '%' || #searchKeyword# || '%'

 /* 공통첨부파일.파일_번호 */ AND tnattachfile.FILE_NO = #FILE_NO#::int 
 /* 공통첨부파일.파일_명 */ AND tnattachfile.FILE_NM = #FILE_NM# 
 /* 공통첨부파일.원본_파일_명 */ AND tnattachfile.ORGINL_FILE_NM = #ORGINL_FILE_NM# 
 /* 공통첨부파일.파일_경로 */ AND tnattachfile.FILE_COURS = #FILE_COURS# 
 /* 공통첨부파일.파일_크기 */ AND tnattachfile.FILE_SIZE = #FILE_SIZE#::int 
 /* 공통첨부파일.사용_여부 */ AND tnattachfile.USE_AT = #USE_AT# 
 /* 공통첨부파일.삭제_여부 */ AND tnattachfile.DELETE_AT = #DELETE_AT# 
 /* 공통첨부파일.생성자_번호 */ AND tnattachfile.CRTR_NO = #CRTR_NO#::int 
 /* 공통첨부파일.생성_일시 */ AND tnattachfile.CREAT_DT = #CREAT_DT# 
 /* 공통첨부파일.수정자_번호 */ AND tnattachfile.UPDUSR_NO = #UPDUSR_NO#::int 
 /* 공통첨부파일.수정_일시 */ AND tnattachfile.UPDT_DT = #UPDT_DT#

sqlMap.select:
/* attachFileDAO.selectAttachFileListPage */ /* 공통첨부파일 조회(페이징) */ WITH tnattachfile AS ( SELECT tnattachfile.FILE_NO /* 공통첨부파일.파일_번호 */ , tnattachfile.FILE_NM /* 공통첨부파일.파일_명 */ , tnattachfile.ORGINL_FILE_NM /* 공통첨부파일.원본_파일_명 */ , tnattachfile.FILE_COURS /* 공통첨부파일.파일_경로 */ , tnattachfile.FILE_SIZE /* 공통첨부파일.파일_크기 */ , tnattachfile.USE_AT /* 공통첨부파일.사용_여부 */ , tnattachfile.DELETE_AT /* 공통첨부파일.삭제_여부 */ , tnattachfile.CRTR_NO /* 공통첨부파일.생성자_번호 */ , tnattachfile.CREAT_DT /* 공통첨부파일.생성_일시 */ , tnattachfile.UPDUSR_NO /* 공통첨부파일.수정자_번호 */ , tnattachfile.UPDT_DT /* 공통첨부파일.수정_일시 */
FROM TN_ATTACH_FILE tnattachfile
WHERE 1 = 1
AND tnattachfile.FILE_NO = #searchKeyword#::int
AND tnattachfile.FILE_NM LIKE '%' || #searchKeyword# || '%'
/* 공통첨부파일.파일_번호 */ AND tnattachfile.FILE_NO = #FILE_NO#::int
/* 공통첨부파일.파일_명 */ AND tnattachfile.FILE_NM = #FILE_NM#
/* 공통첨부파일.원본_파일_명 */ AND tnattachfile.ORGINL_FILE_NM = #ORGINL_FILE_NM#
/* 공통첨부파일.파일_경로 */ AND tnattachfile.FILE_COURS = #FILE_COURS#
/* 공통첨부파일.파일_크기 */ AND tnattachfile.FILE_SIZE = #FILE_SIZE#::int
/* 공통첨부파일.사용_여부 */ AND tnattachfile.USE_AT = #USE_AT#
/* 공통첨부파일.삭제_여부 */ AND tnattachfile.DELETE_AT = #DELETE_AT#
/* 공통첨부파일.생성자_번호 */ AND tnattachfile.CRTR_NO = #CRTR_NO#::int
/* 공통첨부파일.생성_일시 */ AND tnattachfile.CREAT_DT = #CREAT_DT#
/* 공통첨부파일.수정자_번호 */ AND tnattachfile.UPDUSR_NO = #UPDUSR_NO#::int
/* 공통첨부파일.수정_일시 */ AND tnattachfile.UPDT_DT = #UPDT_DT#
) SELECT tnattachfile.* FROM ( SELECT tnattachfile.FILE_NO /* 공통첨부파일.파일_번호 */ , tnattachfile.FILE_NM /* 공통첨부파일.파일_명 */ , tnattachfile.ORGINL_FILE_NM /* 공통첨부파일.원본_파일_명 */ , tnattachfile.FILE_COURS /* 공통첨부파일.파일_경로 */ , tnattachfile.FILE_SIZE /* 공통첨부파일.파일_크기 */ , tnattachfile.USE_AT /* 공통첨부파일.사용_여부 */ , tnattachfile.DELETE_AT /* 공통첨부파일.삭제_여부 */ , tnattachfile.CRTR_NO /* 공통첨부파일.생성자_번호 */ , tnattachfile.CREAT_DT /* 공통첨부파일.생성_일시 */ , tnattachfile.UPDUSR_NO /* 공통첨부파일.수정자_번호 */ , tnattachfile.UPDT_DT /* 공통첨부파일.수정_일시 */ /* 공통첨부파일.파일_번호 */ /* 공통첨부파일.파일_명 */ , ROW_NUMBER() OVER( ORDER BY tnattachfile.FILE_NO ASC, tnattachfile.FILE_NM DESC ) ROW_NO FROM tnattachfile WHERE 1=1 ) tnattachfile WHERE 1=1
AND tnattachfile.ROW_NO BETWEEN #firstIndex# + 1 AND #lastIndex#
AND tnattachfile.ROW_NO <= #lastIndex# AND ROW_NO > #firstIndex#

/* attachFileDAO.selectAttachFileList */ /* 공통첨부파일 조회 */ SELECT * FROM (SELECT ROW_NUMBER() OVER() AS rn, tb.* FROM ( SELECT tnattachfile.FILE_NO /* 공통첨부파일.파일_번호 */ , tnattachfile.FILE_NM /* 공통첨부파일.파일_명 */ , tnattachfile.ORGINL_FILE_NM /* 공통첨부파일.원본_파일_명 */ , tnattachfile.FILE_COURS /* 공통첨부파일.파일_경로 */ , tnattachfile.FILE_SIZE /* 공통첨부파일.파일_크기 */ , tnattachfile.USE_AT /* 공통첨부파일.사용_여부 */ , tnattachfile.DELETE_AT /* 공통첨부파일.삭제_여부 */ , tnattachfile.CRTR_NO /* 공통첨부파일.생성자_번호 */ , tnattachfile.CREAT_DT /* 공통첨부파일.생성_일시 */ , tnattachfile.UPDUSR_NO /* 공통첨부파일.수정자_번호 */ , tnattachfile.UPDT_DT /* 공통첨부파일.수정_일시 */ FROM TN_ATTACH_FILE tnattachfile
ORDER BY tnattachfile.FILE_NO DESC /* 공통첨부파일.파일_번호 */ ) tb) tnattachfile WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#


/* attachFileDAO.selectAttachFileListTotalCount */
/* 공통첨부파일 목록 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TN_ATTACH_FILE tnattachfile
