sqlMap.sql:
WHERE 1 = 1



        AND cellsect.GID = #searchKeyword#





 /* CELL_SECT.GID */ AND cellsect.GID = #GID#::int 
 /* CELL_SECT.OBJECT_ID */ AND cellsect.OBJECT_ID = #OBJECT_ID# 
 /* CELL_SECT.ROUTE_CODE */ AND cellsect.ROUTE_CODE = #ROUTE_CODE# 
 /* CELL_SECT.DIRECT_CODE */ AND cellsect.DIRECT_CODE = #DIRECT_CODE# 
 /* CELL_SECT.TRACK */ AND cellsect.TRACK = #TRACK# 
 /* CELL_SECT.STRTPT */ AND cellsect.STRTPT >= #STRTPT# 
 /* CELL_SECT.ENDPT */ AND cellsect.ENDPT <= #ENDPT# 
 /* CELL_SECT.DEPT_CODE */ AND cellsect.DEPT_CODE = #DEPT_CODE# 
 /* CELL_SECT.CELL_TYPE */ AND cellsect.CELL_TYPE = #CELL_TYPE# 
 /* CELL_SECT.VMTC_GRAD */ AND cellsect.VMTC_GRAD = #VMTC_GRAD# 
 /* CELL_SECT.ROAD_GRAD */ AND cellsect.ROAD_GRAD = #ROAD_GRAD# 
 /* CELL_SECT.ADM_CODE */ AND (cellsect.ADM_CODE || '00000') = #ADM_CODE# 
 /* CELL_SECT.CREAT_DT */ AND cellsect.CREAT_DT = #CREAT_DT# 
 /* CELL_SECT.UPDUSR_NO */ AND cellsect.UPDUSR_NO = #UPDUSR_NO# 
 /* CELL_SECT.UPDT_DT */ AND cellsect.UPDT_DT = #UPDT_DT#

sqlMap.select:
/* cellSectDAO.selectCellSectList */ /* CELL_SECT 조회 */ SELECT * FROM (SELECT ROW_NUMBER() OVER() AS rn, tb.* FROM ( SELECT cellsect.GID /* CELL_SECT.GID */ , cellsect.OBJECT_ID /* CELL_SECT.OBJECT_ID */ , cellsect.ROUTE_CODE::int AS ROUTE_CODE /* CELL_SECT.노선번호 */ , (SELECT ROAD_NAME FROM TN_ROUTE_INFO WHERE ROAD_NO = cellsect.ROUTE_CODE ) ROUTE_NM/* 공사상세정보.노선_코드 */ , cellsect.DIRECT_CODE /* CELL_SECT.행선 */ , cellsect.CELL_TYPE/* CELL_SECT.섹션구분 */ , (SELECT tccode.CODE_NM FROM TC_CODE tccode WHERE tccode.CODE_VAL = cellsect.CELL_TYPE AND tccode.CL_CODE = 'CELT' AND tccode.DELETE_AT='N' AND tccode.USE_AT='Y') CELL_TYPE_NM /*섹션구분명 */ , cellsect.TRACK /* CELL_SECT.차로 */ , cellsect.STRTPT /* CELL_SECT.시점 */ , cellsect.ENDPT /* CELL_SECT.종점 */ , (SELECT tcdept.LOWEST_DEPT_NM FROM TC_DEPT tcdept WHERE tcdept.DEPT_CODE = cellsect.DEPT_CODE::text AND tcdept.DELETE_AT='N' AND tcdept.USE_AT='Y') DEPT_CODE /* CELL_SECT.관리기관 */ , (SELECT tccode.CODE_NM FROM TC_CODE tccode WHERE tccode.CODE_VAL = cellsect.VMTC_GRAD AND tccode.CL_CODE = 'VNTC' AND tccode.DELETE_AT='N' AND tccode.USE_AT='Y') VMTC_GRAD /* CELL_SECT.교통용량 */ , (SELECT tccode.CODE_NM FROM TC_CODE tccode WHERE tccode.CODE_VAL = cellsect.ROAD_GRAD AND tccode.CL_CODE = 'RDGD' AND tccode.DELETE_AT='N' AND tccode.USE_AT='Y') ROAD_GRAD /* CELL_SECT.도로등급 */ , (SELECT n3ag.NAME FROM N3A_G0100000 n3ag WHERE n3ag.BJCD = concat(cellsect.ADM_CODE , '00000') ) ADM_CODE /* CELL_SECT.행정구역 */ , cellsect.CREAT_DT /* CELL_SECT.등록일 */ , cellsect.UPDUSR_NO /* CELL_SECT.수정자번호 */ , cellsect.UPDT_DT /* CELL_SECT.수정일 */ , '' as VNTC_GR/*교통량_임의컬럼명*/ FROM CELL_SECT cellsect
WHERE 1=1 /* CELL_SECT.OBJECT_ID */ AND cellsect.OBJECT_ID IN( $OBJECT_ID$ ) /* CELL_SECT.GID */ AND cellsect.GID IN( $GID$ )
ORDER BY $orderByColumn$ $orderByType$
) tb)as sub_CellSect WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#

/* cellSectDAO.selectCellSectListTotalCount */ /* CELL_SECT 조회 수 */ SELECT COUNT(*) FROM ( SELECT cellsect.GID /* CELL_SECT.GID */ , cellsect.OBJECT_ID /* CELL_SECT.OBJECT_ID */ , cellsect.ROUTE_CODE::int AS ROUTE_CODE /* CELL_SECT.노선번호 */ , (SELECT ROAD_NAME FROM TN_ROUTE_INFO WHERE ROAD_NO = cellsect.ROUTE_CODE ) ROUTE_NM/* 공사상세정보.노선_코드 */ , cellsect.DIRECT_CODE /* CELL_SECT.행선 */ , (SELECT tccode.CODE_NM FROM TC_CODE tccode WHERE tccode.CODE_VAL = cellsect.CELL_TYPE AND tccode.CL_CODE = 'SECT' AND tccode.DELETE_AT='N' AND tccode.USE_AT='Y') CELL_TYPE /* CELL_SECT.섹션구분 */ , cellsect.TRACK /* CELL_SECT.차로 */ , cellsect.STRTPT /* CELL_SECT.시점 */ , cellsect.ENDPT /* CELL_SECT.종점 */ , (SELECT tcdept.LOWEST_DEPT_NM FROM TC_DEPT tcdept WHERE tcdept.DEPT_CODE = cellsect.DEPT_CODE::text AND tcdept.DELETE_AT='N' AND tcdept.USE_AT='Y') DEPT_CODE /* CELL_SECT.관리기관 */ , (SELECT tccode.CODE_NM FROM TC_CODE tccode WHERE tccode.CODE_VAL = cellsect.VMTC_GRAD AND tccode.CL_CODE = 'VNTC' AND tccode.DELETE_AT='N' AND tccode.USE_AT='Y') VMTC_GRAD /* CELL_SECT.교통용량 */ , (SELECT tccode.CODE_NM FROM TC_CODE tccode WHERE tccode.CODE_VAL = cellsect.ROAD_GRAD AND tccode.CL_CODE = 'RDGD' AND tccode.DELETE_AT='N' AND tccode.USE_AT='Y') ROAD_GRAD /* CELL_SECT.도로등급 */ , (SELECT n3ag.NAME FROM N3A_G0100000 n3ag WHERE n3ag.BJCD = concat(cellsect.ADM_CODE , '00000') ) ADM_CODE /* CELL_SECT.행정구역 */ , cellsect.CREAT_DT /* CELL_SECT.등록일 */ , cellsect.UPDUSR_NO /* CELL_SECT.수정자번호 */ , cellsect.UPDT_DT /* CELL_SECT.수정일 */ FROM CELL_SECT cellsect
WHERE 1=1 /* CELL_SECT.OBJECT_ID */ AND cellsect.OBJECT_ID IN( $OBJECT_ID$ )
ORDER BY $orderByColumn$ $orderByType$
) tb

sqlMap.update:

/* cellSectDAO.updateSectSe */
/* 섹션구분 수정 */
UPDATE CELL_SECT
   SET  CELL_TYPE = #CELL_TYPE# /* 섹션구분 */
WHERE GID = #GID#::int /* GID */

sqlMap.select:
/* cellSectDAO.selectCellSectLenArea */
/* 총 노선 연장 및 면적 조회 */
SELECT ROUND(SUM(ROAD_L), 0) ROAD_L
     , ROUND(SUM(ROAD_A), 0) ROAD_A
  FROM CELL_SECT
