sqlMap.delete:

/* clCodeDAO.deleteClCode */
/* 분류코드 삭제 */
DELETE FROM TC_CL_CODE 	
WHERE 1 = 1
AND CL_CODE = #CL_CODE# /* 분류코드.분류_코드 */

sqlMap.sql:
WHERE 1 = 1
AND tcclcode.DELETE_AT = 'N'

	AND tcclcode.CL_CODE = #searchKeyword#


	AND tcclcode.PRIOR_RANK LIKE '%' || #searchKeyword# || '%'

 /* 분류코드.분류_코드 */ AND tcclcode.CL_CODE = #CL_CODE# 
 /* 분류코드.우선_순위 */ AND tcclcode.PRIOR_RANK = #PRIOR_RANK# 
 /* 분류코드.분류_코드_명 */ AND tcclcode.CL_CODE_NM LIKE '%' || #CL_CODE_NM# || '%' 
 /* 분류코드.내용 */ AND tcclcode.CN = #CN# 
 /* 분류코드.사용_여부 */ AND tcclcode.USE_AT = #USE_AT# 
 /* 분류코드.삭제_여부 */ AND tcclcode.DELETE_AT = #DELETE_AT# 
 /* 분류코드.생성자_번호 */ AND tcclcode.CRTR_NO = #CRTR_NO# 
 /* 분류코드.생성_일시 */ AND tcclcode.CREAT_DT = #CREAT_DT# 
 /* 분류코드.수정자_번호 */ AND tcclcode.UPDUSR_NO = #UPDUSR_NO# 
 /* 분류코드.수정_일시 */ AND tcclcode.UPDT_DT = #UPDT_DT#

sqlMap.select:
/* clCodeDAO.selectClCodeListPage */ /* 분류코드 조회(페이징) */ WITH tcclcode AS ( SELECT tcclcode.CL_CODE /* 분류코드.분류_코드 */ , tcclcode.PRIOR_RANK /* 분류코드.우선_순위 */ , tcclcode.CL_CODE_NM /* 분류코드.분류_코드_명 */ , tcclcode.CN /* 분류코드.내용 */ , tcclcode.USE_AT /* 분류코드.사용_여부 */ , tcclcode.DELETE_AT /* 분류코드.삭제_여부 */ , tcclcode.CRTR_NO /* 분류코드.생성자_번호 */ , tcclcode.CREAT_DT /* 분류코드.생성_일시 */ , tcclcode.UPDUSR_NO /* 분류코드.수정자_번호 */ , tcclcode.UPDT_DT /* 분류코드.수정_일시 */
FROM TC_CL_CODE tcclcode
WHERE 1 = 1
AND tcclcode.CL_CODE = #searchKeyword#
AND tcclcode.PRIOR_RANK LIKE '%' || #searchKeyword# || '%'
/* 분류코드.분류_코드 */ AND tcclcode.CL_CODE = #CL_CODE#
/* 분류코드.우선_순위 */ AND tcclcode.PRIOR_RANK = #PRIOR_RANK#
/* 분류코드.분류_코드_명 */ AND tcclcode.CL_CODE_NM = #CL_CODE_NM#
/* 분류코드.내용 */ AND tcclcode.CN = #CN#
/* 분류코드.사용_여부 */ AND tcclcode.USE_AT = #USE_AT#
/* 분류코드.삭제_여부 */ AND tcclcode.DELETE_AT = #DELETE_AT#
/* 분류코드.생성자_번호 */ AND tcclcode.CRTR_NO = #CRTR_NO#
/* 분류코드.생성_일시 */ AND tcclcode.CREAT_DT = #CREAT_DT#
/* 분류코드.수정자_번호 */ AND tcclcode.UPDUSR_NO = #UPDUSR_NO#
/* 분류코드.수정_일시 */ AND tcclcode.UPDT_DT = #UPDT_DT#
) SELECT tcclcode.* , (SELECT tnsysuser.USER_ID /* 시스템사용자.사용자_ID */ FROM TN_SYS_USER tnsysuser WHERE 1 = 1 AND tnsysuser.USER_NO = tcclcode.UPDUSR_NO /* 시스템사용자.사용자_번호 */ AND tnsysuser.USE_AT = 'Y' AND tnsysuser.CONFM_AT = 'Y' AND tnsysuser.DELETE_AT = 'N' ) UPDUSR_ID FROM ( SELECT tcclcode.CL_CODE /* 분류코드.분류_코드 */ , tcclcode.PRIOR_RANK /* 분류코드.우선_순위 */ , tcclcode.CL_CODE_NM /* 분류코드.분류_코드_명 */ , tcclcode.CN /* 분류코드.내용 */ , tcclcode.USE_AT /* 분류코드.사용_여부 */ , tcclcode.DELETE_AT /* 분류코드.삭제_여부 */ , tcclcode.CRTR_NO /* 분류코드.생성자_번호 */ , tcclcode.CREAT_DT /* 분류코드.생성_일시 */ , tcclcode.UPDUSR_NO /* 분류코드.수정자_번호 */ , tcclcode.UPDT_DT /* 분류코드.수정_일시 */ /* 분류코드.분류_코드 */ /* 분류코드.우선_순위 */ , ROW_NUMBER() OVER( ORDER BY tcclcode.CL_CODE_NM ASC, tcclcode.PRIOR_RANK ASC ) ROW_NO FROM tcclcode WHERE 1=1 ) tcclcode WHERE 1=1
AND tcclcode.ROW_NO BETWEEN #firstIndex# + 1 AND #lastIndex#
AND tcclcode.ROW_NO <= #lastIndex# AND ROW_NO > #firstIndex#


/* clCodeDAO.selectClCodeListTotalCount */
/* 분류코드 목록 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TC_CL_CODE tcclcode
