AND tncntrwkdtl.ROW_NO BETWEEN #firstIndex# + 1 AND #lastIndex#
AND tncntrwkdtl.ROW_NO <= #lastIndex# AND ROW_NO > #firstIndex#

/* cntrwkDtlDAO.selectCntrwkDtlList */ /* 공사상세정보 조회 */ SELECT * FROM (SELECT ROW_NUMBER() OVER() AS rn, tb.* FROM ( SELECT cell10.CELL_ID/*CELL_10.셀관리번호*/ , tncntrwkdtl.DETAIL_CNTRWK_ID /* 공사상세정보.세부_공사_ID */ , tncntrwkdtl.CNTRWK_ID /* 공사상세정보.공사_ID */ , tncntrwkdtl.RPAIR_MTHD_CODE /* 공사상세정보.보수_공법_코드 */ , tncntrwkdtl.DETAIL_CNTRWK_GROUP /* 공사상세정보.세부_공사_그룹 */ , tncntrwkdtl.DETAIL_CNTRWK_NM /* 공사상세정보.세부_공사_명 */ , tncntrwkdtl.TRACK_LEN /* 공사상세정보.1차로_연장 */ , tncntrwkdtl.SPRVISN_CO_NO /* 공사상세정보.감리_업체_번호 */ , tncntrwkdtl.SPRVISN_CO_NM /* 공사상세정보.감리_업체_명 */ , tncntrwkdtl.SPRVISN_CO_RPRSNT_NO /* 공사상세정보.감리_업체_대표_번호 */ , tncntrwkdtl.SPRVISN_CO_RPRSNTV_NM /* 공사상세정보.감리_업체_대표자_명 */ , tncntrwkdtl.SPRVISOR_NM /* 공사상세정보.감리원_명 */ , tncntrwkdtl.SPRVISOR_TELNO /* 공사상세정보.감리원_전화번호 */ , tncntrwkdtl.CNSTRCT_CO_NO /* 공사상세정보.시공_업체_번호 */ , tncntrwkdtl.CNSTRCT_CO_NM /* 공사상세정보.시공_업체_명 */ , tncntrwkdtl.CNSTRCT_CO_RPRSNTV_NM /* 공사상세정보.시공_업체_대표자_명 */ , tncntrwkdtl.CNSTRCT_CO_TELNO /* 공사상세정보.시공_업체_전화번호 */ , tncntrwkdtl.CNSTRCT_CO_CHARGER_NM /* 공사상세정보.시공_업체_담당자_명 */ , tncntrwkdtl.SPT_HDCH_NM /* 공사상세정보.현장_소장_명 */ , tncntrwkdtl.SPT_HDCH_TELNO /* 공사상세정보.현장_소장_전화번호 */ , tncntrwkdtl.ROAD_NM /* 공사상세정보.도로_명 */ , tncntrwkdtl.ROUTE_CODE /* 공사상세정보.노선_코드 */ , (SELECT ROAD_NAME FROM TN_ROUTE_INFO WHERE ROAD_NO = tncntrwkdtl.ROUTE_CODE ) ROUTE_NM/* 공사상세정보.노선_코드 */ , tncntrwkdtl.DIRECT_CODE /* 공사상세정보.행선_코드 */ , CASE WHEN tncntrwkdtl.DIRECT_CODE = 'S' THEN '상행' WHEN tncntrwkdtl.DIRECT_CODE = 'E' THEN '하행' WHEN tncntrwkdtl.DIRECT_CODE = 'SE' THEN '양방향' END DIRECT_NM , tncntrwkdtl.TRACK /* 공사상세정보.차로 */ , tncntrwkdtl.STRTPT /* 공사상세정보.시점 */ , tncntrwkdtl.ENDPT /* 공사상세정보.종점 */ , tncntrwkdtl.CNTRWK_STRTPT_NM /* 공사상세정보.공사_시점_명 */ , tncntrwkdtl.CNTRWK_ENDPT_NM /* 공사상세정보.공사_종점_명 */ , tncntrwkdtl.CNTRWK_AMOUNT /* 공사상세정보.공사_금액 */ , tncntrwkdtl.GVSLCT /* 공사상세정보.관급비 */ , tncntrwkdtl.OUTSRCCT /* 공사상세정보.도급비 */ , tncntrwkdtl.ETC_RLOCATCT /* 공사상세정보.기타_이설비 */ , tncntrwkdtl.CNTRWK_TMPRT /* 공사상세정보.공사_기온 */ , tncntrwkdtl.CNTRWK_WETHR /* 공사상세정보.공사_날씨 */ , tncntrwkdtl.CNTRWK_TIME /* 공사상세정보.공사_시간 */ , tncntrwkdtl.CNTRWK_CL /* 공사상세정보.공사_분류 */ , tncntrwkdtl.BUS_DVR_TRACK_AT /* 공사상세정보.버스_전용_차로_여부 */ , tncntrwkdtl.BEFORE_PAV_YEAR /* 공사상세정보.직전_포장_연도 */ , tncntrwkdtl.RPAIR_BEGIN_DE /* 공사상세정보.보수_시작_일자 */ , tncntrwkdtl.RPAIR_END_DE /* 공사상세정보.보수_종료_일자 */ , tncntrwkdtl.RPAIR_AR /* 공사상세정보.보수_면적 */ , tncntrwkdtl.RPAIR_BT /* 공사상세정보.보수_폭 */ , tncntrwkdtl.PAV_STLE_CODE /* 공사상세정보.포장_형태_코드 */ , tncntrwkdtl.PAV_MSRC_ETC /* 공사상세정보.포장_공법_기타 */ , tncntrwkdtl.RPAIR_THICK_DC /* 공사상세정보.보수_두께_설명 */ , tncntrwkdtl.RPAIR_THICK /* 공사상세정보.보수_두께 */ , tncntrwkdtl.RPAIR_THICK_BASE /* 공사상세정보.보수_두께_기층 */ , tncntrwkdtl.RPAIR_THICK_CNTR /* 공사상세정보.보수_두께_중간층 */ , tncntrwkdtl.RPAIR_THICK_ASCON /* 공사상세정보.보수_두께_표층 */ , tncntrwkdtl.PAV_MATRL_ASCON_ETC /* 공사상세정보.포장_재료_표층_기타 */ , tncntrwkdtl.PAV_MATRL_BASE_CODE /* 공사상세정보.포장_재료_기층_코드 */ , tncntrwkdtl.PAV_MATRL_BASE_ETC /* 공사상세정보.포장_재료_기층_기타 */ , tncntrwkdtl.PAV_MATRL_CNTR_CODE /* 공사상세정보.포장_재료_중간층_코드 */ , tncntrwkdtl.PAV_MATRL_CNTR_ETC /* 공사상세정보.포장_재료_중간층_기타 */ , tncntrwkdtl.RPAIR_MATRL_DETAIL_NM /* 공사상세정보.보수_재료_세부_명 */ , tncntrwkdtl.RPAIR_MATRL_PRDCT_CO_NM /* 공사상세정보.보수_재료_생산_업체_명 */ , tncntrwkdtl.PRDCT_CO_TELNO /* 공사상세정보.생산_업체_전화번호 */ , tncntrwkdtl.ASPAT_RNS_CRRSPND_AT /* 공사상세정보.아스팔트_실명제_해당_여부 */ , tncntrwkdtl.RM /* 공사상세정보.비고 */ , tncntrwkdtl.PROCESS_RESULT_MSSAGE /* 공사상세정보.처리_결과_메세지 */ , tncntrwkdtl.OPERT_BFE_PHOTO_NO /* 공사상세정보.작업_전_사진_번호 */ , tncntrwkdtl.OPERT_AFT_PHOTO_NO /* 공사상세정보.작업_후_사진_번호 */ , tncntrwkdtl.FILE_NO /* 공사상세정보.파일_번호 */ , tncntrwkdtl.CONFM_STTUS_CODE /* 공사상세정보.승인_상태_코드 */ , tncntrwkdtl.CONFM_DT /* 공사상세정보.승인_일시 */ , tncntrwkdtl.CONFMER_NO /* 공사상세정보.승인자_번호 */ , tncntrwkdtl.EXTRL_REGIST_AT /* 공사상세정보.외부_등록_여부 */ , tncntrwkdtl.USE_AT /* 공사상세정보.사용_여부 */ , tncntrwkdtl.DELETE_AT /* 공사상세정보.삭제_여부 */ , tncntrwkdtl.CRTR_NO /* 공사상세정보.생성자_번호 */ , tncntrwkdtl.CREAT_DT /* 공사상세정보.생성_일시 */ , tncntrwkdtl.UPDUSR_NO /* 공사상세정보.수정자_번호 */ , tncntrwkdtl.UPDT_DT /* 공사상세정보.수정_일시 */ , tncntrwkdtl.PAV_MATRL_ASCON_CODE /* 공사상세정보.포장_재료_표층_코드 */ , tncntrwkdtl.PAINT_MSRC /* 공사상세정보.차선_도색_공법 */ , tncntrwkdtl.PAINT_YLWSLLN_AR /* 공사상세정보.차선_도색_황색실선_면적 */ , tncntrwkdtl.PAINT_YLWDASHLN_AR /* 공사상세정보.차선_도색_황색파선_면적 */ , tncntrwkdtl.PAINT_WHSLLN_AR /* 공사상세정보.차선_도색_백색실선_면적 */ , tncntrwkdtl.PAINT_CRSLK_CO /* 공사상세정보.차선_도색_횡단보도_개수 */ , tncntrwkdtl.PAINT_STOPLN_CO /* 공사상세정보.차선_도색_정지선_개수 */ , tncntrwkdtl.PAINT_STOPLN_AR /* 공사상세정보.차선_도색_정지선_면적 */ , tncntrwkdtl.PAINT_SPDBMP_CO /* 공사상세정보.차선_도색_방지턱_개수 */ , tncntrwkdtl.PAINT_SPDBMP_AR /* 공사상세정보.차선_도색_방지턱_면적 */ , tncntrwkdtl.PAINT_WHDASHLN_AR /* 공사상세정보.차선_도색_백색파선_면적 */ , tncntrwkdtl.PAINT_CHRCTRSYMBL_CO /* 공사상세정보.차선_도색_문자기호_개수 */ , tncntrwkdtl.PAINT_CHRCTRSYMBL_AR /* 공사상세정보.차선_도색_문자기호_면적 */ , tncntrwkdtl.PAINT_CRSLK_AR /* 공사상세정보.차선_도색_횡단보도_면적 */ FROM TN_CNTRWK_DTL tncntrwkdtl LEFT OUTER JOIN CELL_10 cell10 ON tncntrwkdtl.DIRECT_CODE = cell10.DIRECT_CODE AND tncntrwkdtl.ROUTE_CODE = cell10.ROUTE_CODE AND tncntrwkdtl.TRACK = cell10.TRACK AND tncntrwkdtl.STRTPT = cell10.STRTPT AND tncntrwkdtl.ENDPT = cell10.ENDPT WHERE tncntrwkdtl.delete_at = 'N' AND tncntrwkdtl.use_at = 'Y'
/* 공사상세정보.공사_ID */ AND tncntrwkdtl.CNTRWK_ID = #CNTRWK_ID#
ORDER BY $orderByColumn$ $orderByType$
) tb) tncntrwkdtl WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#


		/* cntrwkDtlDAO.selectCntrwkDtlListTotalCount */
		/* 공사상세정보 목록 개수 조회  */
		SELECT COUNT(*) AS totCnt
		FROM TN_CNTRWK_DTL tncntrwkdtl
		WHERE tncntrwkdtl.delete_at = 'N'
   			  AND tncntrwkdtl.use_at = 'Y'

 /* 공사상세정보.공사_ID */ AND tncntrwkdtl.CNTRWK_ID = #CNTRWK_ID#

/* cntrwkDtlDAO.selectCntrwkDtlListExcel */ /* 상세정보목록 엑셀조회 */ SELECT * FROM (SELECT ROW_NUMBER() OVER() AS rn, tb.* FROM ( SELECT tncntrwkdtl.DETAIL_CNTRWK_ID /* 공사상세정보.세부_공사_ID */ , tncntrwkdtl.CNTRWK_ID /* 공사상세정보.공사_ID */ , tncntrwkdtl.RPAIR_MTHD_CODE /* 공사상세정보.보수_공법_코드 */ , tncntrwkdtl.DETAIL_CNTRWK_GROUP /* 공사상세정보.세부_공사_그룹 */ , tncntrwkdtl.DETAIL_CNTRWK_NM /* 공사상세정보.세부_공사_명 */ , REGEXP_REPLACE(REVERSE(REGEXP_REPLACE( REVERSE(TO_CHAR(ROUND(tncntrwkdtl.TRACK_LEN,0))), '([0-9]{3})','\1,')), '^,','') AS TRACK_LEN /* 공사상세정보.1차로_연장 */ , tncntrwkdtl.SPRVISN_CO_NO /* 공사상세정보.감리_업체_번호 */ , COALESCE((SELECT CO_NM FROM TN_COMPANY tncompany WHERE tncompany.CO_NO = tncntrwkdtl.SPRVISN_CO_NO), tncntrwkdtl.SPRVISN_CO_NM) AS SPRVISN_CO_NM /* 공사상세정보.감리_업체_명 */ , tncntrwkdtl.SPRVISN_CO_RPRSNT_NO /* 공사상세정보.감리_업체_대표_번호 */ , tncntrwkdtl.SPRVISN_CO_RPRSNTV_NM /* 공사상세정보.감리_업체_대표자_명 */ , tncntrwkdtl.SPRVISOR_NM /* 공사상세정보.감리원_명 */ , tncntrwkdtl.SPRVISOR_TELNO /* 공사상세정보.감리원_전화번호 */ , tncntrwkdtl.CNSTRCT_CO_NO /* 공사상세정보.시공_업체_번호 */ , COALESCE((SELECT CO_NM FROM TN_COMPANY tncompany WHERE tncompany.CO_NO = tncntrwkdtl.CNSTRCT_CO_NO), tncntrwkdtl.CNSTRCT_CO_NM) AS CNSTRCT_CO_NM /* 공사상세정보.시공_업체_명 */ , tncntrwkdtl.CNSTRCT_CO_RPRSNTV_NM /* 공사상세정보.시공_업체_대표자_명 */ , tncntrwkdtl.CNSTRCT_CO_TELNO /* 공사상세정보.시공_업체_전화번호 */ , tncntrwkdtl.CNSTRCT_CO_CHARGER_NM /* 공사상세정보.시공_업체_담당자_명 */ , tncntrwkdtl.SPT_HDCH_NM /* 공사상세정보.현장_소장_명 */ , tncntrwkdtl.SPT_HDCH_TELNO /* 공사상세정보.현장_소장_전화번호 */ , tncntrwkdtl.ROAD_NM /* 공사상세정보.도로_명 */ , LTRIM(tncntrwkdtl.ROUTE_CODE,'0') AS ROUTE_CODE/* 공사상세정보.노선_코드 */ , (SELECT ROAD_NAME FROM TN_ROUTE_INFO WHERE ROAD_NO = tncntrwkdtl.ROUTE_CODE ) ROUTE_NM/* 공사상세정보.노선_명 */ , tncntrwkdtl.DIRECT_CODE/* 공사상세정보.행선_코드 */ , CASE WHEN tncntrwkdtl.DIRECT_CODE = 'S' THEN '상행' ELSE '하행' END AS DIRECT_NM/* 행선명 */ , tncntrwkdtl.TRACK /* 공사상세정보.차로 */ , REGEXP_REPLACE(REVERSE(REGEXP_REPLACE( REVERSE(TO_CHAR(ROUND(tncntrwkdtl.STRTPT,0))), '([0-9]{3})','\1,')), '^,','') AS STRTPT /* 공사상세정보.시점 */ , REGEXP_REPLACE(REVERSE(REGEXP_REPLACE( REVERSE(TO_CHAR(ROUND(tncntrwkdtl.ENDPT,0))), '([0-9]{3})','\1,')), '^,','') AS ENDPT /* 공사상세정보.종점 */ , tncntrwkdtl.CNTRWK_STRTPT_NM /* 공사상세정보.공사_시점_명 */ , tncntrwkdtl.CNTRWK_ENDPT_NM /* 공사상세정보.공사_종점_명 */ , REGEXP_REPLACE(REVERSE(REGEXP_REPLACE( REVERSE(TO_CHAR(ROUND(tncntrwkdtl.CNTRWK_AMOUNT,0))), '([0-9]{3})','\1,')), '^,','') AS CNTRWK_AMOUNT /* 공사상세정보.공사_금액 */ , tncntrwkdtl.GVSLCT /* 공사상세정보.관급비 */ , tncntrwkdtl.OUTSRCCT /* 공사상세정보.도급비 */ , tncntrwkdtl.ETC_RLOCATCT /* 공사상세정보.기타_이설비 */ , tncntrwkdtl.CNTRWK_TMPRT /* 공사상세정보.공사_기온 */ , tncntrwkdtl.CNTRWK_WETHR /* 공사상세정보.공사_날씨 */ , tncntrwkdtl.CNTRWK_TIME /* 공사상세정보.공사_시간 */ , tncntrwkdtl.CNTRWK_CL /* 공사상세정보.공사_분류 */ , tncntrwkdtl.BUS_DVR_TRACK_AT /* 공사상세정보.버스_전용_차로_여부 */ , tncntrwkdtl.BEFORE_PAV_YEAR /* 공사상세정보.직전_포장_연도 */ , tncntrwkdtl.RPAIR_BEGIN_DE /* 공사상세정보.보수_시작_일자 */ , tncntrwkdtl.RPAIR_END_DE /* 공사상세정보.보수_종료_일자 */ , REGEXP_REPLACE(REVERSE(REGEXP_REPLACE( REVERSE(TO_CHAR(ROUND(tncntrwkdtl.RPAIR_AR,2))), '([0-9]{3})','\1,')), '^,','') AS RPAIR_AR /*공사상세정보.보수_면적 */ , tncntrwkdtl.RPAIR_BT /* 공사상세정보.보수_폭 */ , tncntrwkdtl.PAV_STLE_CODE /* 공사상세정보.포장_형태_코드 */ , tncntrwkdtl.PAV_MSRC_ETC /* 공사상세정보.포장_공법_기타 */ , tncntrwkdtl.RPAIR_THICK_DC /* 공사상세정보.보수_두께_설명 */ , tncntrwkdtl.RPAIR_THICK /* 공사상세정보.보수_두께 */ , tncntrwkdtl.RPAIR_THICK_BASE /* 공사상세정보.보수_두께_기층 */ , tncntrwkdtl.RPAIR_THICK_CNTR /* 공사상세정보.보수_두께_중간층 */ , tncntrwkdtl.RPAIR_THICK_ASCON /* 공사상세정보.보수_두께_표층 */ , tncntrwkdtl.PAV_MATRL_ASCON_ETC /* 공사상세정보.포장_재료_표층_기타 */ , tncntrwkdtl.PAV_MATRL_BASE_CODE /* 공사상세정보.포장_재료_기층_코드 */ , tncntrwkdtl.PAV_MATRL_BASE_ETC /* 공사상세정보.포장_재료_기층_기타 */ , tncntrwkdtl.PAV_MATRL_CNTR_CODE /* 공사상세정보.포장_재료_중간층_코드 */ , tncntrwkdtl.PAV_MATRL_CNTR_ETC /* 공사상세정보.포장_재료_중간층_기타 */ , tncntrwkdtl.RPAIR_MATRL_DETAIL_NM /* 공사상세정보.보수_재료_세부_명 */ , tncntrwkdtl.RPAIR_MATRL_PRDCT_CO_NM /* 공사상세정보.보수_재료_생산_업체_명 */ , tncntrwkdtl.PRDCT_CO_TELNO /* 공사상세정보.생산_업체_전화번호 */ , tncntrwkdtl.ASPAT_RNS_CRRSPND_AT /* 공사상세정보.아스팔트_실명제_해당_여부 */ , tncntrwkdtl.RM /* 공사상세정보.비고 */ , tncntrwkdtl.PROCESS_RESULT_MSSAGE /* 공사상세정보.처리_결과_메세지 */ , tncntrwkdtl.OPERT_BFE_PHOTO_NO /* 공사상세정보.작업_전_사진_번호 */ , tncntrwkdtl.OPERT_AFT_PHOTO_NO /* 공사상세정보.작업_후_사진_번호 */ , tncntrwkdtl.FILE_NO /* 공사상세정보.파일_번호 */ , tncntrwkdtl.CONFM_STTUS_CODE /* 공사상세정보.승인_상태_코드 */ , tncntrwkdtl.CONFM_DT /* 공사상세정보.승인_일시 */ , tncntrwkdtl.CONFMER_NO /* 공사상세정보.승인자_번호 */ , tncntrwkdtl.EXTRL_REGIST_AT /* 공사상세정보.외부_등록_여부 */ , tncntrwkdtl.USE_AT /* 공사상세정보.사용_여부 */ , tncntrwkdtl.DELETE_AT /* 공사상세정보.삭제_여부 */ , tncntrwkdtl.CRTR_NO /* 공사상세정보.생성자_번호 */ , tncntrwkdtl.CREAT_DT /* 공사상세정보.생성_일시 */ , tncntrwkdtl.UPDUSR_NO /* 공사상세정보.수정자_번호 */ , tncntrwkdtl.UPDT_DT /* 공사상세정보.수정_일시 */ , tncntrwkdtl.PAV_MATRL_ASCON_CODE /* 공사상세정보.포장_재료_표층_코드 */ , tncntrwkdtl.PAINT_MSRC /* 공사상세정보.차선_도색_공법 */ , tncntrwkdtl.PAINT_YLWSLLN_AR /* 공사상세정보.차선_도색_황색실선_면적 */ , tncntrwkdtl.PAINT_YLWDASHLN_AR /* 공사상세정보.차선_도색_황색파선_면적 */ , tncntrwkdtl.PAINT_WHSLLN_AR /* 공사상세정보.차선_도색_백색실선_면적 */ , tncntrwkdtl.PAINT_CRSLK_CO /* 공사상세정보.차선_도색_횡단보도_개수 */ , tncntrwkdtl.PAINT_STOPLN_CO /* 공사상세정보.차선_도색_정지선_개수 */ , tncntrwkdtl.PAINT_STOPLN_AR /* 공사상세정보.차선_도색_정지선_면적 */ , tncntrwkdtl.PAINT_SPDBMP_CO /* 공사상세정보.차선_도색_방지턱_개수 */ , tncntrwkdtl.PAINT_SPDBMP_AR /* 공사상세정보.차선_도색_방지턱_면적 */ , tncntrwkdtl.PAINT_WHDASHLN_AR /* 공사상세정보.차선_도색_백색파선_면적 */ , tncntrwkdtl.PAINT_CHRCTRSYMBL_CO /* 공사상세정보.차선_도색_문자기호_개수 */ , tncntrwkdtl.PAINT_CHRCTRSYMBL_AR /* 공사상세정보.차선_도색_문자기호_면적 */ , tncntrwkdtl.PAINT_CRSLK_AR /* 공사상세정보.차선_도색_횡단보도_면적 */ FROM TN_CNTRWK_DTL tncntrwkdtl WHERE tncntrwkdtl.delete_at = 'N' AND tncntrwkdtl.use_at = 'Y'
/* 공사상세정보.공사_ID */ AND tncntrwkdtl.CNTRWK_ID = #CNTRWK_ID#
ORDER BY tncntrwkdtl.DETAIL_CNTRWK_ID DESC /* 공사상세정보.세부_공사_ID */ ) tb) tncntrwkdtl WHERE 1=1
