sqlMap.insert:
/* codeUsemapDAO.insertCodeUsemap */ /* 코드사용맵 등록 */ INSERT INTO TC_CODE_USEMAP ( TABLE_NM /* 코드사용맵.테이블_명 */ , COLMN_NM /* 코드사용맵.칼럼_명 */ , CL_CODE /* 코드사용맵.분류_코드 */
, USE_AT /* 코드사용맵.사용_여부 */ , CRTR_NO /* 코드사용맵.생성자_번호 */ , CREAT_DT /* 코드사용맵.생성_일시 */ , UPDUSR_NO /* 코드사용맵.수정자_번호 */ , UPDT_DT /* 코드사용맵.수정_일시 */ ) VALUES ( #TABLE_NM# /* 코드사용맵.테이블_명 */ , #COLMN_NM# /* 코드사용맵.칼럼_명 */ , #CL_CODE# /* 코드사용맵.분류_코드 */
, #USE_AT# /* 코드사용맵.사용_여부 */ , #CRTR_NO#::int /* 코드사용맵.생성자_번호 */ , NOW() /* 코드사용맵.생성_일시 */ , #UPDUSR_NO#::int /* 코드사용맵.수정자_번호 */ , NOW() /* 코드사용맵.수정_일시 */ )

sqlMap.sql:
WHERE 1 = 1


	AND tccodeusemap.TABLE_NM = #searchKeyword#


	AND tccodeusemap.COLMN_NM LIKE '%' || #searchKeyword# || '%'

 /* 코드사용맵.테이블_명 */ AND tccodeusemap.TABLE_NM = #TABLE_NM# 
 /* 코드사용맵.칼럼_명 */ AND tccodeusemap.COLMN_NM = #COLMN_NM# 
 /* 코드사용맵.분류_코드 */ AND tccodeusemap.CL_CODE = #CL_CODE# 
 /* 코드사용맵.사용_여부 */ AND tccodeusemap.USE_AT = #USE_AT# 
 /* 코드사용맵.생성자_번호 */ AND tccodeusemap.CRTR_NO = #CRTR_NO#::int 
 /* 코드사용맵.생성_일시 */ AND tccodeusemap.CREAT_DT = #CREAT_DT# 
 /* 코드사용맵.수정자_번호 */ AND tccodeusemap.UPDUSR_NO = #UPDUSR_NO#::int 
 /* 코드사용맵.수정_일시 */ AND tccodeusemap.UPDT_DT = #UPDT_DT#

sqlMap.select:
/* codeUsemapDAO.selectCodeUsemapListPage */ /* 코드사용맵 조회(페이징) */ WITH tccodeusemap AS ( SELECT tccodeusemap.TABLE_NM /* 코드사용맵.테이블_명 */ , tccodeusemap.COLMN_NM /* 코드사용맵.칼럼_명 */ , tccodeusemap.CL_CODE /* 코드사용맵.분류_코드 */
, tccodeusemap.USE_AT /* 코드사용맵.사용_여부 */ , tccodeusemap.CRTR_NO /* 코드사용맵.생성자_번호 */ , tccodeusemap.CREAT_DT /* 코드사용맵.생성_일시 */ , tccodeusemap.UPDUSR_NO /* 코드사용맵.수정자_번호 */ , tccodeusemap.UPDT_DT /* 코드사용맵.수정_일시 */
FROM TC_CODE_USEMAP tccodeusemap
WHERE 1 = 1
AND tccodeusemap.TABLE_NM = #searchKeyword#
AND tccodeusemap.COLMN_NM LIKE '%' || #searchKeyword# || '%'
/* 코드사용맵.테이블_명 */ AND tccodeusemap.TABLE_NM = #TABLE_NM#
/* 코드사용맵.칼럼_명 */ AND tccodeusemap.COLMN_NM = #COLMN_NM#
/* 코드사용맵.분류_코드 */ AND tccodeusemap.CL_CODE = #CL_CODE#
/* 코드사용맵.사용_여부 */ AND tccodeusemap.USE_AT = #USE_AT#
/* 코드사용맵.생성자_번호 */ AND tccodeusemap.CRTR_NO = #CRTR_NO#::int
/* 코드사용맵.생성_일시 */ AND tccodeusemap.CREAT_DT = #CREAT_DT#
/* 코드사용맵.수정자_번호 */ AND tccodeusemap.UPDUSR_NO = #UPDUSR_NO#::int
/* 코드사용맵.수정_일시 */ AND tccodeusemap.UPDT_DT = #UPDT_DT#
) SELECT tccodeusemap.* , (SELECT tnsysuser.USER_ID /* 시스템사용자.사용자_ID */ FROM TN_SYS_USER tnsysuser WHERE 1 = 1 AND tnsysuser.USER_NO = tccodeusemap.UPDUSR_NO /* 시스템사용자.사용자_번호 */ AND tnsysuser.USE_AT = 'Y' AND tnsysuser.CONFM_AT = 'Y' AND tnsysuser.DELETE_AT = 'N' ) UPDUSR_ID FROM ( SELECT tccodeusemap.TABLE_NM /* 코드사용맵.테이블_명 */ , tccodeusemap.COLMN_NM /* 코드사용맵.칼럼_명 */ , tccodeusemap.CL_CODE /* 코드사용맵.분류_코드 */ , (SELECT obj_description(c.oid) FROM pg_catalog.pg_class c inner join pg_catalog.pg_namespace n on c.relnamespace=n.oid WHERE c.relname = tccodeusemap.table_nm AND c.relkind = 'r' AND nspname='gpms') TABLE_COMMENTS , (SELECT (select col_description(a.attrelid, a.attnum)) FROM pg_catalog.pg_class c inner join pg_catalog.pg_attribute a on a.attrelid = c.oid WHERE c.relname = tccodeusemap.table_nm AND tccodeusemap.colmn_nm = a.attname AND a.attnum > 0) COLUMN_COMMENTS , tccodeusemap.USE_AT /* 코드사용맵.사용_여부 */ , tccodeusemap.CRTR_NO /* 코드사용맵.생성자_번호 */ , tccodeusemap.CREAT_DT /* 코드사용맵.생성_일시 */ , tccodeusemap.UPDUSR_NO /* 코드사용맵.수정자_번호 */ , tccodeusemap.UPDT_DT /* 코드사용맵.수정_일시 */ /* 코드사용맵.테이블_명 */ /* 코드사용맵.칼럼_명 */ , ROW_NUMBER() OVER( ORDER BY tccodeusemap.TABLE_NM ASC, tccodeusemap.COLMN_NM ASC, tccodeusemap.CL_CODE DESC ) ROW_NO FROM tccodeusemap WHERE 1=1 ) tccodeusemap WHERE 1=1
AND tccodeusemap.ROW_NO BETWEEN #firstIndex# + 1 AND #lastIndex#
AND tccodeusemap.ROW_NO <= #lastIndex# AND ROW_NO > #firstIndex#

/* codeUsemapDAO.selectCodeUsemapListTotalCount */
/* 코드사용맵 목록 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TC_CODE_USEMAP tccodeusemap
