WHERE 1 = 1
AND tncompany.DELETE_AT = 'N'	/* 업체정보.삭제_여부 */

	AND tncompany.CO_NO = #searchKeyword#


	AND tncompany.CO_NM LIKE '%' || #searchKeyword# || '%'

 /* 업체정보.업체_번호 */ AND tncompany.CO_NO = #CO_NO# 
 /* 업체정보.업체_명 */ AND tncompany.CO_NM  LIKE '%' || #CO_NM# || '%'  
 /* 업체정보.대표자_명 */ AND tncompany.RPRSNTV_NM = #RPRSNTV_NM# 
 /* 업체정보.영업_소재지 */ AND tncompany.BIZ_LOC = #BIZ_LOC# 
 /* 업체정보.대표_전화_번호 */ AND tncompany.RPRSNT_TEL_NO = #RPRSNT_TEL_NO# 
 /* 업체정보.사업자_번호 */ AND REPLACE(tncompany.BIZ_NO, '-', '') LIKE '%' || REPLACE(#BIZ_NO#, '-', '') || '%' 
 /* 업체정보.담당자_명 */ AND tncompany.CHARGER_NM LIKE '%' || #CHARGER_NM# || '%' 
 /* 업체정보.담당자_전화_번호 */ AND tncompany.CHARGER_TEL_NO = #CHARGER_TEL_NO# 
 /* 업체정보.업체_상태_코드 */ AND tncompany.CO_STTUS_CODE = #CO_STTUS_CODE# 
 /* 업체정보.등록_업종 */ AND tncompany.REGIST_INDUTY = #REGIST_INDUTY# 
 /* 업체정보.주력_공사 */ AND tncompany.MFRC_CNTRWK = #MFRC_CNTRWK# 
 /* 업체정보.주요_공사_지역 */ AND tncompany.MAJOR_CNTRWK_AREA = #MAJOR_CNTRWK_AREA# 
 /* 업체정보.생성자_번호 */ AND tncompany.CRTR_NO = #CRTR_NO# 
 /* 업체정보.생성_일시 */ AND tncompany.CREAT_DT = #CREAT_DT# 
 /* 업체정보.수정자_번호 */ AND tncompany.UPDUSR_NO = #UPDUSR_NO# 
 /* 업체정보.수정_일시 */ AND tncompany.UPDT_DT = #UPDT_DT# 
 /* 업체정보.사용_여부 */ AND tncompany.USE_AT = #USE_AT# 
 /* 업체정보.삭제_여부 */ AND tncompany.DELETE_AT = #DELETE_AT#

sqlMap.select:
/* companyDAO.selectCompanyListPage */ /* 업체정보 조회(페이징) */ WITH tncompany AS ( SELECT tncompany.CO_NO /* 업체정보.업체_번호 */ , tncompany.CO_NM /* 업체정보.업체_명 */ , tncompany.RPRSNTV_NM /* 업체정보.대표자_명 */ , tncompany.BIZ_LOC /* 업체정보.영업_소재지 */ , tncompany.RPRSNT_TEL_NO /* 업체정보.대표_전화_번호 */ , tncompany.BIZ_NO /* 업체정보.사업자_번호 */ , tncompany.CHARGER_NM /* 업체정보.담당자_명 */ , tncompany.CHARGER_TEL_NO /* 업체정보.담당자_전화_번호 */ , tncompany.CO_STTUS_CODE /* 업체정보.업체_상태_코드 */ , tncompany.REGIST_INDUTY /* 업체정보.등록_업종 */ , tncompany.MFRC_CNTRWK /* 업체정보.주력_공사 */ , tncompany.MAJOR_CNTRWK_AREA /* 업체정보.주요_공사_지역 */ , tncompany.CRTR_NO /* 업체정보.생성자_번호 */ , tncompany.CREAT_DT /* 업체정보.생성_일시 */ , tncompany.UPDUSR_NO /* 업체정보.수정자_번호 */ , tncompany.UPDT_DT /* 업체정보.수정_일시 */ , tncompany.USE_AT /* 업체정보.사용_여부 */ , tncompany.DELETE_AT /* 업체정보.삭제_여부 */
FROM TN_COMPANY tncompany
WHERE 1 = 1
AND tncompany.CO_NO = #searchKeyword#
AND tncompany.CO_NM LIKE '%' || #searchKeyword# || '%'
/* 업체정보.업체_번호 */ AND tncompany.CO_NO = #CO_NO#
/* 업체정보.업체_명 */ AND tncompany.CO_NM = #CO_NM#
/* 업체정보.대표자_명 */ AND tncompany.RPRSNTV_NM = #RPRSNTV_NM#
/* 업체정보.영업_소재지 */ AND tncompany.BIZ_LOC = #BIZ_LOC#
/* 업체정보.대표_전화_번호 */ AND tncompany.RPRSNT_TEL_NO = #RPRSNT_TEL_NO#
/* 업체정보.사업자_번호 */ AND tncompany.BIZ_NO = #BIZ_NO#
/* 업체정보.담당자_명 */ AND tncompany.CHARGER_NM = #CHARGER_NM#
/* 업체정보.담당자_전화_번호 */ AND tncompany.CHARGER_TEL_NO = #CHARGER_TEL_NO#
/* 업체정보.업체_상태_코드 */ AND tncompany.CO_STTUS_CODE = #CO_STTUS_CODE#
/* 업체정보.등록_업종 */ AND tncompany.REGIST_INDUTY = #REGIST_INDUTY#
/* 업체정보.주력_공사 */ AND tncompany.MFRC_CNTRWK = #MFRC_CNTRWK#
/* 업체정보.주요_공사_지역 */ AND tncompany.MAJOR_CNTRWK_AREA = #MAJOR_CNTRWK_AREA#
/* 업체정보.생성자_번호 */ AND tncompany.CRTR_NO = #CRTR_NO#
/* 업체정보.생성_일시 */ AND tncompany.CREAT_DT = #CREAT_DT#
/* 업체정보.수정자_번호 */ AND tncompany.UPDUSR_NO = #UPDUSR_NO#
/* 업체정보.수정_일시 */ AND tncompany.UPDT_DT = #UPDT_DT#
/* 업체정보.사용_여부 */ AND tncompany.USE_AT = #USE_AT#
/* 업체정보.삭제_여부 */ AND tncompany.DELETE_AT = #DELETE_AT#
) SELECT tncompany.* , (SELECT tccode.CODE_NM FROM TC_CODE tccode WHERE 1 = 1 AND tccode.CODE_VAL = tncompany.CO_STTUS_CODE AND tccode.USE_AT = 'Y' AND tccode.DELETE_AT = 'N' ) CO_STTUS_NM , (SELECT tnsysuser.USER_ID /* 시스템사용자.사용자_ID */ FROM TN_SYS_USER tnsysuser WHERE 1 = 1 AND tnsysuser.USER_NO = tncompany.UPDUSR_NO /* 시스템사용자.사용자_번호 */ AND tnsysuser.USE_AT = 'Y' AND tnsysuser.CONFM_AT = 'Y' AND tnsysuser.DELETE_AT = 'N' ) UPDUSR_ID FROM ( SELECT tncompany.CO_NO /* 업체정보.업체_번호 */ , tncompany.CO_NM /* 업체정보.업체_명 */ , tncompany.RPRSNTV_NM /* 업체정보.대표자_명 */ , tncompany.BIZ_LOC /* 업체정보.영업_소재지 */ , tncompany.RPRSNT_TEL_NO /* 업체정보.대표_전화_번호 */ , tncompany.BIZ_NO /* 업체정보.사업자_번호 */ , tncompany.CHARGER_NM /* 업체정보.담당자_명 */ , tncompany.CHARGER_TEL_NO /* 업체정보.담당자_전화_번호 */ , tncompany.CO_STTUS_CODE /* 업체정보.업체_상태_코드 */ , tncompany.REGIST_INDUTY /* 업체정보.등록_업종 */ , tncompany.MFRC_CNTRWK /* 업체정보.주력_공사 */ , tncompany.MAJOR_CNTRWK_AREA /* 업체정보.주요_공사_지역 */ , tncompany.CRTR_NO /* 업체정보.생성자_번호 */ , tncompany.CREAT_DT /* 업체정보.생성_일시 */ , tncompany.UPDUSR_NO /* 업체정보.수정자_번호 */ , tncompany.UPDT_DT /* 업체정보.수정_일시 */ , tncompany.USE_AT /* 업체정보.사용_여부 */ , tncompany.DELETE_AT /* 업체정보.삭제_여부 */ /* 업체정보.업체_번호 */ /* 업체정보.업체_명 */ , ROW_NUMBER() OVER( ORDER BY tncompany.CO_NO ASC, tncompany.CO_NM DESC ) ROW_NO FROM tncompany WHERE 1=1 ) tncompany WHERE 1=1
AND tncompany.ROW_NO BETWEEN #firstIndex# + 1 AND #lastIndex#
AND tncompany.ROW_NO <= #lastIndex# AND ROW_NO > #firstIndex#

/* companyDAO.selectCompanyList */ /* 업체정보 조회 */ SELECT tncompany.* , (SELECT tccode.CODE_NM FROM TC_CODE tccode WHERE 1 = 1 AND tccode.CODE_VAL = tncompany.CO_STTUS_CODE AND tccode.USE_AT = 'Y' AND tccode.DELETE_AT = 'N' ) CO_STTUS_NM , (SELECT tnsysuser.USER_ID /* 시스템사용자.사용자_ID */ FROM TN_SYS_USER tnsysuser WHERE 1 = 1 AND tnsysuser.USER_NO = tncompany.UPDUSR_NO /* 시스템사용자.사용자_번호 */ AND tnsysuser.USE_AT = 'Y' AND tnsysuser.CONFM_AT = 'Y' AND tnsysuser.DELETE_AT = 'N' ) UPDUSR_ID FROM (SELECT ROW_NUMBER() OVER(ORDER BY $sidx$ $sord$) rn, tb.* FROM ( SELECT tncompany.CO_NO /* 업체정보.업체_번호 */ , tncompany.CO_NM /* 업체정보.업체_명 */ , tncompany.RPRSNTV_NM /* 업체정보.대표자_명 */ , tncompany.BIZ_LOC /* 업체정보.영업_소재지 */ , tncompany.RPRSNT_TEL_NO /* 업체정보.대표_전화_번호 */ , tncompany.BIZ_NO /* 업체정보.사업자_번호 */ , tncompany.CHARGER_NM /* 업체정보.담당자_명 */ , tncompany.CHARGER_TEL_NO /* 업체정보.담당자_전화_번호 */ , tncompany.CO_STTUS_CODE /* 업체정보.업체_상태_코드 */ , tncompany.REGIST_INDUTY /* 업체정보.등록_업종 */ , tncompany.MFRC_CNTRWK /* 업체정보.주력_공사 */ , tncompany.MAJOR_CNTRWK_AREA /* 업체정보.주요_공사_지역 */ , tncompany.CRTR_NO /* 업체정보.생성자_번호 */ , tncompany.CREAT_DT /* 업체정보.생성_일시 */ , tncompany.UPDUSR_NO /* 업체정보.수정자_번호 */ , tncompany.UPDT_DT /* 업체정보.수정_일시 */ , tncompany.USE_AT /* 업체정보.사용_여부 */ , tncompany.DELETE_AT /* 업체정보.삭제_여부 */ FROM TN_COMPANY tncompany
) tb) tncompany WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#

/* companyDAO.selectCompanyListTotalCount */
/* 업체정보 목록 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TN_COMPANY tncompany
