sqlMap.select:
/* dwgInfoDAO.selectDwgInfoList */ /* DWG_INFO 조회 */ SELECT * FROM (SELECT ROW_NUMBER() OVER(ORDER BY $sidx$ $sord$) RN, tb.* FROM ( SELECT dwginfo.MCO /* DWG_INFO.관리기관 */ , dwginfo.ROAD_NO /* DWG_INFO.노선_번호 */ , LTRIM(dwginfo.ROAD_NO, '0') ROAD_NO_VAL , dwginfo.SECT /* DWG_INFO.구간_번호 */ , dwginfo.HCODE /* DWG_INFO.이력_코드 */ , dwginfo.SECT_ST/1000 SECT_ST /* DWG_INFO.도면_시점 */ , dwginfo.SECT_ED/1000 SECT_ED /* DWG_INFO.도면_종점 */ , dwginfo.DWG_LEN /* DWG_INFO.도면_연장 */ , dwginfo.DWG_NAME /* DWG_INFO.도면_명 */ , dwginfo.CON_NAME /* DWG_INFO.구간_도면_명 */ , dwginfo.LOCAT /* DWG_INFO.법정동_위치 */ , dwginfo.DIV_ROAD_NO /* DWG_INFO.분기_도로_번호 */ , dwginfo.BY_DWG_NAME /* DWG_INFO.분기_도로_인접_도면_명 */ , dwginfo.BYPASS /* DWG_INFO.우회도로_유무 */ , dwginfo.INPUT_DATE /* DWG_INFO.입력일 */ , dwginfo.DWG_GUBN /* DWG_INFO.도면_구분 */ , dwginfo.REM /* DWG_INFO.비고 */ , dwginfo.SEQID /* DWG_INFO.SEQID */ FROM DWG_INFO dwginfo
AND COALESCE(dwginfo.SEQID,0) = COALESCE( (SELECT MAX(dwg.SEQID) FROM DWG_INFO dwg WHERE dwg.ROAD_NO = dwginfo.ROAD_NO AND dwg.SECT = dwginfo.SECT AND dwg.DWG_NAME = dwginfo.DWG_NAME),0 )
) tb) WHERE 1=1
rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#
