WHERE 1 = 1
AND tnflaw.DELETE_AT = 'N'

	AND tnflaw.FLAW_ID = #searchKeyword#


	AND tnflaw.CNTRWK_ID LIKE '%' || #searchKeyword# || '%'

 /* 공사하자정보.하자_ID */ AND tnflaw.FLAW_ID = #FLAW_ID# 
 /* 하자_공사_ID */ AND tnflaw.FLAW_CNTRWK_ID = #FLAW_CNTRWK_ID# 
 /* 공사하자정보.공사_ID */ AND tnflaw.CNTRWK_ID = #CNTRWK_ID# 
 /* 공사하자정보.발주_부서_코드 */ AND tnflaw.ORDER_DEPT_CODE = #ORDER_DEPT_CODE# 
 /* 공사하자정보.하자_검사_일자 */ AND tnflaw.FLAW_CHCK_DE = #FLAW_CHCK_DE# 
 /* 공사하자정보.하자_보증금 */ AND tnflaw.FLAW_GTN = #FLAW_GTN# 
 /* 공사하자정보.하자_보증금_종류 */ AND tnflaw.FLAW_GTN_KND = #FLAW_GTN_KND# 
 /* 공사하자정보.하자_담보_시작_일자 */ AND tnflaw.WARNT_BEGIN_DE = #WARNT_BEGIN_DE# 
 /* 공사하자정보.하자_담보_종료_일자 */ AND tnflaw.WARNT_END_DE = #WARNT_END_DE# 
 /* 공사하자정보.사용_여부 */ AND tnflaw.USE_AT = #USE_AT# 
 /* 공사하자정보.삭제_여부 */ AND tnflaw.DELETE_AT = #DELETE_AT# 
 /* 공사하자정보.사용_여부 */ AND tnflaw.USE_AT = 'Y' 
 /* 공사하자정보.삭제_여부 */ AND tnflaw.DELETE_AT = 'N' 
 /* 공사하자정보.삭제_일시 */ AND tnflaw.DELETE_DT = #DELETE_DT# 
 /* 공사하자정보.생성자_번호 */ AND tnflaw.CRTR_NO = #CRTR_NO# 
 /* 공사하자정보.생성_일시 */ AND tnflaw.CREAT_DT = #CREAT_DT# 
 /* 공사하자정보.수정자_번호 */ AND tnflaw.UPDUSR_NO = #UPDUSR_NO# 
 /* 공사하자정보.수정_일시 */ AND tnflaw.UPDT_DT = #UPDT_DT#

sqlMap.select:
/* flawDAO.selectFlawListPage */ /* 공사하자정보 조회(페이징) */ WITH tnflaw AS ( SELECT tnflaw.FLAW_ID /* 공사하자정보.하자_ID */ , tnflaw.CNTRWK_ID /* 공사하자정보.공사_ID */ , tnflaw.ORDER_DEPT_CODE /* 공사하자정보.발주_부서_코드 */ , tnflaw.FLAW_CHCK_DE /* 공사하자정보.하자_검사_일자 */ , tnflaw.FLAW_GTN /* 공사하자정보.하자_보증금 */ , tnflaw.FLAW_GTN_KND /* 공사하자정보.하자_보증금_종류 */ , tnflaw.WARNT_BEGIN_DE /* 공사하자정보.하자_담보_시작_일자 */ , tnflaw.WARNT_END_DE /* 공사하자정보.하자_담보_종료_일자 */ , tnflaw.USE_AT /* 공사하자정보.사용_여부 */ , tnflaw.DELETE_AT /* 공사하자정보.삭제_여부 */ , tnflaw.DELETE_DT /* 공사하자정보.삭제_일시 */ , tnflaw.CRTR_NO /* 공사하자정보.생성자_번호 */ , tnflaw.CREAT_DT /* 공사하자정보.생성_일시 */ , tnflaw.UPDUSR_NO /* 공사하자정보.수정자_번호 */ , tnflaw.UPDT_DT /* 공사하자정보.수정_일시 */
FROM TN_FLAW tnflaw
WHERE 1 = 1
AND tnflaw.FLAW_ID = #searchKeyword#
AND tnflaw.CNTRWK_ID LIKE '%' || #searchKeyword# || '%'
/* 공사하자정보.하자_ID */ AND tnflaw.FLAW_ID = #FLAW_ID#
/* 공사하자정보.공사_ID */ AND tnflaw.CNTRWK_ID = #CNTRWK_ID#
/* 공사하자정보.발주_부서_코드 */ AND tnflaw.ORDER_DEPT_CODE = #ORDER_DEPT_CODE#
/* 공사하자정보.하자_검사_일자 */ AND tnflaw.FLAW_CHCK_DE = #FLAW_CHCK_DE#
/* 공사하자정보.하자_보증금 */ AND tnflaw.FLAW_GTN = #FLAW_GTN#
/* 공사하자정보.하자_보증금_종류 */ AND tnflaw.FLAW_GTN_KND = #FLAW_GTN_KND#
/* 공사하자정보.하자_담보_시작_일자 */ AND tnflaw.WARNT_BEGIN_DE = #WARNT_BEGIN_DE#
/* 공사하자정보.하자_담보_종료_일자 */ AND tnflaw.WARNT_END_DE = #WARNT_END_DE#
/* 공사하자정보.사용_여부 */ AND tnflaw.USE_AT = #USE_AT#
/* 공사하자정보.삭제_여부 */ AND tnflaw.DELETE_AT = #DELETE_AT#
/* 공사하자정보.삭제_일시 */ AND tnflaw.DELETE_DT = #DELETE_DT#
/* 공사하자정보.생성자_번호 */ AND tnflaw.CRTR_NO = #CRTR_NO#
/* 공사하자정보.생성_일시 */ AND tnflaw.CREAT_DT = #CREAT_DT#
/* 공사하자정보.수정자_번호 */ AND tnflaw.UPDUSR_NO = #UPDUSR_NO#
/* 공사하자정보.수정_일시 */ AND tnflaw.UPDT_DT = #UPDT_DT#
) SELECT tnflaw.* FROM ( SELECT tnflaw.FLAW_ID /* 공사하자정보.하자_ID */ , tnflaw.CNTRWK_ID /* 공사하자정보.공사_ID */ , tnflaw.ORDER_DEPT_CODE /* 공사하자정보.발주_부서_코드 */ , tnflaw.FLAW_CHCK_DE /* 공사하자정보.하자_검사_일자 */ , tnflaw.FLAW_GTN /* 공사하자정보.하자_보증금 */ , tnflaw.FLAW_GTN_KND /* 공사하자정보.하자_보증금_종류 */ , tnflaw.WARNT_BEGIN_DE /* 공사하자정보.하자_담보_시작_일자 */ , tnflaw.WARNT_END_DE /* 공사하자정보.하자_담보_종료_일자 */ , tnflaw.USE_AT /* 공사하자정보.사용_여부 */ , tnflaw.DELETE_AT /* 공사하자정보.삭제_여부 */ , tnflaw.DELETE_DT /* 공사하자정보.삭제_일시 */ , tnflaw.CRTR_NO /* 공사하자정보.생성자_번호 */ , tnflaw.CREAT_DT /* 공사하자정보.생성_일시 */ , tnflaw.UPDUSR_NO /* 공사하자정보.수정자_번호 */ , tnflaw.UPDT_DT /* 공사하자정보.수정_일시 */ /* 공사하자정보.하자_ID */ /* 공사하자정보.공사_ID */ , ROW_NUMBER() OVER( ORDER BY tnflaw.FLAW_ID ASC, tnflaw.CNTRWK_ID DESC ) ROW_NO FROM tnflaw WHERE 1=1 ) tnflaw WHERE 1=1
AND tnflaw.ROW_NO BETWEEN #firstIndex# + 1 AND #lastIndex#
AND tnflaw.ROW_NO <= #lastIndex# AND ROW_NO > #firstIndex#

/* flawDAO.selectFlawList */ /* 공사하자정보 조회 */ SELECT * FROM (SELECT ROW_NUMBER() OVER() AS rn, tb.* FROM ( SELECT tnflaw.FLAW_ID /* 공사하자정보.하자_ID */ , tnflaw.CNTRWK_ID /* 공사하자정보.공사_ID */ , tnflaw.ORDER_DEPT_CODE /* 공사하자정보.발주_부서_코드 */ , tnflaw.FLAW_CHCK_DE /* 공사하자정보.하자_검사_일자 */ , tnflaw.FLAW_GTN /* 공사하자정보.하자_보증금 */ , tnflaw.FLAW_GTN_KND /* 공사하자정보.하자_보증금_종류 */ , tnflaw.WARNT_BEGIN_DE /* 공사하자정보.하자_담보_시작_일자 */ , tnflaw.WARNT_END_DE /* 공사하자정보.하자_담보_종료_일자 */ , tnflaw.USE_AT /* 공사하자정보.사용_여부 */ , tnflaw.DELETE_AT /* 공사하자정보.삭제_여부 */ , tnflaw.DELETE_DT /* 공사하자정보.삭제_일시 */ , tnflaw.CRTR_NO /* 공사하자정보.생성자_번호 */ , tnflaw.CREAT_DT /* 공사하자정보.생성_일시 */ , tnflaw.UPDUSR_NO /* 공사하자정보.수정자_번호 */ , tnflaw.UPDT_DT /* 공사하자정보.수정_일시 */ FROM TN_FLAW tnflaw
ORDER BY tnflaw.FLAW_ID DESC /* 공사하자정보.하자_ID */ ) tb) tnflaw WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#


/* flawDAO.selectFlawListTotalCount */
/* 공사하자정보 목록 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TN_FLAW tnflaw
