sqlMap.sql:
WHERE 1 = 1
AND tnmenu.DELETE_AT = 'N' /* 시스템메뉴.삭제_여부 */

	AND tnmenu.MENU_ID = #searchKeyword#


	AND tnmenu.UPPER_MENU_ID LIKE '%' || #searchKeyword# || '%'

 /* 시스템메뉴.메뉴_ID */ AND tnmenu.MENU_ID = #MENU_ID# 
 /* 시스템메뉴.상위_메뉴_ID */ AND tnmenu.UPPER_MENU_ID = #UPPER_MENU_ID# 
 /* 시스템메뉴.사용_여부 */ AND tnmenu.USE_AT = #USE_AT# 
 /* 시스템메뉴.삭제_여부 */ AND tnmenu.DELETE_AT = #DELETE_AT# 
 /* 시스템메뉴.수정자_번호 */ AND tnmenu.UPDUSR_NO = #UPDUSR_NO# 
 /* 시스템메뉴.수정_일시 */ AND tnmenu.UPDT_DT = #UPDT_DT# 
 /* 시스템메뉴.메뉴_명 */ AND REPLACE(tnmenu.MENU_NM, ' ', '') LIKE '%' ||  REPLACE(#MENU_NM#, ' ', '')  || '%'
 /* 시스템메뉴.생성자_번호 */ AND tnmenu.CRTR_NO = #CRTR_NO# 
 /* 시스템메뉴.생성_일시 */ AND tnmenu.CREAT_DT = #CREAT_DT# 
 /* 시스템메뉴.URL */ AND tnmenu.URL = #URL# 
 /* 시스템메뉴.메뉴_설명 */ AND tnmenu.MENU_DC = #MENU_DC# 
 /* 시스템메뉴.URL */ AND tnmenu.URL LIKE '%' || LOWER( #SCH_URL# ) || '%'
 /* 시스템메뉴.시스템구분 */ AND tnmenu.SYS_CODE = #SCH_MENU_D# 

  AND tnmenu.MENU_DP = #SCH_MENU_DP#

sqlMap.select:
/* menuDAO.selectMenuList */ /* 시스템메뉴 조회 */ SELECT tnmenu.* , (SELECT tnsysuser.USER_ID /* 시스템사용자.사용자_ID */ FROM TN_SYS_USER tnsysuser WHERE 1 = 1 AND tnsysuser.USER_NO = tnmenu.UPDUSR_NO /* 시스템사용자.사용자_번호 */ AND tnsysuser.USE_AT = 'Y' AND tnsysuser.CONFM_AT = 'Y' AND tnsysuser.DELETE_AT = 'N' ) UPDUSR_ID FROM (SELECT ROW_NUMBER() OVER() AS rn, tb.* FROM ( SELECT tnmenu.MENU_ID /* 시스템메뉴.메뉴_ID */ , tnmenu.UPPER_MENU_ID /* 시스템메뉴.상위_메뉴_ID */ , tnmenu.USE_AT /* 시스템메뉴.사용_여부 */ , tnmenu.DELETE_AT /* 시스템메뉴.삭제_여부 */ , tnmenu.UPDUSR_NO /* 시스템메뉴.수정자_번호 */ , tnmenu.UPDT_DT /* 시스템메뉴.수정_일시 */ , LPAD(' ', 8 *(LEVEL - 1)) || tnmenu.MENU_NM MENU_NM /* 시스템메뉴.메뉴_명 */ , tnmenu.CRTR_NO /* 시스템메뉴.생성자_번호 */ , tnmenu.CREAT_DT /* 시스템메뉴.생성_일시 */ , tnmenu.URL /* 시스템메뉴.URL */ , tnmenu.MENU_DC /* 시스템메뉴.메뉴_설명 */ , tnmenu.SYS_CODE/* 시스템메뉴.시스템_코드 */ , tnmenu.MENU_DP/* 시스템메뉴.시스템_레벨 */ FROM TN_MENU tnmenu
START WITH tnmenu.MENU_ID = #SCH_MENU_S# CONNECT BY PRIOR MENU_ID = UPPER_MENU_ID
START WITH UPPER_MENU_ID IS NULL CONNECT BY PRIOR MENU_ID = UPPER_MENU_ID
) tb) tnmenu WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#


/* menuDAO.selectMenuListTotalCount */
/* 시스템메뉴 목록 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TN_MENU tnmenu
