sqlMap.select:
/* noticeDAO.selectNoticeList */ SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY $sidx$ $sord$) rn, tnnotice.* FROM ( SELECT SEQ_NO /*일련번호 */ ,POS /*POS*/ ,SUB /*SUB*/ ,DEP /*DEP*/ ,WRTER /*작성자*/ ,SJ /*제목*/ ,CN /*내용*/ ,RDCNT /*조회수*/ ,TO_CHAR(REGIST_DT,'YYYY-MM-DD') AS REGIST_DT /*등록일시*/ ,FILE_NM /*파일명*/ ,POPUP_PD /*팝업기간*/ ,ORGINL_FILE_NM /*원본파일명*/ ,ANSWER /*답변*/ ,ANSWRR /*답변자*/ ,ANSWER_DT /*답변일시*/ FROM TN_NOTICE WHERE 1=1
AND SJ LIKE '%' || #SCH_SJCN_TXT# || '%' OR CN LIKE '%' || #SCH_SJCN_TXT# || '%'
AND SJ LIKE '%' || #SCH_SJCN_TXT# || '%'
AND CN LIKE '%' || #SCH_SJCN_TXT# || '%'
) tnnotice
) tb where 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#

/* noticeDAO.selectNoticeListTotalCount */ SELECT COUNT(*) totCnt FROM ( SELECT SEQ_NO /*일련번호 */ ,POS /*POS*/ ,SUB /*SUB*/ ,DEP /*DEP*/ ,WRTER /*작성자*/ ,SJ /*제목*/ ,CN /*내용*/ ,RDCNT /*조회수*/ ,REGIST_DT /*등록일*/ ,FILE_NM /*파일명*/ ,POPUP_PD /*팝업기간*/ ,ORGINL_FILE_NM /*원본파일명*/ ,ANSWER /*답변*/ ,ANSWRR /*답변자*/ ,ANSWER_DT /*답변일시*/ FROM TN_NOTICE WHERE 1=1
AND SJ LIKE '%' || #SCH_SJCN_TXT# || '%' OR CN LIKE '%' || #SCH_SJCN_TXT# || '%'
AND SJ LIKE '%' || #SCH_SJCN_TXT# || '%'
AND CN LIKE '%' || #SCH_SJCN_TXT# || '%'
) tnnotice

sqlMap.delete:

/* noticeDAO.deleteNotice */
/* 공지사항 삭제 */
DELETE 
  FROM TN_NOTICE 
 WHERE SEQ_NO = #SEQ_NO#::int

sqlMap.update:

/* noticeDAO.updateNoticeRdcnt */
/* 공지사항 조회수수정 */
UPDATE TN_NOTICE
   SET RDCNT = COALESCE(#RDCNT#::int,0)          /*조회수*/
WHERE SEQ_NO = #SEQ_NO#::int

sqlMap.select:
/* noticeDAO.noticeDAO.selectMainNoticeList */ SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY REGIST_DT DESC) rn, tnnotice.* FROM ( SELECT SEQ_NO /*일련번호 */ ,POS /*POS*/ ,SUB /*SUB*/ ,DEP /*DEP*/ ,WRTER /*작성자*/ ,SJ /*제목*/ ,CN /*내용*/ ,RDCNT /*조회수*/ ,TO_CHAR(REGIST_DT,'YYYY-MM-DD') AS REGIST_DT /*등록일시*/ ,FILE_NM /*파일명*/ ,POPUP_PD /*팝업기간*/ ,ORGINL_FILE_NM /*원본파일명*/ ,ANSWER /*답변*/ ,ANSWRR /*답변자*/ ,ANSWER_DT /*답변일시*/ FROM TN_NOTICE WHERE 1=1
) tnnotice
) tb where 1=1
AND rn BETWEEN #firstIndex# AND #recordCountPerPage#

/* noticeDAO.selectMainNoticeListTotalCount */
/* 공지사항 전체 건수 조회*/
SELECT COUNT(*) AS RDCNT  FROM  TN_NOTICE
