sqlMap.select:
/* pavSttusGradStdrDAO.selectPavSttusGradStdrListPage */ /* 포장상태등급기준 조회(페이징) */ WITH tnpavsttusgradstdr AS ( SELECT tnpavsttusgradstdr.GRAD_NO /* 포장상태등급기준.등급_번호 */ , tnpavsttusgradstdr.GRAD_STDR_CODE /* 포장상태등급기준.등급_기준_코드 */ , tnpavsttusgradstdr.APPLC_YEAR /* 포장상태등급기준.적용_년도 */ , tnpavsttusgradstdr.SRVY_INFO_GRAD /* 포장상태등급기준.조사_정보_등급 */ , tnpavsttusgradstdr.GRAD_MUMM_STDR /* 포장상태등급기준.등급_최소_기준 */ , tnpavsttusgradstdr.GRAD_MXMM_STDR /* 포장상태등급기준.등급_최대_기준 */ , tnpavsttusgradstdr.GRAD_JDGMNT_STDR /* 포장상태등급기준.등급_판정_기준 */ , tnpavsttusgradstdr.GRAD_SCTN_STTUS /* 포장상태등급기준.등급_구간_상태 */ , tnpavsttusgradstdr.GRAD_COLOR_CODE /* 포장상태등급기준.등급_색상_코드 */ , tnpavsttusgradstdr.ALPHA /* 포장상태등급기준.ALPHA */ , tnpavsttusgradstdr.DELETE_AT /* 포장상태등급기준.삭제_여부 */ , tnpavsttusgradstdr.USE_AT /* 포장상태등급기준.사용_여부 */ , tnpavsttusgradstdr.CRTR_NO /* 포장상태등급기준.생성자_번호 */ , tnpavsttusgradstdr.CREAT_DT /* 포장상태등급기준.생성_일시 */ , tnpavsttusgradstdr.UPDUSR_NO /* 포장상태등급기준.수정자_번호 */ , tnpavsttusgradstdr.UPDT_DT /* 포장상태등급기준.수정_일시 */
FROM TN_PAV_STTUS_GRAD_STDR tnpavsttusgradstdr
WHERE 1 = 1
AND tnpavsttusgradstdr.GRAD_NO = #searchKeyword#
AND tnpavsttusgradstdr.GRAD_STDR_CODE LIKE '%' || #searchKeyword# || '%'
/* 포장상태등급기준.등급_번호 */ AND tnpavsttusgradstdr.GRAD_NO = #GRAD_NO#
/* 포장상태등급기준.등급_기준_코드 */ AND tnpavsttusgradstdr.GRAD_STDR_CODE = #GRAD_STDR_CODE#
/* 포장상태등급기준.적용_년도 */ AND tnpavsttusgradstdr.APPLC_YEAR = #APPLC_YEAR#
/* 포장상태등급기준.조사_정보_등급 */ AND tnpavsttusgradstdr.SRVY_INFO_GRAD = #SRVY_INFO_GRAD#
/* 포장상태등급기준.등급_최소_기준 */ AND tnpavsttusgradstdr.GRAD_MUMM_STDR = #GRAD_MUMM_STDR#
/* 포장상태등급기준.등급_최대_기준 */ AND tnpavsttusgradstdr.GRAD_MXMM_STDR = #GRAD_MXMM_STDR#
/* 포장상태등급기준.등급_판정_기준 */ AND tnpavsttusgradstdr.GRAD_JDGMNT_STDR = #GRAD_JDGMNT_STDR#
/* 포장상태등급기준.등급_구간_상태 */ AND tnpavsttusgradstdr.GRAD_SCTN_STTUS = #GRAD_SCTN_STTUS#
/* 포장상태등급기준.등급_색상_코드 */ AND tnpavsttusgradstdr.GRAD_COLOR_CODE = #GRAD_COLOR_CODE#
/* 포장상태등급기준.ALPHA */ AND tnpavsttusgradstdr.ALPHA = #ALPHA#
/* 포장상태등급기준.삭제_여부 */ AND tnpavsttusgradstdr.DELETE_AT = #DELETE_AT#
/* 포장상태등급기준.사용_여부 */ AND tnpavsttusgradstdr.USE_AT = #USE_AT#
/* 포장상태등급기준.생성자_번호 */ AND tnpavsttusgradstdr.CRTR_NO = #CRTR_NO#
/* 포장상태등급기준.생성_일시 */ AND tnpavsttusgradstdr.CREAT_DT = #CREAT_DT#
/* 포장상태등급기준.수정자_번호 */ AND tnpavsttusgradstdr.UPDUSR_NO = #UPDUSR_NO#
/* 포장상태등급기준.수정_일시 */ AND tnpavsttusgradstdr.UPDT_DT = #UPDT_DT#
) SELECT tnpavsttusgradstdr.* FROM ( SELECT tnpavsttusgradstdr.GRAD_NO /* 포장상태등급기준.등급_번호 */ , tnpavsttusgradstdr.GRAD_STDR_CODE /* 포장상태등급기준.등급_기준_코드 */ , tnpavsttusgradstdr.APPLC_YEAR /* 포장상태등급기준.적용_년도 */ , tnpavsttusgradstdr.SRVY_INFO_GRAD /* 포장상태등급기준.조사_정보_등급 */ , tnpavsttusgradstdr.GRAD_MUMM_STDR /* 포장상태등급기준.등급_최소_기준 */ , tnpavsttusgradstdr.GRAD_MXMM_STDR /* 포장상태등급기준.등급_최대_기준 */ , tnpavsttusgradstdr.GRAD_JDGMNT_STDR /* 포장상태등급기준.등급_판정_기준 */ , tnpavsttusgradstdr.GRAD_SCTN_STTUS /* 포장상태등급기준.등급_구간_상태 */ , tnpavsttusgradstdr.GRAD_COLOR_CODE /* 포장상태등급기준.등급_색상_코드 */ , tnpavsttusgradstdr.ALPHA /* 포장상태등급기준.ALPHA */ , tnpavsttusgradstdr.DELETE_AT /* 포장상태등급기준.삭제_여부 */ , tnpavsttusgradstdr.USE_AT /* 포장상태등급기준.사용_여부 */ , tnpavsttusgradstdr.CRTR_NO /* 포장상태등급기준.생성자_번호 */ , tnpavsttusgradstdr.CREAT_DT /* 포장상태등급기준.생성_일시 */ , tnpavsttusgradstdr.UPDUSR_NO /* 포장상태등급기준.수정자_번호 */ , tnpavsttusgradstdr.UPDT_DT /* 포장상태등급기준.수정_일시 */ /* 포장상태등급기준.등급_번호 */ /* 포장상태등급기준.등급_기준_코드 */ , ROW_NUMBER() OVER( ORDER BY tnpavsttusgradstdr.GRAD_NO ASC, tnpavsttusgradstdr.GRAD_STDR_CODE DESC ) ROW_NO FROM tnpavsttusgradstdr WHERE 1=1 ) tnpavsttusgradstdr WHERE 1=1
AND tnpavsttusgradstdr.ROW_NO BETWEEN #firstIndex# + 1 AND #lastIndex#
AND tnpavsttusgradstdr.ROW_NO <= #lastIndex# AND ROW_NO > #firstIndex#

/* pavSttusGradStdrDAO.selectPavSttusGradStdrList */ /* 포장상태등급기준 조회 */ SELECT * FROM (SELECT ROW_NUMBER() OVER() AS rn, tb.* FROM ( SELECT tnpavsttusgradstdr.GRAD_NO /* 포장상태등급기준.등급_번호 */ , tnpavsttusgradstdr.GRAD_STDR_CODE /* 포장상태등급기준.등급_기준_코드 */ , tnpavsttusgradstdr.APPLC_YEAR /* 포장상태등급기준.적용_년도 */ , tnpavsttusgradstdr.SRVY_INFO_GRAD /* 포장상태등급기준.조사_정보_등급 */ , tnpavsttusgradstdr.GRAD_MUMM_STDR /* 포장상태등급기준.등급_최소_기준 */ , tnpavsttusgradstdr.GRAD_MXMM_STDR /* 포장상태등급기준.등급_최대_기준 */ , tnpavsttusgradstdr.GRAD_JDGMNT_STDR /* 포장상태등급기준.등급_판정_기준 */ , tnpavsttusgradstdr.GRAD_SCTN_STTUS /* 포장상태등급기준.등급_구간_상태 */ , tnpavsttusgradstdr.GRAD_COLOR_CODE /* 포장상태등급기준.등급_색상_코드 */ , tnpavsttusgradstdr.ALPHA /* 포장상태등급기준.ALPHA */ , tnpavsttusgradstdr.DELETE_AT /* 포장상태등급기준.삭제_여부 */ , tnpavsttusgradstdr.USE_AT /* 포장상태등급기준.사용_여부 */ , tnpavsttusgradstdr.CRTR_NO /* 포장상태등급기준.생성자_번호 */ , tnpavsttusgradstdr.CREAT_DT /* 포장상태등급기준.생성_일시 */ , tnpavsttusgradstdr.UPDUSR_NO /* 포장상태등급기준.수정자_번호 */ , tnpavsttusgradstdr.UPDT_DT /* 포장상태등급기준.수정_일시 */ FROM TN_PAV_STTUS_GRAD_STDR tnpavsttusgradstdr
ORDER BY tnpavsttusgradstdr.GRAD_NO DESC /* 포장상태등급기준.등급_번호 */ ) tb) tnpavsttusgradstdr WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#


/* pavSttusGradStdrDAO.selectPavSttusGradStdrListTotalCount */
/* 포장상태등급기준 목록 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TN_PAV_STTUS_GRAD_STDR tnpavsttusgradstdr
