/* cmptncDAO.selectCmptncList */ /* 관할_노선 목록 조회 */ SELECT * FROM ( SELECT tb.* ,ROW_NUMBER() OVER (ORDER BY $sidx$ $sord$) rnum FROM ( SELECT tb.* ,FN_GET_LIST_G2_ID(DEPT_CODE) GID ,ROW_NUMBER() OVER() as deptnum FROM ( SELECT dept.DEPT_CODE , (CASE WHEN dept.DEPT_CODE > '7000300' AND dept.DEPT_CODE < '7000400' THEN CONCAT( '민자도로사업자', ' ('|| dept.LOWEST_DEPT_NM || ')' ) WHEN (dept.DEPT_CODE in ('3750000','3760000','3770000','5610000')) THEN '수원시 ' || dept.LOWEST_DEPT_NM WHEN (dept.DEPT_CODE in ('3790000','3800000','3810000')) THEN '성남시 ' || dept.LOWEST_DEPT_NM WHEN (dept.DEPT_CODE in ('3950000','3960100','4100100')) THEN '고양시 ' || dept.LOWEST_DEPT_NM WHEN (dept.DEPT_CODE in ('3850000','3840000')) THEN '안양시 ' || dept.LOWEST_DEPT_NM WHEN (dept.DEPT_CODE in ('3910029','3910038')) THEN '평택시 ' || dept.LOWEST_DEPT_NM WHEN (dept.DEPT_CODE in ('5550000','5560000')) THEN '안산시 ' || dept.LOWEST_DEPT_NM WHEN (dept.DEPT_CODE in ('5620000','5630000','5640000')) THEN '용인시 ' || dept.LOWEST_DEPT_NM WHEN (dept.DEPT_CODE in ('5530073','5530074')) THEN '화성시 ' || dept.LOWEST_DEPT_NM ELSE dept.LOWEST_DEPT_NM END) AS LOWEST_DEPT_NM , cmptnczone.CHARGER_NM , cmptnczone.CTTPC , COALESCE(cmptnczone.AR, 0) AS AR FROM ( SELECT DEPT_CODE, LOWEST_DEPT_NM FROM TC_DEPT WHERE 1=1 AND ( DEPT_CODE > 7000000 AND DEPT_CODE != 7000300 ) OR ( DEPT_CODE < 6410000 AND ODR = 2 ) OR (DEPT_CODE in ('3750000','3760000','3770000','5610000' /* 수원시 구 추가 */ ,'3790000','3800000','3810000' /* 성남시 구 추가 */ ,'3950000','3960100','4100100' /* 고양시 구 추가 */ ,'3850000','3840000' /* 안양시 구 추가 */ ,'3910019','3910029','3910038' /* 평택시청, 출장소 추가 */ ,'5550000','5560000' /* 안산시 구 추가 */ ,'5620000','5630000','5640000' /* 용인시 구 추가 */ ,'5530072','5530073','5530074')) /* 화성시청, 출장소 추가 */ OR ( DEPT_CODE IN (6411399, 6411799)) ) dept LEFT OUTER JOIN ( SELECT cmptnczone.DEPT_CODE , ( SELECT tcdept.LOWEST_DEPT_NM FROM TC_DEPT tcdept WHERE cmptnczone.DEPT_CODE = tcdept.DEPT_CODE ) LOWEST_DEPT_NM , cmptnczone.CHARGER_NM , cmptnczone.CTTPC , SUM(AR) AS AR FROM CMPTNC_ZONE cmptnczone WHERE 1=1 GROUP BY cmptnczone.DEPT_CODE, cmptnczone.CHARGER_NM, cmptnczone.CTTPC ) cmptnczone
ON cmptnczone.DEPT_CODE = dept.DEPT_CODE WHERE 1=1
/* 7000300 으로 조회했을 경우 */ AND ((#DEPT_CODE# = '7000300' AND dept.DEPT_CODE > '7000300' AND dept.DEPT_CODE < '7000400') /* lsk 구가 존재하는 시로 조회했을 경우 - 시,구 모두 조회되도록 함.(평택시,화성시 제외) */ OR (#DEPT_CODE# = '3740000' AND dept.DEPT_CODE in ('3750000','3760000','3770000','5610000')) /* 수원시 */ OR (#DEPT_CODE# = '3940000' AND dept.DEPT_CODE in ('3950000','3960100','4100100')) /* 고양시 */ OR (#DEPT_CODE# = '4050000' AND dept.DEPT_CODE in ('5620000','5630000','5640000')) /* 용인시 */ OR (#DEPT_CODE# = '3780000' AND dept.DEPT_CODE in ('3790000','3800000','3810000')) /* 성남시 */ OR (#DEPT_CODE# = '3930000' AND dept.DEPT_CODE in ('5550000','5560000')) /* 안산시 */ OR (#DEPT_CODE# = '3830000' AND dept.DEPT_CODE in ('3840000','3850000')) /* 안양시 */ OR (#DEPT_CODE# = '5530000' AND dept.DEPT_CODE in ('5530072','5530073','5530074')) /* 화성시 */ OR (#DEPT_CODE# = '3910000' AND dept.DEPT_CODE in ('3910019','3910029','3910038')) /* 평택시 */ OR (#DEPT_CODE# != '7000300' AND dept.DEPT_CODE = #DEPT_CODE# ) )
AND cmptnczone.CHARGER_NM LIKE '%' || #SCH_CHARGER_NM# || '%'
ORDER BY CASE WHEN LOWEST_DEPT_NM = '수원시' THEN 1 WHEN LOWEST_DEPT_NM = '수원시 장안구' THEN 2 WHEN LOWEST_DEPT_NM = '수원시 권선구' THEN 3 WHEN LOWEST_DEPT_NM = '수원시 팔달구' THEN 4 WHEN LOWEST_DEPT_NM = '수원시 영통구' THEN 5 WHEN LOWEST_DEPT_NM = '고양시' THEN 6 WHEN LOWEST_DEPT_NM = '고양시 덕양구' THEN 7 WHEN LOWEST_DEPT_NM = '고양시 일산동구' THEN 8 WHEN LOWEST_DEPT_NM = '고양시 일산서구' THEN 9 WHEN LOWEST_DEPT_NM = '용인시' THEN 10 WHEN LOWEST_DEPT_NM = '용인시 처인구' THEN 11 WHEN LOWEST_DEPT_NM = '용인시 기흥구' THEN 12 WHEN LOWEST_DEPT_NM = '용인시 수지구' THEN 13 WHEN LOWEST_DEPT_NM = '성남시' THEN 14 WHEN LOWEST_DEPT_NM = '성남시 수정구' THEN 15 WHEN LOWEST_DEPT_NM = '성남시 중원구' THEN 16 WHEN LOWEST_DEPT_NM = '성남시 분당구' THEN 17 WHEN LOWEST_DEPT_NM = '부천시' THEN 18 WHEN LOWEST_DEPT_NM = '안산시' THEN 19 WHEN LOWEST_DEPT_NM = '안산시 상록구' THEN 20 WHEN LOWEST_DEPT_NM = '안산시 단원구' THEN 21 WHEN LOWEST_DEPT_NM = '화성시' THEN 22 WHEN LOWEST_DEPT_NM = '화성시청' THEN 23 WHEN LOWEST_DEPT_NM = '화성시 동부출장소' THEN 24 WHEN LOWEST_DEPT_NM = '화성시 동탄출장소' THEN 25 WHEN LOWEST_DEPT_NM = '남양주' THEN 26 WHEN LOWEST_DEPT_NM = '안양시' THEN 27 WHEN LOWEST_DEPT_NM = '안양시 만안구' THEN 28 WHEN LOWEST_DEPT_NM = '안양시 동안구' THEN 29 WHEN LOWEST_DEPT_NM = '평택시' THEN 30 WHEN LOWEST_DEPT_NM = '평택시청' THEN 31 WHEN LOWEST_DEPT_NM = '평택시 송탄출장소' THEN 32 WHEN LOWEST_DEPT_NM = '평택시 안중출장소' THEN 33 WHEN LOWEST_DEPT_NM = '의정부시' THEN 34 WHEN LOWEST_DEPT_NM = '파주시' THEN 35 WHEN LOWEST_DEPT_NM = '시흥시' THEN 36 WHEN LOWEST_DEPT_NM = '김포시' THEN 37 WHEN LOWEST_DEPT_NM = '광명시' THEN 38 WHEN LOWEST_DEPT_NM = '광주시' THEN 39 WHEN LOWEST_DEPT_NM = '군포시' THEN 40 WHEN LOWEST_DEPT_NM = '이천시' THEN 41 WHEN LOWEST_DEPT_NM = '오산시' THEN 42 WHEN LOWEST_DEPT_NM = '하남시' THEN 43 WHEN LOWEST_DEPT_NM = '양주시' THEN 44 WHEN LOWEST_DEPT_NM = '구리시' THEN 45 WHEN LOWEST_DEPT_NM = '안성시' THEN 46 WHEN LOWEST_DEPT_NM = '포천시' THEN 47 WHEN LOWEST_DEPT_NM = '의왕시' THEN 48 WHEN LOWEST_DEPT_NM = '여주시' THEN 49 WHEN LOWEST_DEPT_NM = '양평군' THEN 50 WHEN LOWEST_DEPT_NM = '동두천시' THEN 51 WHEN LOWEST_DEPT_NM = '과천시' THEN 52 WHEN LOWEST_DEPT_NM = '가평군' THEN 53 WHEN LOWEST_DEPT_NM = '연천군' THEN 54 END , DEPT_CODE
) tb ) tb ) tb WHERE rnum BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#

