TTPC , SUM(AR) AS AR FROM CMPTNC_ZONE cmptnczone WHERE 1=1 GROUP BY cmptnczone.DEPT_CODE, cmptnczone.CHARGER_NM, cmptnczone.CTTPC ) cmptnczone
ON cmptnczone.DEPT_CODE = dept.DEPT_CODE WHERE 1=1
/* 7000300 으로 조회했을 경우 */ AND ((#DEPT_CODE# = '7000300' AND dept.DEPT_CODE > '7000300' AND dept.DEPT_CODE < '7000400') OR (#DEPT_CODE# != '7000300' AND dept.DEPT_CODE = #DEPT_CODE# ) )
