/* cmptncDAO.updateCmptncList */
/* 관할_노선 수정 */
UPDATE CMPTNC_ZONE SET
    CHARGER_NM = #CHARGER_NM#
    ,CTTPC = #CTTPC#
WHERE DEPT_CODE = #DEPT_CODE#
