WHERE 1 = 1


	AND tnrolemenu.ROLE_NO = #searchKeyword#


	AND tnrolemenu.AUTHOR_ID LIKE '%' || #searchKeyword# || '%'

 /* 권한역할메뉴.역할_번호 */ AND tnrolemenu.ROLE_NO = #ROLE_NO# 
 /* 권한역할메뉴.권한_ID */ AND tnrolemenu.AUTHOR_ID = #AUTHOR_ID# 
 /* 권한역할메뉴.메뉴_ID */ AND tnrolemenu.MENU_ID = #MENU_ID# 
 /* 권한역할메뉴.생성자_번호 */ AND tnrolemenu.CRTR_NO = #CRTR_NO# 
 /* 권한역할메뉴.생성_일시 */ AND tnrolemenu.CREAT_DT = #CREAT_DT# 
 /* 권한역할메뉴.삭제_여부 */ AND tnrolemenu.DELETE_AT = #DELETE_AT# 
 /* 권한역할메뉴.수정자_번호 */ AND tnrolemenu.UPDUSR_NO = #UPDUSR_NO# 
 /* 권한역할메뉴.수정_일시 */ AND tnrolemenu.UPDT_DT = #UPDT_DT#

sqlMap.select:
/* roleMenuDAO.selectRoleMenuListPage */ /* 권한역할메뉴 조회(페이징) */ WITH tnrolemenu AS ( SELECT tnrolemenu.ROLE_NO /* 권한역할메뉴.역할_번호 */ , tnrolemenu.AUTHOR_ID /* 권한역할메뉴.권한_ID */ , tnrolemenu.MENU_ID /* 권한역할메뉴.메뉴_ID */ , tnrolemenu.CRTR_NO /* 권한역할메뉴.생성자_번호 */ , tnrolemenu.CREAT_DT /* 권한역할메뉴.생성_일시 */ , tnrolemenu.DELETE_AT /* 권한역할메뉴.삭제_여부 */ , tnrolemenu.UPDUSR_NO /* 권한역할메뉴.수정자_번호 */ , tnrolemenu.UPDT_DT /* 권한역할메뉴.수정_일시 */
FROM TN_ROLE_MENU tnrolemenu
WHERE 1 = 1
AND tnrolemenu.ROLE_NO = #searchKeyword#
AND tnrolemenu.AUTHOR_ID LIKE '%' || #searchKeyword# || '%'
/* 권한역할메뉴.역할_번호 */ AND tnrolemenu.ROLE_NO = #ROLE_NO#
/* 권한역할메뉴.권한_ID */ AND tnrolemenu.AUTHOR_ID = #AUTHOR_ID#
/* 권한역할메뉴.메뉴_ID */ AND tnrolemenu.MENU_ID = #MENU_ID#
/* 권한역할메뉴.생성자_번호 */ AND tnrolemenu.CRTR_NO = #CRTR_NO#
/* 권한역할메뉴.생성_일시 */ AND tnrolemenu.CREAT_DT = #CREAT_DT#
/* 권한역할메뉴.삭제_여부 */ AND tnrolemenu.DELETE_AT = #DELETE_AT#
/* 권한역할메뉴.수정자_번호 */ AND tnrolemenu.UPDUSR_NO = #UPDUSR_NO#
/* 권한역할메뉴.수정_일시 */ AND tnrolemenu.UPDT_DT = #UPDT_DT#
) SELECT tnrolemenu.* FROM ( SELECT tnrolemenu.ROLE_NO /* 권한역할메뉴.역할_번호 */ , tnrolemenu.AUTHOR_ID /* 권한역할메뉴.권한_ID */ , tnrolemenu.MENU_ID /* 권한역할메뉴.메뉴_ID */ , tnrolemenu.CRTR_NO /* 권한역할메뉴.생성자_번호 */ , tnrolemenu.CREAT_DT /* 권한역할메뉴.생성_일시 */ , tnrolemenu.DELETE_AT /* 권한역할메뉴.삭제_여부 */ , tnrolemenu.UPDUSR_NO /* 권한역할메뉴.수정자_번호 */ , tnrolemenu.UPDT_DT /* 권한역할메뉴.수정_일시 */ /* 권한역할메뉴.역할_번호 */ /* 권한역할메뉴.권한_ID */ , ROW_NUMBER() OVER( ORDER BY tnrolemenu.ROLE_NO ASC, tnrolemenu.AUTHOR_ID DESC ) ROW_NO FROM tnrolemenu WHERE 1=1 ) tnrolemenu WHERE 1=1
AND tnrolemenu.ROW_NO BETWEEN #firstIndex# + 1 AND #lastIndex#
AND tnrolemenu.ROW_NO <= #lastIndex# AND ROW_NO > #firstIndex#

/* roleMenuDAO.selectRoleMenuYList */ /* 권한이 있는 메뉴 조회 */ SELECT * FROM (SELECT ROWNUM AS rn, tb.* FROM ( SELECT tnmenu.MENU_ID /*시스템메뉴.메뉴ID*/ ,LPAD(' ', 4 *(LEVEL - 1)) || tnmenu.MENU_NM MENU_NM /*시스템메뉴.메뉴명*/ ,tnmenu.MENU_DC /*시스템메뉴.메뉴설명*/ ,tnmenu.CREAT_DT /*시스템메뉴.생성_일시*/ ,tnmenu.USE_AT /*시스템메뉴.사용여부*/ ,tnmenu.CRTR_NO /*시스템메뉴.생성자번호*/ ,tnmenu.UPDUSR_NO /*시스템메뉴.수정자번호*/ ,tnmenu.UPDT_DT /*시스템메뉴.수정일*/ FROM TN_MENU tnmenu WHERE EXISTS( SELECT 't' FROM TN_ROLE_MENU tnrolemenu WHERE tnrolemenu.DELETE_AT = 'N' AND tnrolemenu.MENU_ID = tnmenu.MENU_ID
/* 권한역할그룹.권한_ID */ AND tnrolemenu.AUTHOR_ID = #SCH_AUTH#
) AND tnmenu.DELETE_AT = 'N' AND tnmenu.USE_AT = 'Y' START WITH tnmenu.UPPER_MENU_ID IS NULL CONNECT BY PRIOR tnmenu.MENU_ID = tnmenu.UPPER_MENU_ID
ORDER BY $orderByColumn$ $orderByType$
) tb) tnrolemenu WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#

SELECT COUNT(*) FROM TN_MENU tnmenu WHERE EXISTS( SELECT 't' FROM TN_ROLE_MENU tnrolemenu WHERE tnrolemenu.DELETE_AT = 'N' AND tnrolemenu.MENU_ID = tnmenu.MENU_ID
/* 권한그룹.권한_ID */ AND tnrolemenu.AUTHOR_ID = #SCH_AUTH#
) AND tnmenu.DELETE_AT = 'N' AND tnmenu.USE_AT = 'Y'

/* roleMenuDAO.selectRoleMenuNList */ /* 권한이 없는메뉴 조회 */ SELECT * FROM (SELECT ROWNUM AS rn, tb.* FROM ( SELECT tnmenu.MENU_ID /*시스템메뉴.메뉴ID*/ ,LPAD(' ', 4 *(LEVEL - 1)) || tnmenu.MENU_NM MENU_NM /*시스템메뉴.메뉴명*/ ,tnmenu.MENU_DC /*시스템메뉴.메뉴설명*/ ,tnmenu.CREAT_DT /*시스템메뉴.생성_일시*/ ,tnmenu.USE_AT /*시스템메뉴.사용여부*/ ,tnmenu.CRTR_NO /*시스템메뉴.생성자번호*/ ,tnmenu.UPDUSR_NO /*시스템메뉴.수정자번호*/ ,tnmenu.UPDT_DT /*시스템메뉴.수정일*/ FROM TN_MENU tnmenu WHERE NOT EXISTS( SELECT 't' FROM TN_ROLE_MENU tnrolemenu WHERE tnrolemenu.DELETE_AT = 'N' AND tnrolemenu.MENU_ID = tnmenu.MENU_ID
/* 권한역할그룹.권한_ID */ AND tnrolemenu.AUTHOR_ID = #SCH_AUTH# ) AND tnmenu.DELETE_AT = 'N' AND tnmenu.USE_AT = 'Y' START WITH tnmenu.UPPER_MENU_ID IS NULL CONNECT BY PRIOR tnmenu.MENU_ID = tnmenu.UPPER_MENU_ID
ORDER BY $orderByColumn$ $orderByType$
) tb) tnrolemenu WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#

SELECT COUNT(*) FROM TN_MENU tnmenu WHERE NOT EXISTS( SELECT 't' FROM TN_ROLE_MENU tnrolemenu WHERE tnrolemenu.DELETE_AT = 'N' AND tnrolemenu.MENU_ID = tnmenu.MENU_ID
/* 권한그룹.권한_ID */ AND tnrolemenu.AUTHOR_ID = #SCH_AUTH#
) AND tnmenu.DELETE_AT = 'N' AND tnmenu.USE_AT = 'Y'
