sqlMap.delete:

/* rpairMthdDAO.deleteRpairMthd */
/* 보수공법코드 삭제 */
DELETE FROM TC_RPAIR_MTHD 
WHERE 1 = 1
AND RPAIR_MTHD_CODE = #RPAIR_MTHD_CODE# /* 보수공법코드.보수_공법_코드 */

sqlMap.sql:
WHERE 1 = 1
	AND tcrpairmthd.DELETE_AT = 'N' AND tcrpairmthd.USE_AT = 'Y'

	AND tcrpairmthd.RPAIR_MTHD_CODE = #searchKeyword#


	AND tcrpairmthd.MSRC_CL_CODE LIKE '%' || #searchKeyword# || '%'

 /* 보수공법코드.보수_공법_코드 */ AND tcrpairmthd.RPAIR_MTHD_CODE = #RPAIR_MTHD_CODE# 
 /* 보수공법코드.공법_분류_코드 */ AND tcrpairmthd.MSRC_CL_CODE = #MSRC_CL_CODE# 
 /* 보수공법코드.공법_명 */ AND tcrpairmthd.MSRC_NM = #MSRC_NM# 

 /* 보수공법코드.생성자_번호 */ AND tcrpairmthd.CRTR_NO = #CRTR_NO# 
 /* 보수공법코드.생성_일시 */ AND tcrpairmthd.CREAT_DT = #CREAT_DT# 
 /* 보수공법코드.수정자_번호 */ AND tcrpairmthd.UPDUSR_NO = #UPDUSR_NO# 
 /* 보수공법코드.수정_일시 */ AND tcrpairmthd.UPDT_DT = #UPDT_DT#

sqlMap.select:
/* rpairMthdDAO.selectRpairMthdListPage */ /* 보수공법코드 조회(페이징) */ WITH tcrpairmthd AS ( SELECT tcrpairmthd.RPAIR_MTHD_CODE /* 보수공법코드.보수_공법_코드 */ , tcrpairmthd.MSRC_CL_CODE /* 보수공법코드.공법_분류_코드 */ , (SELECT tccode.CODE_NM FROM TC_CODE tccode WHERE tccode.CODE_VAL = tcrpairmthd.MSRC_CL_CODE ) MSRC_CL_NM , tcrpairmthd.MSRC_NM /* 보수공법코드.공법_명 */ , tcrpairmthd.DELETE_AT /* 보수공법코드.삭제_여부 */ , tcrpairmthd.USE_AT /* 보수공법코드.사용_여부 */ , tcrpairmthd.CRTR_NO /* 보수공법코드.생성자_번호 */ , tcrpairmthd.CREAT_DT /* 보수공법코드.생성_일시 */ , tcrpairmthd.UPDUSR_NO /* 보수공법코드.수정자_번호 */ , tcrpairmthd.UPDT_DT /* 보수공법코드.수정_일시 */ , tcrpairmthd.MSRC_CT /* 보수공법코드.공법_비용 */ , tcrpairmthd.MSRC_LCLAS_CODE /* 보수공법코드.공법_대분류_코드 */ , (SELECT tccode.CODE_NM FROM TC_CODE tccode WHERE tccode.CODE_VAL = tcrpairmthd.MSRC_LCLAS_CODE ) MSRC_LCLAS_NM , tcrpairmthd.MSR_DM_CODE /* 보수공법코드.공법선정비율_결정방식_코드 */ , (SELECT tccode.CODE_NM FROM TC_CODE tccode WHERE tccode.CODE_VAL = tcrpairmthd.MSR_DM_CODE ) MSR_DM_NM
FROM TC_RPAIR_MTHD tcrpairmthd
WHERE 1 = 1
AND tcrpairmthd.RPAIR_MTHD_CODE = #searchKeyword#
AND tcrpairmthd.MSRC_CL_CODE LIKE '%' || #searchKeyword# || '%'
/* 보수공법코드.보수_공법_코드 */ AND tcrpairmthd.RPAIR_MTHD_CODE = #RPAIR_MTHD_CODE#
/* 보수공법코드.공법_분류_코드 */ AND tcrpairmthd.MSRC_CL_CODE = #MSRC_CL_CODE#
/* 보수공법코드.공법_명 */ AND tcrpairmthd.MSRC_NM = #MSRC_NM#
/* 보수공법코드.삭제_여부 */ AND tcrpairmthd.DELETE_AT = #DELETE_AT#
/* 보수공법코드.사용_여부 */ AND tcrpairmthd.USE_AT = #USE_AT#
/* 보수공법코드.생성자_번호 */ AND tcrpairmthd.CRTR_NO = #CRTR_NO#
/* 보수공법코드.생성_일시 */ AND tcrpairmthd.CREAT_DT = #CREAT_DT#
/* 보수공법코드.수정자_번호 */ AND tcrpairmthd.UPDUSR_NO = #UPDUSR_NO#
/* 보수공법코드.수정_일시 */ AND tcrpairmthd.UPDT_DT = #UPDT_DT#
) SELECT tcrpairmthd.* FROM ( SELECT tcrpairmthd.RPAIR_MTHD_CODE /* 보수공법코드.보수_공법_코드 */ , tcrpairmthd.MSRC_CL_CODE /* 보수공법코드.공법_분류_코드 */ , tcrpairmthd.MSRC_NM /* 보수공법코드.공법_명 */ , tcrpairmthd.DELETE_AT /* 보수공법코드.삭제_여부 */ , tcrpairmthd.USE_AT /* 보수공법코드.사용_여부 */ , tcrpairmthd.CRTR_NO /* 보수공법코드.생성자_번호 */ , tcrpairmthd.CREAT_DT /* 보수공법코드.생성_일시 */ , tcrpairmthd.UPDUSR_NO /* 보수공법코드.수정자_번호 */ , tcrpairmthd.UPDT_DT /* 보수공법코드.수정_일시 */ /* 보수공법코드.보수_공법_코드 */ /* 보수공법코드.공법_분류_코드 */ , ROW_NUMBER() OVER( ORDER BY tcrpairmthd.RPAIR_MTHD_CODE ASC, tcrpairmthd.MSRC_CL_CODE DESC ) ROW_NO FROM tcrpairmthd WHERE 1=1 ) tcrpairmthd WHERE 1=1
AND tcrpairmthd.ROW_NO BETWEEN #firstIndex# + 1 AND #lastIndex#
AND tcrpairmthd.ROW_NO <= #lastIndex# AND ROW_NO > #firstIndex#

/* rpairMthdDAO.selectRpairMthdList */ /* 보수공법코드 조회 */ SELECT * FROM (SELECT ROWNUM AS rn, tb.* FROM ( SELECT tcrpairmthd.RPAIR_MTHD_CODE /* 보수공법코드.보수_공법_코드 */ , tcrpairmthd.MSRC_CL_CODE /* 보수공법코드.공법_분류_코드 */ , (SELECT tccode.CODE_NM FROM TC_CODE tccode WHERE tccode.CODE_VAL = tcrpairmthd.MSRC_CL_CODE ) MSRC_CL_NM , tcrpairmthd.MSRC_NM /* 보수공법코드.공법_명 */ , tcrpairmthd.DELETE_AT /* 보수공법코드.삭제_여부 */ , tcrpairmthd.USE_AT /* 보수공법코드.사용_여부 */ , tcrpairmthd.CRTR_NO /* 보수공법코드.생성자_번호 */ , tcrpairmthd.CREAT_DT /* 보수공법코드.생성_일시 */ , tcrpairmthd.UPDUSR_NO /* 보수공법코드.수정자_번호 */ , tcrpairmthd.UPDT_DT /* 보수공법코드.수정_일시 */ , tcrpairmthd.MSRC_CT /* 보수공법코드.공법_비용 */ , tcrpairmthd.MSRC_LCLAS_CODE /* 보수공법코드.공법_대분류_코드 */ , (SELECT tccode.CODE_NM FROM TC_CODE tccode WHERE tccode.CODE_VAL = tcrpairmthd.MSRC_LCLAS_CODE ) MSRC_LCLAS_NM , tcrpairmthd.MSR_DM_CODE /* 보수공법코드.공법선정비율_결정방식_코드 */ , (SELECT tccode.CODE_NM FROM TC_CODE tccode WHERE tccode.CODE_VAL = tcrpairmthd.MSR_DM_CODE ) MSR_DM_NM FROM TC_RPAIR_MTHD tcrpairmthd
ORDER BY tcrpairmthd.RPAIR_MTHD_CODE DESC /* 보수공법코드.보수_공법_코드 */ ) tb) tcrpairmthd WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#


/* rpairMthdDAO.selectRpairMthdListTotalCount */
/* 보수공법코드 목록 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TC_RPAIR_MTHD tcrpairmthd
