sqlMap.sql:
WHERE 1 = 1
	AND tnrpairmthdfrmula.USE_AT = 'Y'
	AND tnrpairmthdfrmula.DELETE_AT = 'N'



		AND tnrpairmthdfrmula.STEP_STTUS_CODE = #searchKeyword#




		AND tnrpairmthdfrmula.STEP_SE_CODE LIKE '%' || #searchKeyword# || '%'


 /* 보수_공법_수식_관리.단계_상태_코드 */ AND tnrpairmthdfrmula.STEP_STTUS_CODE = #STEP_STTUS_CODE# 
 /* 보수_공법_수식_관리.단계_구분_코드 */ AND tnrpairmthdfrmula.STEP_SE_CODE = #STEP_SE_CODE# 
 /* 보수_공법_수식_관리.단계_상태_명 */ AND tnrpairmthdfrmula.STEP_STTUS_NM = #STEP_STTUS_NM# 
 /* 보수_공법_수식_관리.시작_값 */ AND tnrpairmthdfrmula.BEGIN_VAL = #BEGIN_VAL#::int 
 /* 보수_공법_수식_관리.종료_값 */ AND tnrpairmthdfrmula.END_VAL = #END_VAL#::int 
 /* 보수_공법_수식_관리.시작_연산_구분_코드 */ AND tnrpairmthdfrmula.BEGIN_CALC_SE_CODE = #BEGIN_CALC_SE_CODE# 
 /* 보수_공법_수식_관리.종료_연산_구분_코드 */ AND tnrpairmthdfrmula.END_CALC_SE_CODE = #END_CALC_SE_CODE# 
 /* 보수_공법_수식_관리.생성자_번호 */ AND tnrpairmthdfrmula.CRTR_NO = #CRTR_NO#::int 
 /* 보수_공법_수식_관리.생성_일시 */ AND tnrpairmthdfrmula.CREAT_DT = #CREAT_DT# 
 /* 보수_공법_수식_관리.수정자_번호 */ AND tnrpairmthdfrmula.UPDUSR_NO = #UPDUSR_NO#::int 
 /* 보수_공법_수식_관리.수정_일시 */ AND tnrpairmthdfrmula.UPDT_DT = #UPDT_DT# 
