sqlMap.insert:
select nextval('SEQ_TN_RPAIR_MTHD_UNTPC')
/* rpairMthdUntpcDAO.insertRpairMthdUntpc */ /* 보수_공법_단가 등록 */
INSERT INTO TN_RPAIR_MTHD_UNTPC ( UNTPC_MANAGE_NO /* 보수_공법_단가.단가_관리_번호 */ , RPAIR_MTHD_CODE /* 보수_공법_단가.보수_공법_코드 */ , CNTRWK_AMOUNT /* 보수_공법_단가.공사_금액 */ , PAINT_AMOUNT /* 보수_공법_단가.차선_도색_금액 */ , ETC_AMOUNT /* 보수_공법_단가.기타_금액 */ , TOT_AMOUNT /* 보수_공법_단가.총_금액 */ , DSGN_DT /* 보수_공법_단가.설계_일시 */ , USE_AT /* 보수_공법_단가.사용_여부 */ , DELETE_AT /* 보수_공법_단가.삭제_여부 */ , CRTR_NO /* 보수_공법_단가.생성자_번호 */ , CREAT_DT /* 보수_공법_단가.생성_일시 */ , UPDUSR_NO /* 보수_공법_단가.수정자_번호 */ , UPDT_DT /* 보수_공법_단가.수정_일시 */ ) VALUES ( #UNTPC_MANAGE_NO# /* 보수_공법_단가.단가_관리_번호 */ , #RPAIR_MTHD_CODE# /* 보수_공법_단가.보수_공법_코드 */ , #CNTRWK_AMOUNT# /* 보수_공법_단가.공사_금액 */ , #PAINT_AMOUNT# /* 보수_공법_단가.차선_도색_금액 */ , #ETC_AMOUNT# /* 보수_공법_단가.기타_금액 */ , #TOT_AMOUNT# /* 보수_공법_단가.총_금액 */ , #DSGN_DT# /* 보수_공법_단가.설계_일시 */ , #USE_AT# /* 보수_공법_단가.사용_여부 */ , #DELETE_AT# /* 보수_공법_단가.삭제_여부 */ , #CRTR_NO# /* 보수_공법_단가.생성자_번호 */ , #CREAT_DT# /* 보수_공법_단가.생성_일시 */ , #UPDUSR_NO# /* 보수_공법_단가.수정자_번호 */ , NOW() /* 보수_공법_단가.수정_일시 */ )

sqlMap.sql:
WHERE 1 = 1
	AND tnrpairmthduntpc.DELETE_AT = 'N'
	AND tnrpairmthduntpc.USE_AT = 'Y'



		AND tnrpairmthduntpc.UNTPC_MANAGE_NO = #searchKeyword#




		AND tnrpairmthduntpc.RPAIR_MTHD_CODE LIKE '%' || #searchKeyword# || '%'


 /* 보수_공법_단가.단가_관리_번호 */ AND tnrpairmthduntpc.UNTPC_MANAGE_NO = #UNTPC_MANAGE_NO# 
 /* 보수_공법_단가.보수_공법_코드 */ AND tnrpairmthduntpc.RPAIR_MTHD_CODE = #RPAIR_MTHD_CODE# 
 /* 보수_공법_단가.공사_금액 */ AND tnrpairmthduntpc.CNTRWK_AMOUNT = #CNTRWK_AMOUNT# 
 /* 보수_공법_단가.차선_도색_금액 */ AND tnrpairmthduntpc.PAINT_AMOUNT = #PAINT_AMOUNT# 
 /* 보수_공법_단가.기타_금액 */ AND tnrpairmthduntpc.ETC_AMOUNT = #ETC_AMOUNT# 
 /* 보수_공법_단가.총_금액 */ AND tnrpairmthduntpc.TOT_AMOUNT = #TOT_AMOUNT# 
 /* 보수_공법_단가.설계_일시 */ AND tnrpairmthduntpc.DSGN_DT = #DSGN_DT# 
 /* 보수_공법_단가.생성자_번호 */ AND tnrpairmthduntpc.CRTR_NO = #CRTR_NO# 
 /* 보수_공법_단가.생성_일시 */ AND tnrpairmthduntpc.CREAT_DT = #CREAT_DT# 
 /* 보수_공법_단가.수정자_번호 */ AND tnrpairmthduntpc.UPDUSR_NO = #UPDUSR_NO# 
 /* 보수_공법_단가.수정_일시 */ AND tnrpairmthduntpc.UPDT_DT = #UPDT_DT#

sqlMap.select:
/* rpairMthdUntpcDAO.selectRpairMthdUntpcList */ /* 보수_공법_단가 조회 */ SELECT * FROM (SELECT ROW_NUMBER() OVER() AS rn, tb.* FROM ( SELECT tnrpairmthduntpc.UNTPC_MANAGE_NO /* 보수_공법_단가.단가_관리_번호 */ , tnrpairmthduntpc.RPAIR_MTHD_CODE /* 보수_공법_단가.보수_공법_코드 */ , tnrpairmthduntpc.CNTRWK_AMOUNT /* 보수_공법_단가.공사_금액 */ , tnrpairmthduntpc.PAINT_AMOUNT /* 보수_공법_단가.차선_도색_금액 */ , tnrpairmthduntpc.ETC_AMOUNT /* 보수_공법_단가.기타_금액 */ , tnrpairmthduntpc.TOT_AMOUNT /* 보수_공법_단가.총_금액 */ , tnrpairmthduntpc.DSGN_DT /* 보수_공법_단가.설계_일시 */ , tnrpairmthduntpc.USE_AT /* 보수_공법_단가.사용_여부 */ , tnrpairmthduntpc.DELETE_AT /* 보수_공법_단가.삭제_여부 */ , tnrpairmthduntpc.CRTR_NO /* 보수_공법_단가.생성자_번호 */ , tnrpairmthduntpc.CREAT_DT /* 보수_공법_단가.생성_일시 */ , tnrpairmthduntpc.UPDUSR_NO /* 보수_공법_단가.수정자_번호 */ , tnrpairmthduntpc.UPDT_DT /* 보수_공법_단가.수정_일시 */ FROM TN_RPAIR_MTHD_UNTPC tnrpairmthduntpc
ORDER BY tnrpairmthduntpc.RPAIR_MTHD_CODE ASC ) tb) WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#


/* rpairMthdUntpcDAO.selectRpairMthdUntpcListTotalCount */
/* 보수_공법_단가 목록 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TN_RPAIR_MTHD_UNTPC tnrpairmthduntpc
