sqlMap.insert:
select SEQ_TN_SM_CALC_PREDCT.NEXTVAL from dual
/* smCalcPredctDAO.insertSmCalcPredct */ /* 집계_산정_예측 등록 */
INSERT INTO TN_SM_CALC_PREDCT ( CALC_YEAR /* 집계_산정_예측.산정_년도 */ , CALC_MT /* 집계_산정_예측.산정_월 */ , PREDCT_YEAR /* 집계_산정_예측.예측_년도 */ , PREDCT_MT /* 집계_산정_예측.예측_월 */ , ROUTE_CODE /* 집계_산정_예측.노선_코드 */ , DIRECT_CODE /* 집계_산정_예측.행선_코드 */ , TRACK /* 집계_산정_예측.차로 */ , STRTPT /* 집계_산정_예측.시점 */ , ENDPT /* 집계_산정_예측.종점 */ , AC_IDX /* 집계_산정_예측.거북등균열_지수 */ , LC_IDX /* 집계_산정_예측.종방향균열_지수 */ , TC_IDX /* 집계_산정_예측.횡방향균열_지수 */ , BC_IDX /* 집계_산정_예측.블럭균열_지수 */ , PTCHG_IDX /* 집계_산정_예측.패칭_지수 */ , POTHOLE_IDX /* 집계_산정_예측.포트홀_지수 */ , RCI /* 집계_산정_예측.표면_조도_지수 */ , SCR /* 집계_산정_예측.표면_상태_지수 */ , GPCI /* 집계_산정_예측.GPCI */ , SRVY_YEAR /* 집계_산정_예측.조사_년도 */ , SRVY_MT /* 집계_산정_예측.조사_월 */ , PREDCT_NO /* 집계_산정_예측.예측_번호 */ , RD_IDX /* 집계_산정_예측.소성변형지수 */ ) VALUES ( #CALC_YEAR# /* 집계_산정_예측.산정_년도 */ , #CALC_MT# /* 집계_산정_예측.산정_월 */ , #PREDCT_YEAR# /* 집계_산정_예측.예측_년도 */ , #PREDCT_MT# /* 집계_산정_예측.예측_월 */ , #ROUTE_CODE# /* 집계_산정_예측.노선_코드 */ , #DIRECT_CODE# /* 집계_산정_예측.행선_코드 */ , #TRACK# /* 집계_산정_예측.차로 */ , #STRTPT# /* 집계_산정_예측.시점 */ , #ENDPT# /* 집계_산정_예측.종점 */ , #AC_IDX# /* 집계_산정_예측.거북등균열_지수 */ , #LC_IDX# /* 집계_산정_예측.종방향균열_지수 */ , #TC_IDX# /* 집계_산정_예측.횡방향균열_지수 */ , #BC_IDX# /* 집계_산정_예측.블럭균열_지수 */ , #PTCHG_IDX# /* 집계_산정_예측.패칭_지수 */ , #POTHOLE_IDX# /* 집계_산정_예측.포트홀_지수 */ , #RCI# /* 집계_산정_예측.표면_조도_지수 */ , #SCR# /* 집계_산정_예측.표면_상태_지수 */ , #GPCI# /* 집계_산정_예측.GPCI */ , #SRVY_YEAR# /* 집계_산정_예측.조사_년도 */ , #SRVY_MT# /* 집계_산정_예측.조사_월 */ , #PREDCT_NO# /* 집계_산정_예측.예측_번호 */ , #RD_IDX# /* 집계_산정_예측.소성변형지수 */ )

sqlMap.delete:

/* smCalcPredctDAO.deleteSmCalcPredct */
/* 집계_산정_예측 삭제 */
DELETE FROM TN_SM_CALC_PREDCT
WHERE 1 = 1
AND PREDCT_NO = #PREDCT_NO# /* 집계_산정_예측.예측_번호 */

sqlMap.sql:
WHERE 1 = 1



		AND tnsmcalcpredct.CALC_YEAR = #searchKeyword#




		AND tnsmcalcpredct.CALC_MT LIKE '%' || #searchKeyword# || '%'


 /* 집계_산정_예측.산정_년도 */ AND tnsmcalcpredct.CALC_YEAR = #CALC_YEAR# 
 /* 집계_산정_예측.산정_월 */ AND tnsmcalcpredct.CALC_MT = #CALC_MT# 
 /* 집계_산정_예측.예측_년도 */ AND tnsmcalcpredct.PREDCT_YEAR = #PREDCT_YEAR# 
 /* 집계_산정_예측.예측_월 */ AND tnsmcalcpredct.PREDCT_MT = #PREDCT_MT# 
 /* 집계_산정_예측.노선_코드 */ AND tnsmcalcpredct.ROUTE_CODE = #ROUTE_CODE# 
 /* 집계_산정_예측.행선_코드 */ AND tnsmcalcpredct.DIRECT_CODE = #DIRECT_CODE# 
 /* 집계_산정_예측.차로 */ AND tnsmcalcpredct.TRACK = #TRACK# 
 /* 집계_산정_예측.시점 */ AND tnsmcalcpredct.STRTPT = #STRTPT# 
 /* 집계_산정_예측.종점 */ AND tnsmcalcpredct.ENDPT = #ENDPT# 
 /* 집계_산정_예측.거북등균열_지수 */ AND tnsmcalcpredct.AC_IDX = #AC_IDX# 
 /* 집계_산정_예측.종방향균열_지수 */ AND tnsmcalcpredct.LC_IDX = #LC_IDX# 
 /* 집계_산정_예측.횡방향균열_지수 */ AND tnsmcalcpredct.TC_IDX = #TC_IDX# 
 /* 집계_산정_예측.블럭균열_지수 */ AND tnsmcalcpredct.BC_IDX = #BC_IDX# 
 /* 집계_산정_예측.패칭_지수 */ AND tnsmcalcpredct.PTCHG_IDX = #PTCHG_IDX# 
 /* 집계_산정_예측.포트홀_지수 */ AND tnsmcalcpredct.POTHOLE_IDX = #POTHOLE_IDX# 
 /* 집계_산정_예측.표면_조도_지수 */ AND tnsmcalcpredct.RCI = #RCI# 
 /* 집계_산정_예측.표면_상태_지수 */ AND tnsmcalcpredct.SCR = #SCR# 
 /* 집계_산정_예측.GPCI */ AND tnsmcalcpredct.GPCI = #GPCI# 
 /* 집계_산정_예측.조사_년도 */ AND tnsmcalcpredct.SRVY_YEAR = #SRVY_YEAR# 
 /* 집계_산정_예측.조사_월 */ AND tnsmcalcpredct.SRVY_MT = #SRVY_MT# 
 /* 집계_산정_예측.예측_번호 */ AND tnsmcalcpredct.PREDCT_NO = #PREDCT_NO# 
 /* 집계_산정_예측.소성변형지수 */ AND tnsmcalcpredct.RD_IDX = #RD_IDX#

sqlMap.select:
/* smCalcPredctDAO.selectSmCalcPredctList */ /* 집계_산정_예측 조회 */ SELECT * FROM (SELECT ROWNUM AS rn, tb.* FROM ( SELECT tnsmcalcpredct.CALC_YEAR /* 집계_산정_예측.산정_년도 */ , tnsmcalcpredct.CALC_MT /* 집계_산정_예측.산정_월 */ , tnsmcalcpredct.PREDCT_YEAR /* 집계_산정_예측.예측_년도 */ , tnsmcalcpredct.PREDCT_MT /* 집계_산정_예측.예측_월 */ , tnsmcalcpredct.ROUTE_CODE /* 집계_산정_예측.노선_코드 */ , tnsmcalcpredct.DIRECT_CODE /* 집계_산정_예측.행선_코드 */ , tnsmcalcpredct.TRACK /* 집계_산정_예측.차로 */ , tnsmcalcpredct.STRTPT /* 집계_산정_예측.시점 */ , tnsmcalcpredct.ENDPT /* 집계_산정_예측.종점 */ , tnsmcalcpredct.AC_IDX /* 집계_산정_예측.거북등균열_지수 */ , tnsmcalcpredct.LC_IDX /* 집계_산정_예측.종방향균열_지수 */ , tnsmcalcpredct.TC_IDX /* 집계_산정_예측.횡방향균열_지수 */ , tnsmcalcpredct.BC_IDX /* 집계_산정_예측.블럭균열_지수 */ , tnsmcalcpredct.PTCHG_IDX /* 집계_산정_예측.패칭_지수 */ , tnsmcalcpredct.POTHOLE_IDX /* 집계_산정_예측.포트홀_지수 */ , tnsmcalcpredct.RCI /* 집계_산정_예측.표면_조도_지수 */ , tnsmcalcpredct.SCR /* 집계_산정_예측.표면_상태_지수 */ , tnsmcalcpredct.GPCI /* 집계_산정_예측.GPCI */ , tnsmcalcpredct.SRVY_YEAR /* 집계_산정_예측.조사_년도 */ , tnsmcalcpredct.SRVY_MT /* 집계_산정_예측.조사_월 */ , tnsmcalcpredct.PREDCT_NO /* 집계_산정_예측.예측_번호 */ , tnsmcalcpredct.RD_IDX /* 집계_산정_예측.소성변형지수 */ FROM TN_SM_CALC_PREDCT tnsmcalcpredct
ORDER BY
tnsmcalcpredct.PREDCT_YEAR
) tb) WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#


/* smCalcPredctDAO.selectSmCalcPredctListTotalCount */
/* 집계_산정_예측 목록 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TN_SM_CALC_PREDCT tnsmcalcpredct
