/* smDtaGnlSttusDAO.selectSmDtaGnlSttusListTotalCount */
/* 집계_자료_일반_현황 목록 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TN_SM_DTA_GNL_STTUS tnsmdtagnlsttus

/* smDtaGnlSttusDAO.selectSmDtaGnlSttusYearList */
/* 집계_자료 조사년도 목록 조회 */
SELECT DISTINCT SRVY_YEAR SRVY_YEAR
