NHPCI /* 집계_자료_일반_현황.NHPCI */ , tnsmdtagnlsttus.SPI /* 집계_자료_일반_현황.SPI */ , tnsmdtagnlsttus.XCR /* 집계_자료_일반_현황.NHPCI_균열 */ , tnsmdtagnlsttus.RCI /* 집계_자료_일반_현황.표면_조도_지수 */ , tnsmdtagnlsttus.SCR /* 집계_자료_일반_현황.표면_상태_지수 */ , tnsmdtagnlsttus.AC_LOW /* 집계_자료_일반_현황.거북등균열_LOW */ , tnsmdtagnlsttus.AC_MED/* 집계_자료_일반_현황.거북등균열_MED */ , tnsmdtagnlsttus.AC_HI /* 집계_자료_일반_현황.거북등균열_HI */ , tnsmdtagnlsttus.LC_LOW /* 집계_자료_일반_현황.종방향균열_LOW */ , tnsmdtagnlsttus.LC_MED /* 집계_자료_일반_현황.종방향균열_MED */ , tnsmdtagnlsttus.LC_HI /* 집계_자료_일반_현황.종방향균열_HI */ , tnsmdtagnlsttus.TC_LOW /* 집계_자료_일반_현황.횡방향균열_LOW */ , tnsmdtagnlsttus.TC_MED /* 집계_자료_일반_현황.횡방향균열_MED */ , tnsmdtagnlsttus.TC_HI /* 집계_자료_일반_현황.횡방향균열_HI */ , tnsmdtagnlsttus.AC_IDX /* 집계_자료_일반_현황.거북등균열_지수 */ , tnsmdtagnlsttus.LC_IDX /* 집계_자료_일반_현황.종방향균열_지수 */ , tnsmdtagnlsttus.TC_IDX /* 집계_자료_일반_현황.횡방향균열_지수 */ , tnsmdtagnlsttus.PTCHG_IDX /* 집계_자료_일반_현황.패칭_지수 */ , tnsmdtagnlsttus.RD_LOW /* 집계_자료_일반_현황.소성변형_LOW */ , tnsmdtagnlsttus.RD_MED /* 집계_자료_일반_현황.소성변형_MED */ , tnsmdtagnlsttus.RD_HI /* 집계_자료_일반_현황.소성변형_HI */ , tnsmdtagnlsttus.BLOCK_CR_LOW /* 집계_자료_일반_현황.블럭_균열_LOW */ , tnsmdtagnlsttus.BLOCK_CR_MED /* 집계_자료_일반_현황.블럭_균열_MED */ , tnsmdtagnlsttus.BLOCK_CR_HI /* 집계_자료_일반_현황.블럭_균열_HI */ , tnsmdtagnlsttus.POTHOLE_IDX /* 집계_자료_일반_현황.포트홀_지수 */ , tnsmdtagnlsttus.DMG_CUZ_CLMT /* 집계_자료_일반_현황.파손_원인_기후 */ , tnsmdtagnlsttus.DMG_CUZ_VMTC /* 집계_자료_일반_현황.파손_원인_교통량 */ , tnsmdtagnlsttus.DMG_CUZ_ETC /* 집계_자료_일반_현황.파손_원인_기타 */ , tnsmdtagnlsttus.GPCI /* 집계_자료_일반_현황.GPCI */ , tnsmdtagnlsttus.PC_GRAD /* 집계_자료_일반_현황.포장상태등급 */ , tnsmdtagnlsttus.BC_IDX /* 집계_자료_일반_현황.블럭균열_지수 */ , TO_NUMBER(tnsmdtagnlsttus.ROUTE_CODE) AS ROAD_NO_VAL , road.ROAD_NAME AS ROAD_NM , ( SELECT tccode.CODE_NM FROM TC_CODE tccode WHERE tccode.CODE_VAL = tnsmdtagnlsttus.CNTL_DFECT ) AS CNTL_DFECT_NM , ( SELECT code.CODE_NM FROM TC_CODE code WHERE code.CODE_VAL = road.ROAD_GRAD ) AS ROAD_GRAD , ( SELECT tcdept.LOWEST_DEPT_NM FROM TC_DEPT tcdept WHERE tcdept.DEPT_CODE = sect.DEPT_CODE ) AS DEPT_CODE , ( SELECT tccode.CODE_NM FROM TC_CODE tccode WHERE tccode.CODE_VAL = sect.CELL_TYPE ) AS CELL_TYPE , ( SELECT tccode.CODE_NM FROM TC_CODE tccode WHERE tccode.CODE_VAL = sect.VMTC_GRAD ) AS VMTC_GRAD , ( SELECT n3a.NAME FROM N3A_G0100000 n3a WHERE SUBSTR(n3a.BJCD, 0, 5) = sect.ADM_CODE ) AS ADM_CODE , sect.CELL_ID , FN_GET_GPCI_RDUCT_RATE(tnsmdtagnlsttus.AC_IDX, 0, 0, sect.CELL_TYPE, 'SCR', 0) AC_RATE , FN_GET_GPCI_RDUCT_RATE(tnsmdtagnlsttus.BC_IDX, 0, 0, sect.CELL_TYPE, 'SCR', 0) BC_RATE , FN_GET_GPCI_RDUCT_RATE(tnsmdtagnlsttus.LC_IDX, 0, 0, sect.CELL_TYPE, 'SCR', 0) LC_RATE , FN_GET_GPCI_RDUCT_RATE(tnsmdtagnlsttus.PTCHG_IDX, 0, 0, sect.CELL_TYPE, 'SCR', 0) PTCHG_RATE , FN_GET_GPCI_RDUCT_RATE(tnsmdtagnlsttus.POTHOLE_IDX, 0, 0, sect.CELL_TYPE, 'SCR', 0) POT_RATE , FN_GET_GPCI_RDUCT_RATE(tnsmdtagnlsttus.RD_IDX, 0, 0, sect.CELL_TYPE, 'SCR', 0) RD_RATE , FN_GET_GPCI_RDUCT_RATE(tnsmdtagnlsttus.RCI, 0, 0, sect.CELL_TYPE, 'RCI', 0) RCI_RATE FROM TN_SM_DTA_GNL_STTUS tnsmdtagnlsttus INNER JOIN CELL_SECT sect ON tnsmdtagnlsttus.DIRECT_CODE = sect.DIRECT_CODE AND tnsmdtagnlsttus.TRACK = sect.TRACK AND tnsmdtagnlsttus.ROUTE_CODE = sect.ROUTE_CODE AND tnsmdtagnlsttus.STRTPT = sect.STRTPT AND tnsmdtagnlsttus.ENDPT = sect.ENDPT AND sect.CELL_ID = #CELL_ID# LEFT OUTER JOIN TN_ROUTE_INFO road ON road.ROAD_NO = tnsmdtagnlsttus.ROUTE_CODE WHERE 1 = 1 AND tnsmdtagnlsttus.DELETE_AT = 'N' /* 최소_구간_조사_자료.삭제_여부 */ AND tnsmdtagnlsttus.USE_AT = 'Y' /* 최소_구간_조사_자료.사용_여부 */
/* 최소_구간_조사_자료.조사_년도 */ AND tnsmdtagnlsttus.SRVY_YEAR = #SRVY_YEAR# /* 최소_구간_조사_자료.조사_년도 */ AND tnsmdtagnlsttus.LAST_DTA_AT = 'Y'
) , RESULTLIST AS ( SELECT tb.* , CASE WHEN tb.SCR = 0 THEN (tb.AC_RATE + tb.BC_RATE + tb.LC_RATE + tb.PTCHG_RATE + tb.POT_RATE + tb.RD_RATE) ELSE 0 SUM_RATE FROM LASTLIST tb ) SELECT tb.* , FN_GET_GPCI_RDUCT_RATE(tb.AC_IDX, 0, 0, tb.CELL_TYPE, 'SCR', tb.SUM_RATE) AC_RDUCT_RATE , FN_GET_GPCI_RDUCT_RATE(tb.BC_IDX, 0, 0, tb.CELL_TYPE, 'SCR', tb.SUM_RATE) BC_RDUCT_RATE , FN_GET_GPCI_RDUCT_RATE(tb.LC_IDX, 0, 0, tb.CELL_TYPE, 'SCR', tb.SUM_RATE) LC_RDUCT_RATE , FN_GET_GPCI_RDUCT_RATE(tb.PTCHG_IDX, 0, 0, tb.CELL_TYPE, 'SCR', tb.SUM_RATE) PTCHG_RDUCT_RATE , FN_GET_GPCI_RDUCT_RATE(tb.POTHOLE_IDX, 0, 0, tb.CELL_TYPE, 'SCR', tb.SUM_RATE) POT_RDUCT_RATE , FN_GET_GPCI_RDUCT_RATE(tb.RD_IDX, 0, 0, tb.CELL_TYPE, 'SCR', tb.SUM_RATE) RD_RDUCT_RATE , FN_GET_GPCI_RDUCT_RATE(tb.RCI, 0, 0, tb.CELL_TYPE, 'RCI', tb.SUM_RATE) RCI_RDUCT_RATE , RCI_RATE RCI_RDUCT_RATE FROM RESULTLIST tb INNER JOIN CELL_SECT sect ON sect.CELL_ID = tb.CELL_ID
