 /* 조사_자료_현황.조사_월 */ AND tnsrvydtasttus.SRVY_MT = #SRVY_MT# 
 /* 조사_자료_현황.엑셀_자료_SEQ */ AND tnsrvydtasttus.EXCEL_DTA_SEQ = #EXCEL_DTA_SEQ#::int 
 /* 조사_자료_현황.조사_번호 */ AND tnsrvydtasttus.SRVY_NO = #SRVY_NO#::int 
