/* srvyDtaSttusDAO.selectSrvyDtaSttusListPage */ /* 조사_자료_현황 조회(페이징) */ WITH tnsrvydtasttus AS ( SELECT tnsrvydtasttus.DTA_STTUS_NO /* 조사_자료_현황.자료_현황_번호 */ , tnsrvydtasttus.SRVY_YEAR /* 조사_자료_현황.조사_년도 */ , tnsrvydtasttus.ROUTE_CODE /* 조사_자료_현황.노선_코드 */ , tnsrvydtasttus.DIRECT_CODE /* 조사_자료_현황.행선_코드 */ , tnsrvydtasttus.TRACK /* 조사_자료_현황.차로 */ , tnsrvydtasttus.STRTPT /* 조사_자료_현황.시점 */ , tnsrvydtasttus.ENDPT /* 조사_자료_현황.종점 */ , tnsrvydtasttus.SRVY_KND /* 조사_자료_현황.조사_종류 */ , tnsrvydtasttus.SRVY_MT /* 조사_자료_현황.조사_월 */ , tnsrvydtasttus.EXCEL_DTA_SEQ /* 조사_자료_현황.엑셀_자료_SEQ */ , tnsrvydtasttus.SRVY_NO /* 조사_자료_현황.조사_번호 */ , tnsrvydtasttus.SM_PROCESS_AT /* 조사_자료_현황.집계_구간_산출_여부 */ , tnsrvydtasttus.PRDTMDL_PROCESS_AT /* 조사_자료_현황.예측_모델_적용_여부 */ , tnsrvydtasttus.DELETE_AT /* 조사_자료_현황.삭제_여부 */ , tnsrvydtasttus.USE_AT /* 조사_자료_현황.사용_여부 */ , tnsrvydtasttus.CRTR_NO /* 조사_자료_현황.생성자_번호 */ , tnsrvydtasttus.CREAT_DT /* 조사_자료_현황.생성_일시 */ , tnsrvydtasttus.UPDUSR_NO /* 조사_자료_현황.수정자_번호 */ , tnsrvydtasttus.UPDT_DT /* 조사_자료_현황.수정_일시 */ , tnsrvydtasttus.DTA_NO /* 조사_자료_현황.자료_번호 */
FROM TN_SRVY_DTA_STTUS tnsrvydtasttus
WHERE 1 = 1
AND tnsrvydtasttus.DTA_STTUS_NO = #searchKeyword#::int
AND tnsrvydtasttus.SRVY_YEAR LIKE '%' || #searchKeyword# || '%'
/* 조사_자료_현황.자료_현황_번호 */ AND tnsrvydtasttus.DTA_STTUS_NO = #DTA_STTUS_NO#::int
/* 조사_자료_현황.조사_년도 */ AND tnsrvydtasttus.SRVY_YEAR = #SRVY_YEAR#
/* 조사_자료_현황.노선_코드 */ AND tnsrvydtasttus.ROUTE_CODE = #ROUTE_CODE#
/* 조사_자료_현황.행선_코드 */ AND tnsrvydtasttus.DIRECT_CODE = #DIRECT_CODE#
/* 조사_자료_현황.차로 */ AND tnsrvydtasttus.TRACK = #TRACK#
/* 조사_자료_현황.시점 */ AND tnsrvydtasttus.STRTPT = #STRTPT#::int
/* 조사_자료_현황.종점 */ AND tnsrvydtasttus.ENDPT = #ENDPT#::int
/* 조사_자료_현황.조사_종류 */ AND tnsrvydtasttus.SRVY_KND = #SRVY_KND#
/* 조사_자료_현황.조사_월 */ AND tnsrvydtasttus.SRVY_MT = #SRVY_MT#
/* 조사_자료_현황.엑셀_자료_SEQ */ AND tnsrvydtasttus.EXCEL_DTA_SEQ = #EXCEL_DTA_SEQ#::int
/* 조사_자료_현황.조사_번호 */ AND tnsrvydtasttus.SRVY_NO = #SRVY_NO#::int
/* 조사_자료_현황.집계_구간_산출_여부 */ AND tnsrvydtasttus.SM_PROCESS_AT = #SM_PROCESS_AT#
/* 조사_자료_현황.예측_모델_적용_여부 */ AND tnsrvydtasttus.PRDTMDL_PROCESS_AT = #PRDTMDL_PROCESS_AT#
/* 조사_자료_현황.삭제_여부 */ AND tnsrvydtasttus.DELETE_AT = #DELETE_AT#
/* 조사_자료_현황.사용_여부 */ AND tnsrvydtasttus.USE_AT = #USE_AT#
/* 조사_자료_현황.생성자_번호 */ AND tnsrvydtasttus.CRTR_NO = #CRTR_NO#::int
/* 조사_자료_현황.생성_일시 */ AND tnsrvydtasttus.CREAT_DT = #CREAT_DT#
/* 조사_자료_현황.수정자_번호 */ AND tnsrvydtasttus.UPDUSR_NO = #UPDUSR_NO#::int
/* 조사_자료_현황.수정_일시 */ AND tnsrvydtasttus.UPDT_DT = #UPDT_DT#
/* 조사_자료_현황.자료_번호 */ AND tnsrvydtasttus.DTA_NO = #DTA_NO#::int
) SELECT tnsrvydtasttus.* FROM ( SELECT tnsrvydtasttus.DTA_STTUS_NO /* 조사_자료_현황.자료_현황_번호 */ , tnsrvydtasttus.SRVY_YEAR /* 조사_자료_현황.조사_년도 */ , tnsrvydtasttus.ROUTE_CODE /* 조사_자료_현황.노선_코드 */ , tnsrvydtasttus.DIRECT_CODE /* 조사_자료_현황.행선_코드 */ , tnsrvydtasttus.TRACK /* 조사_자료_현황.차로 */ , tnsrvydtasttus.STRTPT /* 조사_자료_현황.시점 */ , tnsrvydtasttus.ENDPT /* 조사_자료_현황.종점 */ , tnsrvydtasttus.SRVY_KND /* 조사_자료_현황.조사_종류 */ , tnsrvydtasttus.SRVY_MT /* 조사_자료_현황.조사_월 */ , tnsrvydtasttus.EXCEL_DTA_SEQ /* 조사_자료_현황.엑셀_자료_SEQ */ , tnsrvydtasttus.SRVY_NO /* 조사_자료_현황.조사_번호 */ , tnsrvydtasttus.SM_PROCESS_AT /* 조사_자료_현황.집계_구간_산출_여부 */ , tnsrvydtasttus.PRDTMDL_PROCESS_AT /* 조사_자료_현황.예측_모델_적용_여부 */ , tnsrvydtasttus.DELETE_AT /* 조사_자료_현황.삭제_여부 */ , tnsrvydtasttus.USE_AT /* 조사_자료_현황.사용_여부 */ , tnsrvydtasttus.CRTR_NO /* 조사_자료_현황.생성자_번호 */ , tnsrvydtasttus.CREAT_DT /* 조사_자료_현황.생성_일시 */ , tnsrvydtasttus.UPDUSR_NO /* 조사_자료_현황.수정자_번호 */ , tnsrvydtasttus.UPDT_DT /* 조사_자료_현황.수정_일시 */ , tnsrvydtasttus.DTA_NO /* 조사_자료_현황.자료_번호 */ /* 조사_자료_현황.자료_현황_번호 */ /* 조사_자료_현황.조사_년도 */ , ROW_NUMBER() OVER( ORDER BY tnsrvydtasttus.DTA_STTUS_NO ASC, tnsrvydtasttus.SRVY_YEAR DESC ) ROW_NO FROM tnsrvydtasttus WHERE 1=1 ) tnsrvydtasttus WHERE 1=1
AND tnsrvydtasttus.ROW_NO BETWEEN #firstIndex# + 1 AND #lastIndex#
AND tnsrvydtasttus.ROW_NO <= #lastIndex# AND ROW_NO > #firstIndex#

/* srvyDtaSttusDAO.selectSrvyDtaSttusList */ /* 조사_자료_현황 조회 */ SELECT * FROM (SELECT ROW_NUMBER() OVER() AS rn, tb.* FROM ( SELECT tnsrvydtasttus.DTA_STTUS_NO /* 조사_자료_현황.자료_현황_번호 */ , tnsrvydtasttus.SRVY_YEAR /* 조사_자료_현황.조사_년도 */ , tnsrvydtasttus.ROUTE_CODE /* 조사_자료_현황.노선_코드 */ , tnsrvydtasttus.DIRECT_CODE /* 조사_자료_현황.행선_코드 */ , tnsrvydtasttus.TRACK /* 조사_자료_현황.차로 */ , tnsrvydtasttus.STRTPT /* 조사_자료_현황.시점 */ , tnsrvydtasttus.ENDPT /* 조사_자료_현황.종점 */ , tnsrvydtasttus.SRVY_KND /* 조사_자료_현황.조사_종류 */ , tnsrvydtasttus.SRVY_MT /* 조사_자료_현황.조사_월 */ , tnsrvydtasttus.EXCEL_DTA_SEQ /* 조사_자료_현황.엑셀_자료_SEQ */ , tnsrvydtasttus.SRVY_NO /* 조사_자료_현황.조사_번호 */ , tnsrvydtasttus.SM_PROCESS_AT /* 조사_자료_현황.집계_구간_산출_여부 */ , tnsrvydtasttus.PRDTMDL_PROCESS_AT /* 조사_자료_현황.예측_모델_적용_여부 */ , tnsrvydtasttus.DELETE_AT /* 조사_자료_현황.삭제_여부 */ , tnsrvydtasttus.USE_AT /* 조사_자료_현황.사용_여부 */ , tnsrvydtasttus.CRTR_NO /* 조사_자료_현황.생성자_번호 */ , tnsrvydtasttus.CREAT_DT /* 조사_자료_현황.생성_일시 */ , tnsrvydtasttus.UPDUSR_NO /* 조사_자료_현황.수정자_번호 */ , tnsrvydtasttus.UPDT_DT /* 조사_자료_현황.수정_일시 */ , tnsrvydtasttus.DTA_NO /* 조사_자료_현황.자료_번호 */ FROM TN_SRVY_DTA_STTUS tnsrvydtasttus
ORDER BY tnsrvydtasttus.DTA_STTUS_NO DESC /* 조사_자료_현황.자료_현황_번호 */ ) tb) tnsrvydtasttus WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#


/* srvyDtaSttusDAO.selectSrvyDtaSttusListTotalCount */
/* 조사_자료_현황 목록 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TN_SRVY_DTA_STTUS tnsrvydtasttus


/* srvyDtaSttusDAO.selectSrvyDtaAnalTrgetListTotalCount */
/* 조사_자료_분석대상 목록 개수 조회  */
SELECT COUNT(*) AS totCnt FROM (
	SELECT tnsrvydtasttus.SRVY_YEAR, tnsrvydtasttus.SRVY_MT
	  FROM TN_SRVY_DTA_STTUS tnsrvydtasttus
	 WHERE tnsrvydtasttus.SM_PROCESS_AT = 'N'
	   AND tnsrvydtasttus.USE_AT = 'Y'
	   AND tnsrvydtasttus.DELETE_AT = 'N'
	 GROUP BY tnsrvydtasttus.SRVY_YEAR, tnsrvydtasttus.SRVY_MT
) tnsrvydtasttus
