sqlMap.insert:

/* srvyRequstSctnCellInfoDAO.insertSrvyRequstSctnCellInfo */
/* 조사_요청_구간_셀_정보 등록 */

INSERT INTO TN_SRVY_REQUST_SCTN_CELL_INFO (
	SRVY_REQUST_SCTN_NO /* 조사_요청_구간_셀_정보.조사_요청_구간_번호 */
	, PAV_CELL_ID /* 조사_요청_구간_셀_정보.포장_셀_ID */
	, CRTR_NO /* 조사_요청_구간_셀_정보.생성자_번호 */
	, CREAT_DT /* 조사_요청_구간_셀_정보.생성_일시 */
) VALUES (
	#SRVY_REQUST_SCTN_NO#::numeric    /* 조사_요청_구간_셀_정보.조사_요청_구간_번호 */
	, #PAV_CELL_ID#          /* 조사_요청_구간_셀_정보.포장_셀_ID */
	, #CRTR_NO#::numeric              /* 조사_요청_구간_셀_정보.생성자_번호 */
	, NOW()                  /* 조사_요청_구간_셀_정보.생성_일시 */
)

sqlMap.delete:

/* srvyRequstSctnCellInfoDAO.deleteSrvyRequstSctnCellInfo */
/* 조사_요청_구간_셀_정보 삭제 */
DELETE FROM TN_SRVY_REQUST_SCTN_CELL_INFO
WHERE 1 = 1

 /* 조사_요청_구간_셀_정보.조사_요청_구간_번호 */ AND SRVY_REQUST_SCTN_NO = #SRVY_REQUST_SCTN_NO#::numeric  
 /* 조사_요청_구간_셀_정보.포장_셀_ID */ AND PAV_CELL_ID = #PAV_CELL_ID#

sqlMap.sql:
WHERE 1 = 1
 /* 조사_요청_구간_셀_정보.조사_요청_구간_번호 */ AND srvyrequstsctncellinfo.SRVY_REQUST_SCTN_NO = #SRVY_REQUST_SCTN_NO#::numeric 
 /* 조사_요청_구간_셀_정보.포장_셀_ID */ AND srvyrequstsctncellinfo.PAV_CELL_ID = #PAV_CELL_ID# 
 /* 조사_요청_구간_셀_정보.생성자_번호 */ AND srvyrequstsctncellinfo.CRTR_NO = #CRTR_NO#::numeric

sqlMap.select:
/* srvyRequstSctnCellInfoDAO.selectSrvyRequstSctnCellInfoList */ /* 조사_요청_구간_셀_정보 조회 */ SELECT srvyrequstsctncellinfo.SRVY_REQUST_SCTN_NO /* 조사_요청_구간_셀_정보.조사_요청_구간_번호 */ , srvyrequstsctncellinfo.PAV_CELL_ID /* 조사_요청_구간_셀_정보.포장_셀_ID */ , srvyrequstsctncellinfo.CRTR_NO /* 조사_요청_구간_셀_정보.생성자_번호 */ , srvyrequstsctncellinfo.CREAT_DT /* 조사_요청_구간_셀_정보.생성_일시 */ FROM TN_SRVY_REQUST_SCTN_CELL_INFO srvyrequstsctncellinfo
ORDER BY srvyrequstsctncellinfo.SRVY_REQUST_SCTN_NO ASC /* 조사_요청_구간_셀_정보.세부_공사_ID */ , srvyrequstsctncellinfo.PAV_CELL_ID ASC /* 조사_요청_구간_셀_정보.포장_셀_ID */
LIMIT #recordCountPerPage# OFFSET #firstIndex#


/* srvyRequstSctnCellInfoDAO.selectSrvyRequstSctnCellInfoListTotalCount */
/* 조사_요청_구간_셀_정보 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TN_SRVY_REQUST_SCTN_CELL_INFO srvyrequstsctncellinfo

/* srvyRequstSctnCellInfoDAO.selectRouteInfoListByCellID */ /* CELL_10 리스트 조회 - 조사요청 > 요청구간 CELL 선택용 */ SELECT cell10.CELL_ID , cell10.ROUTE_CODE , tnrouteinfo.ROAD_NAME , tccode.code_nm ROAD_GRAD , cell10.direct_code DIRECT_CODE , CASE cell10.DIRECT_CODE WHEN 'S' THEN '상행' WHEN 'E' THEN '하행' WHEN 'SE' THEN '양방향' ELSE '' END DIRECT_NM , cell10.TRACK TRACK , cell10.STRTPT STRTPT , cell10.ENDPT ENDPT FROM( SELECT CELL_ID , ROUTE_CODE , DIRECT_CODE , TRACK , STRTPT , ENDPT , ROAD_GRAD FROM CELL_10 WHERE 1=1
#PAV_CELL_ID_LIST[]#
AND CELL_ID IN ( SELECT PAV_CELL_ID FROM TN_SRVY_REQUST_SCTN_CELL_INFO WHERE SRVY_REQUST_SCTN_NO = #SRVY_REQUST_SCTN_NO#::numeric )
AND ROUTE_CODE = #ROUTE_CODE#
) cell10 , TN_ROUTE_INFO tnrouteinfo , TC_CODE tccode WHERE cell10.ROUTE_CODE = tnrouteinfo.ROAD_NO AND CELL10.ROAD_GRAD = tccode.CODE_VAL AND tccode.USE_AT = 'Y' AND tccode.DELETE_AT = 'N' ORDER BY tnrouteinfo.ROAD_NO, cell10.STRTPT, cell10.DIRECT_CODE, cell10.TRACK
LIMIT #recordCountPerPage# OFFSET #firstIndex#
