sqlMap.insert:
select nextval('SEQ_TL_SYS_CONNECT')
/* userConnectDAO.insertUserConnect */ /* 사용자접속로그 등록 */
INSERT INTO TL_USER_CONNECT ( CONECT_LOG_NO /* 사용자접속로그.접속_로그_번호 */ , USER_NO /* 사용자접속로그.사용자_번호 */ , SESION_ID /* 사용자접속로그.세션_ID */ , CONECT_BEGIN_DT /* 사용자접속로그.접속_시작_일시 */ , CONECT_END_DT /* 사용자접속로그.접속_종료_일시 */ ) VALUES ( #CONECT_LOG_NO#::int /* 사용자접속로그.접속_로그_번호 */ , #USER_NO#::int /* 사용자접속로그.사용자_번호 */ , #SESION_ID# /* 사용자접속로그.세션_ID */ , NOW() /* 사용자접속로그.접속_시작_일시 */ , #CONECT_END_DT# /* 사용자접속로그.접속_종료_일시 */ )

sqlMap.delete:

/* userConnectDAO.deleteUserConnect */
/* 사용자접속로그 삭제 */
DELETE FROM TL_USER_CONNECT
WHERE 1 = 1
AND CONECT_LOG_NO = #CONECT_LOG_NO#::int /* 사용자접속로그.접속_로그_번호 */

sqlMap.sql:
WHERE 1 = 1



		AND tluserconnect.CONECT_LOG_NO = #searchKeyword#




		AND tluserconnect.USER_NO LIKE '%' || #searchKeyword# || '%'


 /* 사용자접속로그.접속_로그_번호 */ AND tluserconnect.CONECT_LOG_NO = #CONECT_LOG_NO#::int 
 /* 사용자접속로그.사용자_번호 */ AND tluserconnect.USER_NO = #USER_NO#::int 
 /* 사용자접속로그.세션_ID */ AND tluserconnect.SESION_ID = #SESION_ID# 
 /* 사용자접속로그.접속_시작_일시 */ AND tluserconnect.CONECT_BEGIN_DT = #CONECT_BEGIN_DT# 
 /* 사용자접속로그.접속_종료_일시 */ AND tluserconnect.CONECT_END_DT = #CONECT_END_DT#

sqlMap.select:
/* userConnectDAO.selectUserConnectList */ /* 사용자접속로그 조회 */ SELECT * FROM (SELECT (ROW_NUMBER() OVER()) AS rn, tb.* FROM ( SELECT tluserconnect.CONECT_LOG_NO /* 사용자접속로그.접속_로그_번호 */ , tluserconnect.USER_NO /* 사용자접속로그.사용자_번호 */ , tluserconnect.SESION_ID /* 사용자접속로그.세션_ID */ , tluserconnect.CONECT_BEGIN_DT /* 사용자접속로그.접속_시작_일시 */ , tluserconnect.CONECT_END_DT /* 사용자접속로그.접속_종료_일시 */ FROM TL_USER_CONNECT tluserconnect
ORDER BY
tluserconnect.CONECT_LOG_NO DESC /* 사용자접속로그.접속_로그_번호 */
) tb) WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#

/* userConnectDAO.selectUserConnectListTotalCount */
/* 사용자접속로그 목록 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TL_USER_CONNECT tluserconnect
