sqlMap.delete:

/* userMenuDAO.deleteUserMenu */
/* 사용자권한메뉴 삭제 */
DELETE FROM TN_USER_MENU 
WHERE 1 = 1
AND AUTHOR_NO = #AUTHOR_NO# /* 사용자권한메뉴.권한_번호 */

sqlMap.sql:
WHERE 1 = 1


	AND tnusermenu.AUTHOR_NO = #searchKeyword#


	AND tnusermenu.DELETE_AT LIKE '%' || #searchKeyword# || '%'

 /* 사용자권한메뉴.권한_번호 */ AND tnusermenu.AUTHOR_NO = #AUTHOR_NO# 
 /* 사용자권한메뉴.삭제_여부 */ AND tnusermenu.DELETE_AT = #DELETE_AT# 
 /* 사용자권한메뉴.생성자_번호 */ AND tnusermenu.CRTR_NO = #CRTR_NO# 
 /* 사용자권한메뉴.생성_일시 */ AND tnusermenu.CREAT_DT = #CREAT_DT# 
 /* 사용자권한메뉴.메뉴_ID */ AND tnusermenu.MENU_ID = #MENU_ID# 
 /* 사용자권한메뉴.사용자_번호 */ AND tnusermenu.USER_NO = #USER_NO# 
 /* 사용자권한메뉴.권한_ID */ AND tnusermenu.AUTHOR_ID = #AUTHOR_ID#

sqlMap.select:
/* userMenuDAO.selectUserMenuListPage */ /* 사용자권한메뉴 조회(페이징) */ WITH tnusermenu AS ( SELECT tnusermenu.AUTHOR_NO /* 사용자권한메뉴.권한_번호 */ , tnusermenu.DELETE_AT /* 사용자권한메뉴.삭제_여부 */ , tnusermenu.CRTR_NO /* 사용자권한메뉴.생성자_번호 */ , tnusermenu.CREAT_DT /* 사용자권한메뉴.생성_일시 */ , tnusermenu.MENU_ID /* 사용자권한메뉴.메뉴_ID */ , tnusermenu.USER_NO /* 사용자권한메뉴.사용자_번호 */ , tnusermenu.AUTHOR_ID /* 사용자권한메뉴.권한_ID */
FROM TN_USER_MENU tnusermenu
WHERE 1 = 1
AND tnusermenu.AUTHOR_NO = #searchKeyword#
AND tnusermenu.DELETE_AT LIKE '%' || #searchKeyword# || '%'
/* 사용자권한메뉴.권한_번호 */ AND tnusermenu.AUTHOR_NO = #AUTHOR_NO#
/* 사용자권한메뉴.삭제_여부 */ AND tnusermenu.DELETE_AT = #DELETE_AT#
/* 사용자권한메뉴.생성자_번호 */ AND tnusermenu.CRTR_NO = #CRTR_NO#
/* 사용자권한메뉴.생성_일시 */ AND tnusermenu.CREAT_DT = #CREAT_DT#
/* 사용자권한메뉴.메뉴_ID */ AND tnusermenu.MENU_ID = #MENU_ID#
/* 사용자권한메뉴.사용자_번호 */ AND tnusermenu.USER_NO = #USER_NO#
/* 사용자권한메뉴.권한_ID */ AND tnusermenu.AUTHOR_ID = #AUTHOR_ID#
) SELECT tnusermenu.* FROM ( SELECT tnusermenu.AUTHOR_NO /* 사용자권한메뉴.권한_번호 */ , tnusermenu.DELETE_AT /* 사용자권한메뉴.삭제_여부 */ , tnusermenu.CRTR_NO /* 사용자권한메뉴.생성자_번호 */ , tnusermenu.CREAT_DT /* 사용자권한메뉴.생성_일시 */ , tnusermenu.MENU_ID /* 사용자권한메뉴.메뉴_ID */ , tnusermenu.USER_NO /* 사용자권한메뉴.사용자_번호 */ , tnusermenu.AUTHOR_ID /* 사용자권한메뉴.권한_ID */ /* 사용자권한메뉴.권한_번호 */ /* 사용자권한메뉴.삭제_여부 */ , ROW_NUMBER() OVER( ORDER BY tnusermenu.AUTHOR_NO ASC, tnusermenu.DELETE_AT DESC ) ROW_NO FROM tnusermenu WHERE 1=1 ) tnusermenu WHERE 1=1
AND tnusermenu.ROW_NO BETWEEN #firstIndex# + 1 AND #lastIndex#
AND tnusermenu.ROW_NO <= #lastIndex# AND ROW_NO > #firstIndex#

/* userMenuDAO.selectUserMenuList */ /* 사용자권한메뉴 조회 */ SELECT * FROM (SELECT ROW_NUMBER() OVER() AS rn, tb.* FROM ( SELECT tnusermenu.AUTHOR_NO /* 사용자권한메뉴.권한_번호 */ , tnusermenu.DELETE_AT /* 사용자권한메뉴.삭제_여부 */ , tnusermenu.CRTR_NO /* 사용자권한메뉴.생성자_번호 */ , tnusermenu.CREAT_DT /* 사용자권한메뉴.생성_일시 */ , tnusermenu.MENU_ID /* 사용자권한메뉴.메뉴_ID */ , tnusermenu.USER_NO /* 사용자권한메뉴.사용자_번호 */ , tnusermenu.AUTHOR_ID /* 사용자권한메뉴.권한_ID */ FROM TN_USER_MENU tnusermenu
ORDER BY tnusermenu.AUTHOR_NO DESC /* 사용자권한메뉴.권한_번호 */ ) tb) tnusermenu WHERE 1=1
AND rn BETWEEN #firstIndex# + 1 AND #firstIndex# + #recordCountPerPage#


/* userMenuDAO.selectUserMenuListTotalCount */
/* 사용자권한메뉴 목록 개수 조회  */
SELECT COUNT(*) AS totCnt
FROM TN_USER_MENU tnusermenu
